import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button, Card } from 'react-bootstrap';

const Register = () => {
  const [formData, setFormData] = useState({ username: '', password: '', role: 'student' });
  const navigate = useNavigate();

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/auth/register`, formData);
      navigate('/login');
    } catch (err) {
      console.error(err);
      alert('Registration failed. Username may be taken.');
    }
  };

  return (
    <Card className="p-4 report-form">
      <h2>Register</h2>
      <Form onSubmit={onSubmit}>
        <Form.Group controlId="username" className="mb-3">
          <Form.Label>Username</Form.Label>
          <Form.Control type="text" name="username" value={formData.username} onChange={onChange} required />
        </Form.Group>
        <Form.Group controlId="password" className="mb-3">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" name="password" value={formData.password} onChange={onChange} required />
        </Form.Group>
        <Form.Group controlId="role" className="mb-3">
          <Form.Label>Role</Form.Label>
          <Form.Select name="role" value={formData.role} onChange={onChange} required>
            <option value="student">Student</option>
            <option value="lecturer">Lecturer</option>
            <option value="prl">Principal Lecturer</option>
            <option value="pl">Program Leader</option>
          </Form.Select>
        </Form.Group>
        <Button variant="primary" type="submit">Register</Button>
      </Form>
    </Card>
  );
};

export default Register;
