import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button, Card, Alert } from 'react-bootstrap';

const Register = () => {
  const [formData, setFormData] = useState({ username: '', password: '', role: 'student' });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const navigate = useNavigate();

  const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      setError(null);
      setSuccess(null);
      // ✅ Fix: remove duplicate /api
      await axios.post(`${process.env.REACT_APP_API_URL}/auth/register`, formData);
      setSuccess('Registration successful. Please log in.');
      navigate('/login');
    } catch (err) {
      console.error('Registration error:', err.response ? err.response.data : err);
      setError('Registration failed. ' + (err.response?.data?.msg || 'Check fields or server.'));
    }
  };

  return (
    <Card className="p-4" style={{ maxWidth: '400px', margin: '0 auto' }}>
      <h2>Register</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}
      <Form onSubmit={onSubmit}>
        <Form.Group controlId="username" className="mb-3">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            name="username"
            value={formData.username}
            onChange={onChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="password" className="mb-3">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            name="password"
            value={formData.password}
            onChange={onChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="role" className="mb-3">
          <Form.Label>Role</Form.Label>
          <Form.Select name="role" value={formData.role} onChange={onChange} required>
            <option value="student">Student</option>
          </Form.Select>
          <small className="form-text text-muted">
            Only students can register. Other roles are admin-managed.
          </small>
        </Form.Group>
        <Button variant="primary" type="submit">
          Register
        </Button>
      </Form>
    </Card>
  );
};

export default Register;
