import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Table, Form, Button, Alert, Modal } from 'react-bootstrap';

const Lectures = ({ role }) => {
  const [lectures, setLectures] = useState([]);
  const [classes, setClasses] = useState([]);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [newLecture, setNewLecture] = useState({
    course_name: '',
    course_code: '',
    lecturer_name: '',
    scheduled_date: '',
    venue: '',
    class_id: ''
  });

  // Fetch lectures
  const fetchLectures = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/lectures`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setLectures(res.data);
      setError(null);
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Error fetching lectures');
    }
  };

  // Fetch classes for assignment dropdown
  const fetchClasses = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/classes`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setClasses(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchLectures();
    if (role === 'pl') fetchClasses();
  }, [role]);

  const handleChange = (e) => {
    setNewLecture({ ...newLecture, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return;

    if (!newLecture.course_name || !newLecture.lecturer_name || !newLecture.scheduled_date || !newLecture.class_id) {
      setError('Please fill all required fields');
      return;
    }

    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/lectures`,
        newLecture,
        { headers: { 'x-auth-token': token } }
      );
      setSuccess('Lecture assigned successfully');
      setError(null);
      setShowModal(false);
      setNewLecture({ course_name: '', course_code: '', lecturer_name: '', scheduled_date: '', venue: '', class_id: '' });
      fetchLectures();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Error assigning lecture');
    }
  };

  return (
    <Card className="p-4">
      <h2>Lectures</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      {role === 'pl' && (
        <>
          <Button variant="primary" className="mb-3" onClick={() => setShowModal(true)}>Assign New Lecture</Button>

          <Modal show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header closeButton>
              <Modal.Title>Assign New Lecture</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-2">
                  <Form.Label>Course Name *</Form.Label>
                  <Form.Control type="text" name="course_name" value={newLecture.course_name} onChange={handleChange} required />
                </Form.Group>
                <Form.Group className="mb-2">
                  <Form.Label>Course Code</Form.Label>
                  <Form.Control type="text" name="course_code" value={newLecture.course_code} onChange={handleChange} />
                </Form.Group>
                <Form.Group className="mb-2">
                  <Form.Label>Lecturer Name *</Form.Label>
                  <Form.Control type="text" name="lecturer_name" value={newLecture.lecturer_name} onChange={handleChange} required />
                </Form.Group>
                <Form.Group className="mb-2">
                  <Form.Label>Scheduled Date *</Form.Label>
                  <Form.Control type="date" name="scheduled_date" value={newLecture.scheduled_date} onChange={handleChange} required />
                </Form.Group>
                <Form.Group className="mb-2">
                  <Form.Label>Venue</Form.Label>
                  <Form.Control type="text" name="venue" value={newLecture.venue} onChange={handleChange} />
                </Form.Group>
                <Form.Group className="mb-2">
                  <Form.Label>Class *</Form.Label>
                  <Form.Select name="class_id" value={newLecture.class_id} onChange={handleChange} required>
                    <option value="">Select Class</option>
                    {classes.map(cls => (
                      <option key={cls.class_id} value={cls.class_id}>{cls.class_name}</option>
                    ))}
                  </Form.Select>
                </Form.Group>
                <Button variant="primary" type="submit" className="mt-2">Assign Lecture</Button>
              </Form>
            </Modal.Body>
          </Modal>
        </>
      )}

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Course Name</th>
            <th>Course Code</th>
            <th>Lecturer Name</th>
            <th>Scheduled Date</th>
            <th>Venue</th>
            <th>Class</th>
          </tr>
        </thead>
        <tbody>
          {lectures.length ? (
            lectures.map(l => (
              <tr key={l.lecture_id}>
                <td>{l.lecture_id}</td>
                <td>{l.course_name}</td>
                <td>{l.course_code}</td>
                <td>{l.lecturer_name}</td>
                <td>{l.scheduled_date}</td>
                <td>{l.venue}</td>
                <td>{l.class_name}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7" className="text-center">No lectures assigned yet</td>
            </tr>
          )}
        </tbody>
      </Table>
    </Card>
  );
};

export default Lectures;
