import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import jwtDecode from 'jwt-decode'; // Corrected import without braces
import 'bootstrap/dist/css/bootstrap.min.css';
import './index.css';

import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import Reports from './components/Reports';
import Courses from './components/Courses';
import Classes from './components/Classes';
import Monitoring from './components/Monitoring';
import Rating from './components/Rating';
import Lectures from './components/Lectures';

const App = () => {
  const [role, setRole] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setRole(decoded.role);
      } catch (err) {
        console.error('Invalid token:', err);
        localStorage.removeItem('token');
        setRole(null);
      }
    }
  }, []);

  const logout = () => {
    localStorage.removeItem('token');
    setRole(null);
  };

  axios.defaults.headers.common['x-auth-token'] = localStorage.getItem('token');

  return (
    <Router>
      {role && <Navbar role={role} logout={logout} />}
      <div className="container mt-4">
        <Routes>
          <Route path="/login" element={<Login setRole={setRole} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/reports" element={<Reports role={role} />} />
          <Route path="/courses" element={<Courses role={role} />} />
          <Route path="/classes" element={<Classes role={role} />} />
          <Route path="/monitoring" element={<Monitoring role={role} />} />
          <Route path="/rating" element={<Rating role={role} />} />
          <Route path="/lectures" element={<Lectures role={role} />} />
          <Route
            path="/"
            element={role ? <Navigate to="/reports" replace /> : <h1>Welcome to LUCT Reporting System</h1>}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
