import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Card, InputGroup, FormControl, Alert } from 'react-bootstrap';

const Lectures = ({ role }) => {
  const [courses, setCourses] = useState([]);
  const [lecturers, setLecturers] = useState([]);
  const [formData, setFormData] = useState({ course_id: '', lecturer_id: '' });
  const [search, setSearch] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    fetchCourses();
    fetchLecturers();
  }, []);

  const fetchCourses = async (q = '') => {
    try {
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/courses${q ? `?q=${q}` : ''}`,
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setCourses(res.data);
    } catch (err) {
      console.error(err);
      setError('Error fetching courses');
    }
  };

  const fetchLecturers = async () => {
    try {
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/auth/users?role=lecturer`,
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setLecturers(res.data);
    } catch (err) {
      console.error(err);
      setError('Error fetching lecturers');
    }
  };

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    setError(null);
    setSuccess(null);

    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/lectures`,
        {
          course_id: Number(formData.course_id),
          lecturer_id: Number(formData.lecturer_id)
        },
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setFormData({ course_id: '', lecturer_id: '' });
      setSuccess(res.data.message);
      fetchCourses();
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Error assigning lecture');
    }
  };

  const handleSearch = e => {
    setSearch(e.target.value);
    fetchCourses(e.target.value);
  };

  return (
    <Card className="p-4">
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      {role === 'pl' && (
        <Form onSubmit={onSubmit} className="mb-4">
          <Form.Group className="mb-3">
            <Form.Label>Course</Form.Label>
            <Form.Select name="course_id" value={formData.course_id} onChange={onChange} required>
              <option value="">Select a course</option>
              {courses.map(course => (
                <option key={course.id} value={course.id}>
                  {course.name} ({course.code})
                </option>
              ))}
            </Form.Select>
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Lecturer</Form.Label>
            <Form.Select name="lecturer_id" value={formData.lecturer_id} onChange={onChange} required>
              <option value="">Select a lecturer</option>
              {lecturers.map(l => (
                <option key={l.id} value={l.id}>
                  {l.username}
                </option>
              ))}
            </Form.Select>
          </Form.Group>

          <Button variant="primary" type="submit">
            Assign Lecture
          </Button>
        </Form>
      )}

      <InputGroup className="mb-3">
        <FormControl placeholder="Search courses..." value={search} onChange={handleSearch} />
      </InputGroup>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Lecture ID</th>
            <th>Course ID</th>
            <th>Lecturer ID</th>
          </tr>
        </thead>
        <tbody>
          {courses.map(course =>
            course.lecturer_id ? (
              <tr key={course.id}>
                <td>{course.lecture_id || 'N/A'}</td>
                <td>{course.id}</td>
                <td>{course.lecturer_id}</td>
              </tr>
            ) : null
          )}
        </tbody>
      </Table>
    </Card>
  );
};

export default Lectures;
