import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Rating = ({ role, lectureId }) => {
  const [form, setForm] = useState({
    rating: '',
    comment: ''
  });
  const [message, setMessage] = useState('');
  const [ratings, setRatings] = useState([]);

  const token = localStorage.getItem('token');

  // Fetch existing ratings for this lecture
  const fetchRatings = async () => {
    if (!lectureId) return;
    try {
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/rating/${lectureId}`,
        { headers: { 'x-auth-token': token } }
      );
      setRatings(res.data || []);
    } catch (err) {
      console.error('Error fetching ratings:', err);
    }
  };

  useEffect(() => {
    fetchRatings();
  }, [lectureId]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.rating) {
      setMessage('Please select a rating');
      return;
    }

    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/rating`,
        {
          lecture_id: lectureId,
          rating: form.rating,
          comment: form.comment
        },
        { headers: { 'x-auth-token': token } }
      );
      setMessage('Rating submitted successfully!');
      setForm({ rating: '', comment: '' });
      fetchRatings(); // refresh ratings after submit
    } catch (err) {
      console.error('Rating submit error:', err);
      setMessage(err.response?.data?.message || 'Error submitting rating');
    }
  };

  return (
    <div style={{ maxWidth: '500px', margin: '20px auto' }}>
      <h2>{role === 'student' ? 'Rate Lecturer' : 'Rate Student'}</h2>
      {message && <p>{message}</p>}

      <form onSubmit={handleSubmit}>
        <label>Rating (1-5)</label>
        <select
          name="rating"
          value={form.rating}
          onChange={handleChange}
          required
        >
          <option value="">Select rating</option>
          {[1, 2, 3, 4, 5].map((n) => (
            <option key={n} value={n}>{n}</option>
          ))}
        </select>

        <label>Comment (optional)</label>
        <textarea
          name="comment"
          placeholder="Leave a comment..."
          value={form.comment}
          onChange={handleChange}
        />

        <button type="submit">Submit Rating</button>
      </form>

      <hr />
      <h3>Existing Ratings</h3>
      {ratings.length > 0 ? (
        <ul>
          {ratings.map((r) => (
            <li key={r.id}>
              <strong>{r.rating}/5</strong> - {r.comment || 'No comment'}{' '}
              <em>({r.student_id} → {r.lecturer_id})</em>
            </li>
          ))}
        </ul>
      ) : (
        <p>No ratings yet.</p>
      )}
    </div>
  );
};

export default Rating;
