import React, { useState, useEffect } from 'react';
import { Card, Alert } from 'react-bootstrap';
import axios from 'axios';

const Classes = () => {
  const [classes, setClasses] = useState([]);
  const [error, setError] = useState(null);
  const [query, setQuery] = useState('');

  const fetchClasses = async (q = '') => {
    const token = localStorage.getItem('token');
    console.log('Token used:', token);
    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }
    try {
      setError(null);
      const url = `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/classes${q ? `?q=${q}` : ''}`;
      console.log('Fetching from:', url); // Log the exact URL
      const res = await axios.get(url, {
        headers: { 'x-auth-token': token }
      });
      setClasses(res.data);
    } catch (err) {
      console.error('Fetch error:', err.response ? { status: err.response.status, data: err.response.data } : err);
      setError('Failed to load classes. ' + (err.response?.data?.msg || 'Check server or token.'));
    }
  };

  const handleSearch = (e) => {
    setQuery(e.target.value);
    fetchClasses(e.target.value);
  };

  useEffect(() => {
    fetchClasses();
  }, []);

  return (
    <Card className="p-4">
      <h2>Classes</h2>
      <input
        type="text"
        value={query}
        onChange={handleSearch}
        placeholder="Search classes (e.g., algebra)"
        className="mb-3 form-control"
      />
      {error && <Alert variant="danger">{error}</Alert>}
      {classes.length ? (
        <ul>
          {classes.map((classItem) => (
            <li key={classItem.id}>{classItem.name || classItem.course_code}</li> // Adjust based on schema
          ))}
        </ul>
      ) : (
        <p>No classes available.</p>
      )}
    </Card>
  );
};

export default Classes;
