import React, { useState } from 'react';
import axios from 'axios';

const Rating = ({ role }) => {
  const [form, setForm] = useState({ lecture_id: '', target_id: '', rating: '', comment: '' });
  const [message, setMessage] = useState('');
  const [ratings, setRatings] = useState([]);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/rating`,
        form,
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setMessage(res.data.message);
      setRatings(res.data.ratings); // show all existing ratings for this lecture
      setForm({ lecture_id: '', target_id: '', rating: '', comment: '' });
    } catch (err) {
      setMessage(err.response?.data?.message || 'Error submitting rating');
      console.error('Rating submit error:', err);
    }
  };

  return (
    <div>
      <h2>{role === 'student' ? 'Rate Lecturer' : 'Rate Student'}</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input type="text" name="lecture_id" placeholder="Lecture ID" value={form.lecture_id} onChange={handleChange} required />
        <input type="text" name="target_id" placeholder={role === 'student' ? 'Lecturer ID' : 'Student ID'} value={form.target_id} onChange={handleChange} required />
        <input type="number" name="rating" placeholder="Rating (1-5)" min="1" max="5" value={form.rating} onChange={handleChange} required />
        <textarea name="comment" placeholder="Comment (optional)" value={form.comment} onChange={handleChange} />
        <button type="submit">Submit Rating</button>
      </form>

      <h3>Existing Ratings:</h3>
      {ratings.length ? (
        <ul>
          {ratings.map(r => (
            <li key={r.id}>
              Student: {r.student_id}, Lecturer: {r.lecturer_id}, Rating: {r.rating}, Comment: {r.comment || 'None'}
            </li>
          ))}
        </ul>
      ) : (
        <p>No ratings yet.</p>
      )}
    </div>
  );
};

export default Rating;
