import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Table, Button, Form, Modal, Alert } from 'react-bootstrap';

const Lectures = ({ role }) => {
  const [lectures, setLectures] = useState([]);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [newLecture, setNewLecture] = useState({ course_id: '', lecturer_id: '', date_of_lecture: '' });
  const [editingLectureId, setEditingLectureId] = useState(null);
  const [editFormData, setEditFormData] = useState({ course_id: '', lecturer_id: '', date_of_lecture: '' });

  useEffect(() => {
    fetchLectures();
  }, []);

  const fetchLectures = async () => {
    try {
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/api/lectures`,
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setLectures(res.data || []);
      setError(null);
      setSuccess(null);
    } catch (err) {
      console.error('Error fetching lectures:', err);
      setError(err.response?.data?.message || 'Error fetching lectures');
      setLectures([]);
    }
  };

  const handleNewChange = (e) => {
    setNewLecture({ ...newLecture, [e.target.name]: e.target.value });
  };

  const submitNewLecture = async () => {
    if (role !== 'pl') return;
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token.');
      return;
    }

    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/api/lectures`,
        newLecture,
        { headers: { 'x-auth-token': token } }
      );
      setShowCreateModal(false);
      setNewLecture({ course_id: '', lecturer_id: '', date_of_lecture: '' });
      setSuccess('Lecture created successfully');
      fetchLectures();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Error creating lecture');
    }
  };

  const startEdit = (lecture) => {
    if (role !== 'pl') return;
    setEditingLectureId(lecture.id);
    setEditFormData({
      course_id: lecture.course_id,
      lecturer_id: lecture.lecturer_id,
      date_of_lecture: lecture.date_of_lecture,
    });
  };

  const handleEditChange = (e) => {
    setEditFormData({ ...editFormData, [e.target.name]: e.target.value });
  };

  const submitEdit = async (lectureId) => {
    if (role !== 'pl') return;
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token.');
      return;
    }

    try {
      await axios.put(
        `${process.env.REACT_APP_API_URL}/api/lectures/${lectureId}`,
        editFormData,
        { headers: { 'x-auth-token': token } }
      );
      setEditingLectureId(null);
      setSuccess('Lecture updated successfully');
      fetchLectures();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Error updating lecture');
    }
  };

  const handleDelete = async (lectureId) => {
    if (role !== 'pl') return;
    if (!window.confirm('Are you sure you want to delete this lecture?')) return;
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token.');
      return;
    }

    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL}/api/lectures/${lectureId}`,
        { headers: { 'x-auth-token': token } }
      );
      setSuccess('Lecture deleted successfully');
      fetchLectures();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Error deleting lecture');
    }
  };

  return (
    <Card className="p-4">
      <h2>Lectures Management</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      {role === 'pl' && (
        <Button variant="primary" className="mb-3" onClick={() => setShowCreateModal(true)}>
          Add New Lecture
        </Button>
      )}

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Course ID</th>
            <th>Lecturer ID</th>
            <th>Date of Lecture</th>
            {role === 'pl' && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {lectures.length > 0 ? (
            lectures.map((lecture) => (
              <tr key={lecture.id}>
                <td>{lecture.id}</td>
                <td>{lecture.course_id}</td>
                <td>{lecture.lecturer_id}</td>
                <td>{new Date(lecture.date_of_lecture).toLocaleDateString()}</td>
                {role === 'pl' && (
                  <td>
                    {editingLectureId === lecture.id ? (
                      <Form
                        onSubmit={(e) => {
                          e.preventDefault();
                          submitEdit(lecture.id);
                        }}
                      >
                        <Form.Group className="mb-2">
                          <Form.Control
                            type="text"
                            name="course_id"
                            value={editFormData.course_id}
                            onChange={handleEditChange}
                            placeholder="Course ID"
                            required
                          />
                        </Form.Group>
                        <Form.Group className="mb-2">
                          <Form.Control
                            type="text"
                            name="lecturer_id"
                            value={editFormData.lecturer_id}
                            onChange={handleEditChange}
                            placeholder="Lecturer ID"
                            required
                          />
                        </Form.Group>
                        <Form.Group className="mb-2">
                          <Form.Control
                            type="date"
                            name="date_of_lecture"
                            value={editFormData.date_of_lecture}
                            onChange={handleEditChange}
                            required
                          />
                        </Form.Group>
                        <Button type="submit" variant="primary" className="me-2">
                          Save
                        </Button>
                        <Button
                          variant="secondary"
                          onClick={() => setEditingLectureId(null)}
                        >
                          Cancel
                        </Button>
                      </Form>
                    ) : (
                      <>
                        <Button
                          variant="warning"
                          className="me-2"
                          onClick={() => startEdit(lecture)}
                        >
                          Edit
                        </Button>
                        <Button
                          variant="danger"
                          onClick={() => handleDelete(lecture.id)}
                        >
                          Delete
                        </Button>
                      </>
                    )}
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={role === 'pl' ? 5 : 4} className="text-center">
                No lectures found.
              </td>
            </tr>
          )}
        </tbody>
      </Table>

      {/* Create New Lecture Modal */}
      {role === 'pl' && (
        <Modal show={showCreateModal} onHide={() => setShowCreateModal(false)}>
          <Modal.Header closeButton>
            <Modal.Title>Add New Lecture</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Course ID</Form.Label>
                <Form.Control
                  type="text"
                  name="course_id"
                  value={newLecture.course_id}
                  onChange={handleNewChange}
                  placeholder="Enter Course ID"
                  required
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Lecturer ID</Form.Label>
                <Form.Control
                  type="text"
                  name="lecturer_id"
                  value={newLecture.lecturer_id}
                  onChange={handleNewChange}
                  placeholder="Enter Lecturer ID"
                  required
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Date of Lecture</Form.Label>
                <Form.Control
                  type="date"
                  name="date_of_lecture"
                  value={newLecture.date_of_lecture}
                  onChange={handleNewChange}
                  required
                />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowCreateModal(false)}>
              Cancel
            </Button>
            <Button variant="primary" onClick={submitNewLecture}>
              Create
            </Button>
          </Modal.Footer>
        </Modal>
      )}
    </Card>
  );
};

export default Lectures;
