import React, { useState, useEffect, useCallback } from 'react';
import { Form, Button, Card, Alert, Modal } from 'react-bootstrap';
import axios from 'axios';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';

const Reports = ({ role }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);
  const [feedbacks, setFeedbacks] = useState({});
  const [expandedReportId, setExpandedReportId] = useState(null);
  const [editReportId, setEditReportId] = useState(null);
  const [editFormData, setEditFormData] = useState({});
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [deleteReportId, setDeleteReportId] = useState(null);

  // Fetch reports
  const fetchReports = useCallback(async (q = '') => {
    const token = localStorage.getItem('token');
    if (!token) return setError('No token. Please log in.');

    let url;
    if (role === 'pl') url = `${process.env.REACT_APP_API_URL}/api/reports/prl-feedback`; // Added /api prefix
    else url = `${process.env.REACT_APP_API_URL}/api/reports${q ? `?q=${q}` : ''}`; // Added /api prefix

    try {
      setError(null);
      const response = await axios.get(url, { headers: { 'x-auth-token': token } });
      setReports(response.data);
    } catch (err) {
      if (err.response) setError(err.response.data.msg || 'Server error');
      else setError(err.message);
    }
  }, [role]);

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  // Input change handlers
  const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
  const handleEditChange = (e) => setEditFormData({ ...editFormData, [e.target.name]: e.target.value });

  // Submit new report
  const onSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return setError('No token. Please log in.');

    try {
      setError(null);
      const submitData = {
        faculty_name: formData.faculty_name,
        class_name: formData.class_name,
        week_of_reporting: parseInt(formData.week_of_reporting) || 0,
        date_of_lecture: new Date(formData.date_of_lecture).toISOString().split('T')[0],
        course_name: formData.course_name,
        course_code: formData.course_code,
        lecturer_name: formData.lecturer_name,
        actual_number_of_students_present: parseInt(formData.actual_students) || 0,
        total_number_of_registered_students: parseInt(formData.total_registered) || 0,
        venue: formData.venue,
        scheduled_lecture_time: formData.scheduled_time,
        topic_taught: formData.topic_taught,
        learning_outcomes: formData.learning_outcomes,
        recommendations: formData.recommendations,
        lecturer_id: formData.lecturer_id || null
      };

      await axios.post(`${process.env.REACT_APP_API_URL}/api/reports`, submitData, { headers: { 'x-auth-token': token } }); // Added /api prefix
      alert('Report submitted successfully');
      setFormData({
        faculty_name: '', class_name: '', week_of_reporting: '', date_of_lecture: '',
        course_name: '', course_code: '', lecturer_name: '', actual_students: '', total_registered: '',
        venue: '', scheduled_time: '', topic_taught: '', learning_outcomes: '', recommendations: '', lecturer_id: ''
      });
      fetchReports();
    } catch (err) {
      if (err.response) setError(err.response.data.msg || 'Server error');
      else setError(err.message);
    }
  };

  // Edit report
  const handleEditClick = (report) => {
    setEditReportId(report.id);
    setEditFormData({
      faculty_name: report.faculty_name || '',
      class_name: report.class_name || '',
      week_of_reporting: report.week_of_reporting || '',
      date_of_lecture: report.date_of_lecture || '',
      course_name: report.course_name || '',
      course_code: report.course_code || '',
      lecturer_name: report.lecturer_name || '',
      actual_students: report.actual_number_of_students_present || '',
      total_registered: report.total_number_of_registered_students || '',
      venue: report.venue || '',
      scheduled_time: report.scheduled_lecture_time || '',
      topic_taught: report.topic_taught || '',
      learning_outcomes: report.learning_outcomes || '',
      recommendations: report.recommendations || '',
      lecturer_id: report.lecturer_id || ''
    });
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return setError('No token. Please log in.');

    try {
      setError(null);
      const submitData = {
        faculty_name: editFormData.faculty_name,
        class_name: editFormData.class_name,
        week_of_reporting: parseInt(editFormData.week_of_reporting) || 0,
        date_of_lecture: new Date(editFormData.date_of_lecture).toISOString().split('T')[0],
        course_name: editFormData.course_name,
        course_code: editFormData.course_code,
        lecturer_name: editFormData.lecturer_name,
        actual_number_of_students_present: parseInt(editFormData.actual_students) || 0,
        total_number_of_registered_students: parseInt(editFormData.total_registered) || 0,
        venue: editFormData.venue,
        scheduled_lecture_time: editFormData.scheduled_time,
        topic_taught: editFormData.topic_taught,
        learning_outcomes: editFormData.learning_outcomes,
        recommendations: editFormData.recommendations,
        lecturer_id: editFormData.lecturer_id || null
      };

      await axios.put(`${process.env.REACT_APP_API_URL}/api/reports/${editReportId}`, submitData, { headers: { 'x-auth-token': token } }); // Added /api prefix
      alert('Report updated successfully');
      setEditReportId(null);
      setEditFormData({});
      fetchReports();
    } catch (err) {
      if (err.response) setError(err.response.data.msg || 'Server error');
      else setError(err.message);
    }
  };

  // Delete report
  const handleDeleteClick = (id) => { setDeleteReportId(id); setShowDeleteModal(true); };
  const confirmDelete = async () => {
    const token = localStorage.getItem('token');
    if (!token) { setError('No token'); setShowDeleteModal(false); return; }

    try {
      setError(null);
      await axios.delete(`${process.env.REACT_APP_API_URL}/api/reports/${deleteReportId}`, { headers: { 'x-auth-token': token } }); // Added /api prefix
      alert('Report deleted successfully');
      setShowDeleteModal(false);
      setDeleteReportId(null);
      fetchReports();
    } catch (err) {
      setShowDeleteModal(false);
      setDeleteReportId(null);
      if (err.response) setError(err.response.data.msg || 'Server error');
      else setError(err.message);
    }
  };
  const cancelDelete = () => { setShowDeleteModal(false); setDeleteReportId(null); };

  // PRL feedback
  const handleFeedbackSubmit = async (e, reportId) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return setError('No token');

    try {
      setError(null);
      await axios.post(`${process.env.REACT_APP_API_URL}/api/reports/feedback/${reportId}`, { feedback: feedbacks[reportId] }, { headers: { 'x-auth-token': token } }); // Added /api prefix
      setFeedbacks({ ...feedbacks, [reportId]: '' });
      fetchReports();
    } catch (err) {
      if (err.response) setError(err.response.data.msg || 'Server error');
      else setError(err.message);
    }
  };

  // Export
  const exportToExcel = () => {
    if (reports.length === 0) return alert('No reports to export');
    const worksheet = XLSX.utils.json_to_sheet(reports);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Reports');
    const excelBuffer = XLSX.write(workbook, { bookType: 'xlsx', type: 'array' });
    saveAs(new Blob([excelBuffer], { type: 'application/octet-stream' }), 'reports.xlsx');
  };

  return (
    <div className="container my-4">
      <h2>Reports</h2>
      {error && <Alert variant="danger">{error}</Alert>}

      {(role === 'lecturer' || role === 'admin') && (
        <Card className="mb-4">
          <Card.Header>Submit New Report</Card.Header>
          <Card.Body>
            <Form onSubmit={onSubmit}>
              <Form.Group controlId="faculty_name"><Form.Label>Faculty Name</Form.Label><Form.Control type="text" name="faculty_name" value={formData.faculty_name} onChange={onChange} required/></Form.Group>
              <Form.Group controlId="class_name"><Form.Label>Class Name</Form.Label><Form.Control type="text" name="class_name" value={formData.class_name} onChange={onChange} required/></Form.Group>
              <Form.Group controlId="week_of_reporting"><Form.Label>Week of Reporting</Form.Label><Form.Control type="number" name="week_of_reporting" value={formData.week_of_reporting} onChange={onChange} required min="1"/></Form.Group>
              <Form.Group controlId="date_of_lecture"><Form.Label>Date of Lecture</Form.Label><Form.Control type="date" name="date_of_lecture" value={formData.date_of_lecture} onChange={onChange} required/></Form.Group>
              <Form.Group controlId="course_name"><Form.Label>Course Name</Form.Label><Form.Control type="text" name="course_name" value={formData.course_name} onChange={onChange} required/></Form.Group>
              <Form.Group controlId="course_code"><Form.Label>Course Code</Form.Label><Form.Control type="text" name="course_code" value={formData.course_code} onChange={onChange} required/></Form.Group>
              <Form.Group controlId="lecturer_name"><Form.Label>Lecturer Name</Form.Label><Form.Control type="text" name="lecturer_name" value={formData.lecturer_name} onChange={onChange} required/></Form.Group>
              <Form.Group controlId="actual_students"><Form.Label>Actual Students Present</Form.Label><Form.Control type="number" name="actual_students" value={formData.actual_students} onChange={onChange} required min="0"/></Form.Group>
              <Form.Group controlId="total_registered"><Form.Label>Total Registered Students</Form.Label><Form.Control type="number" name="total_registered" value={formData.total_registered} onChange={onChange} required min="0"/></Form.Group>
              <Form.Group controlId="venue"><Form.Label>Venue</Form.Label><Form.Control type="text" name="venue" value={formData.venue} onChange={onChange} required/></Form.Group>
              <Form.Group controlId="scheduled_time"><Form.Label>Scheduled Lecture Time</Form.Label><Form.Control type="text" name="scheduled_time" value={formData.scheduled_time} onChange={onChange} required placeholder="e.g. 09:00 - 10:30"/></Form.Group>
              <Form.Group controlId="topic_taught"><Form.Label>Topic Taught</Form.Label><Form.Control as="textarea" rows={2} name="topic_taught" value={formData.topic_taught} onChange={onChange} required/></Form.Group>
              <Form.Group controlId="learning_outcomes"><Form.Label>Learning Outcomes</Form.Label><Form.Control as="textarea" rows={2} name="learning_outcomes" value={formData.learning_outcomes} onChange={onChange} required/></Form.Group>
              <Form.Group controlId="recommendations"><Form.Label>Recommendations</Form.Label><Form.Control as="textarea" rows={2} name="recommendations" value={formData.recommendations} onChange={onChange}/></Form.Group>
              <Form.Group controlId="lecturer_id" className="d-none"><Form.Control type="text" name="lecturer_id" value={formData.lecturer_id} onChange={onChange}/></Form.Group>
              <Button type="submit" variant="primary" className="mt-3">Submit Report</Button>
            </Form>
          </Card.Body>
        </Card>
      )}

      <Button variant="success" className="mb-3" onClick={exportToExcel}>Export Reports to Excel</Button>

      {reports.length === 0 ? <p>No reports available.</p> : reports.map(report => (
        <Card key={report.id} className="mb-3">
          <Card.Header onClick={() => setExpandedReportId(expandedReportId === report.id ? null : report.id)} style={{ cursor: 'pointer' }}>
            <strong>{report.course_name} - Week {report.week_of_reporting}</strong>
            <small className="text-muted float-end">{report.date_of_lecture}</small>
          </Card.Header>
          {expandedReportId === report.id && (
            <Card.Body>
              {editReportId === report.id ? (
                <Form onSubmit={handleEditSubmit}>
                  <Form.Group controlId="faculty_name_edit"><Form.Label>Faculty Name</Form.Label><Form.Control type="text" name="faculty_name" value={editFormData.faculty_name} onChange={handleEditChange} required/></Form.Group>
                  <Form.Group controlId="class_name_edit"><Form.Label>Class Name</Form.Label><Form.Control type="text" name="class_name" value={editFormData.class_name} onChange={handleEditChange} required/></Form.Group>
                  <Form.Group controlId="week_of_reporting_edit"><Form.Label>Week of Reporting</Form.Label><Form.Control type="number" name="week_of_reporting" value={editFormData.week_of_reporting} onChange={handleEditChange} required min="1"/></Form.Group>
                  <Form.Group controlId="date_of_lecture_edit"><Form.Label>Date of Lecture</Form.Label><Form.Control type="date" name="date_of_lecture" value={editFormData.date_of_lecture} onChange={handleEditChange} required/></Form.Group>
                  <Form.Group controlId="course_name_edit"><Form.Label>Course Name</Form.Label><Form.Control type="text" name="course_name" value={editFormData.course_name} onChange={handleEditChange} required/></Form.Group>
                  <Form.Group controlId="course_code_edit"><Form.Label>Course Code</Form.Label><Form.Control type="text" name="course_code" value={editFormData.course_code} onChange={handleEditChange} required/></Form.Group>
                  <Form.Group controlId="lecturer_name_edit"><Form.Label>Lecturer Name</Form.Label><Form.Control type="text" name="lecturer_name" value={editFormData.lecturer_name} onChange={handleEditChange} required/></Form.Group>
                  <Form.Group controlId="actual_students_edit"><Form.Label>Actual Students Present</Form.Label><Form.Control type="number" name="actual_students" value={editFormData.actual_students} onChange={handleEditChange} required min="0"/></Form.Group>
                  <Form.Group controlId="total_registered_edit"><Form.Label>Total Registered Students</Form.Label><Form.Control type="number" name="total_registered" value={editFormData.total_registered} onChange={handleEditChange} required min="0"/></Form.Group>
                  <Form.Group controlId="venue_edit"><Form.Label>Venue</Form.Label><Form.Control type="text" name="venue" value={editFormData.venue} onChange={handleEditChange} required/></Form.Group>
                  <Form.Group controlId="scheduled_time_edit"><Form.Label>Scheduled Lecture Time</Form.Label><Form.Control type="text" name="scheduled_time" value={editFormData.scheduled_time} onChange={handleEditChange} required/></Form.Group>
                  <Form.Group controlId="topic_taught_edit"><Form.Label>Topic Taught</Form.Label><Form.Control as="textarea" rows={2} name="topic_taught" value={editFormData.topic_taught} onChange={handleEditChange} required/></Form.Group>
                  <Form.Group controlId="learning_outcomes_edit"><Form.Label>Learning Outcomes</Form.Label><Form.Control as="textarea" rows={2} name="learning_outcomes" value={editFormData.learning_outcomes} onChange={handleEditChange} required/></Form.Group>
                  <Form.Group controlId="recommendations_edit"><Form.Label>Recommendations</Form.Label><Form.Control as="textarea" rows={2} name="recommendations" value={editFormData.recommendations} onChange={handleEditChange}/></Form.Group>
                  <Button type="submit" variant="primary" className="me-2 mt-3">Save</Button>
                  <Button variant="secondary" className="mt-3" onClick={() => setEditReportId(null)}>Cancel</Button>
                </Form>
              ) : (
                <>
                  <p><strong>Class Name:</strong> {report.class_name}</p>
                  <p><strong>Course Code:</strong> {report.course_code}</p>
                  <p><strong>Lecturer Name:</strong> {report.lecturer_name}</p>
                  <p><strong>Actual Students:</strong> {report.actual_number_of_students_present}</p>
                  <p><strong>Total Registered:</strong> {report.total_number_of_registered_students}</p>
                  <p><strong>Venue:</strong> {report.venue}</p>
                  <p><strong>Scheduled Time:</strong> {report.scheduled_lecture_time}</p>
                  <p><strong>Topic Taught:</strong> {report.topic_taught}</p>
                  <p><strong>Learning Outcomes:</strong> {report.learning_outcomes}</p>
                  <p><strong>Recommendations:</strong> {report.recommendations}</p>
                  {(role === 'lecturer' || role === 'admin') && (
                    <>
                      <Button variant="warning" size="sm" className="me-2" onClick={() => handleEditClick(report)}>Edit</Button>
                      <Button variant="danger" size="sm" onClick={() => handleDeleteClick(report.id)}>Delete</Button>
                    </>
                  )}
                  {role === 'pl' && (
                    <Form className="mt-3" onSubmit={(e) => handleFeedbackSubmit(e, report.id)}>
                      <Form.Group controlId={`feedback-${report.id}`}>
                        <Form.Label>PRL Feedback</Form.Label>
                        <Form.Control type="text" value={feedbacks[report.id] || ''} onChange={(e) => setFeedbacks({ ...feedbacks, [report.id]: e.target.value })}/>
                      </Form.Group>
                      <Button type="submit" className="mt-2">Submit Feedback</Button>
                    </Form>
                  )}
                </>
              )}
            </Card.Body>
          )}
        </Card>
      ))}

      <Modal show={showDeleteModal} onHide={cancelDelete}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Delete</Modal.Title>
        </Modal.Header>
        <Modal.Body>Are you sure you want to delete this report?</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={cancelDelete}>Cancel</Button>
          <Button variant="danger" onClick={confirmDelete}>Delete</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Reports;
