import React, { useState, useEffect } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';

const Reports = ({ role }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);

  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const fetchReports = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in.');
      return;
    }

    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/reports`, {
        headers: { 'x-auth-token': token }
      });
      setReports(res.data);
    } catch (err) {
      console.error('Fetch error:', err);
      setError(err.response?.data?.msg || 'Failed to fetch reports.');
    }
  };

  const onSubmit = async e => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No token found. Please log in.');
      return;
    }

    const submitData = {
      faculty_name: formData.faculty_name,
      class_name: formData.class_name,
      week_of_reporting: parseInt(formData.week_of_reporting) || 0,
      date_of_lecture: formData.date_of_lecture,
      course_name: formData.course_name,
      course_code: formData.course_code,
      lecturer_name: formData.lecturer_name,
      actual_number_of_students_present: parseInt(formData.actual_students) || 0,
      total_number_of_registered_students: parseInt(formData.total_registered) || 0,
      venue: formData.venue,
      scheduled_lecture_time: formData.scheduled_time,
      topic_taught: formData.topic_taught,
      learning_outcomes: formData.learning_outcomes,
      recommendations: formData.recommendations
    };

    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/reports`, submitData, {
        headers: { 'x-auth-token': token }
      });
      alert('Report submitted successfully');
      setFormData({
        faculty_name: '', class_name: '', week_of_reporting: '', date_of_lecture: '',
        course_name: '', course_code: '', lecturer_name: '', actual_students: '',
        total_registered: '', venue: '', scheduled_time: '', topic_taught: '',
        learning_outcomes: '', recommendations: ''
      });
      fetchReports();
    } catch (err) {
      console.error('Submit error:', err);
      setError(err.response?.data?.msg || 'Failed to submit report.');
    }
  };

  useEffect(() => {
    fetchReports();
  }, []);

  return (
    <Card className="p-4">
      <h2>Submit Report</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form onSubmit={onSubmit}>
        <Form.Group controlId="faculty_name" className="mb-3">
          <Form.Label>Faculty Name</Form.Label>
          <Form.Control
            type="text"
            name="faculty_name"
            value={formData.faculty_name}
            onChange={onChange}
            required
          />
        </Form.Group>

        <Form.Group controlId="class_name" className="mb-3">
          <Form.Label>Class Name</Form.Label>
          <Form.Control
            type="text"
            name="class_name"
            value={formData.class_name}
            onChange={onChange}
            required
          />
        </Form.Group>

        {/* Add other fields in the same pattern */}
        <Button variant="primary" type="submit">Submit Report</Button>
      </Form>

      <h3 className="mt-4">Reports</h3>
      {reports.length ? (
        <ul>
          {reports.map(report => (
            <li key={report.id}>{report.topic_taught} - {report.course_name}</li>
          ))}
        </ul>
      ) : <p>No reports available.</p>}
    </Card>
  );
};

export default Reports;
