// frontend/src/components/Classes.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, Card, InputGroup, FormControl } from 'react-bootstrap';

const Classes = ({ role }) => {
  const [classes, setClasses] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchClasses();
  }, []);

  const fetchClasses = async (q = '') => {
    try {
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/classes${q ? `?q=${q}` : ''}`,
        {
          headers: {
            'x-auth-token': localStorage.getItem('token'),
          },
        }
      );
      setClasses(res.data || []);
    } catch (err) {
      console.error('Error fetching classes:', err);
      setClasses([]);
    }
  };

  const handleSearch = (e) => {
    const value = e.target.value;
    setSearch(value);
    fetchClasses(value);
  };

  return (
    <Card className="p-4">
      <InputGroup className="mb-3 search-input">
        <FormControl
          placeholder="Search classes..."
          value={search}
          onChange={handleSearch}
        />
      </InputGroup>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Venue</th>
          </tr>
        </thead>
        <tbody>
          {classes.length > 0 ? (
            classes.map((cls) => (
              <tr key={cls.class_id}>
                <td>{cls.class_id}</td>
                <td>{cls.class_name}</td>
                <td>{cls.venue}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3" className="text-center">
                No classes found.
              </td>
            </tr>
          )}
        </tbody>
      </Table>
    </Card>
  );
};

export default Classes;
