import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Card, InputGroup, FormControl } from 'react-bootstrap';

const Lectures = ({ role }) => {
  const [lectures, setLectures] = useState([]);
  const [reports, setReports] = useState([]);
  const [formData, setFormData] = useState({ report_id: '', lecturer_id: '', date_of_lecture: '' });
  const [search, setSearch] = useState('');

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  // Fetch all lectures
  const fetchLectures = async () => {
    try {
      const res = await axios.get(`${API_URL}/lectures`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setLectures(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  // Fetch reports (for selecting lecture names)
  const fetchReports = async () => {
    try {
      const res = await axios.get(`${API_URL}/reports`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setReports(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchLectures();
    fetchReports();
  }, []);

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post(`${API_URL}/lectures`, formData, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setFormData({ report_id: '', lecturer_id: '', date_of_lecture: '' });
      fetchLectures();
    } catch (err) {
      console.error(err);
    }
  };

  const deleteLecture = async (id) => {
    if (!window.confirm('Are you sure you want to delete this lecture?')) return;
    try {
      await axios.delete(`${API_URL}/lectures/${id}`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchLectures();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Card className="p-4">
      {role === 'pl' && (
        <Form onSubmit={onSubmit} className="mb-4">
          <Form.Group controlId="report_id" className="mb-3">
            <Form.Label>Lecture Name (from Reports)</Form.Label>
            <Form.Control as="select" name="report_id" value={formData.report_id} onChange={onChange} required>
              <option value="">Select Lecture</option>
              {reports.map(r => (
                <option key={r.id} value={r.id}>{r.course_name}</option>
              ))}
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="lecturer_id" className="mb-3">
            <Form.Label>Lecturer ID</Form.Label>
            <Form.Control type="number" name="lecturer_id" value={formData.lecturer_id} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="date_of_lecture" className="mb-3">
            <Form.Label>Date of Lecture (optional)</Form.Label>
            <Form.Control type="date" name="date_of_lecture" value={formData.date_of_lecture} onChange={onChange} />
          </Form.Group>
          <Button variant="primary" type="submit">Assign Lecture</Button>
        </Form>
      )}

      <InputGroup className="mb-3">
        <FormControl placeholder="Search lectures..." value={search} onChange={e => setSearch(e.target.value)} />
      </InputGroup>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Lecture Name</th>
            <th>Course ID</th>
            <th>Lecturer Name</th>
            <th>Lecturer ID</th>
            <th>Date</th>
            {role === 'pl' && <th>Action</th>}
          </tr>
        </thead>
        <tbody>
          {lectures
            .filter(l => !search || (l.lecture_name && l.lecture_name.toLowerCase().includes(search.toLowerCase())))
            .map(l => (
              <tr key={l.id}>
                <td>{l.id}</td>
                <td>{l.lecture_name || 'N/A'}</td>
                <td>{l.course_id}</td>
                <td>{l.lecturer_name || 'N/A'}</td>
                <td>{l.lecturer_id}</td>
                <td>{l.date_of_lecture ? new Date(l.date_of_lecture).toLocaleDateString() : '-'}</td>
                {role === 'pl' && (
                  <td>
                    <Button variant="danger" size="sm" onClick={() => deleteLecture(l.id)}>Delete</Button>
                  </td>
                )}
              </tr>
            ))}
        </tbody>
      </Table>
    </Card>
  );
};

export default Lectures;
