import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, Card, InputGroup, FormControl, Form, Button, Alert } from 'react-bootstrap';

const Classes = ({ role, onReportUpdate }) => {
  const [classes, setClasses] = useState([]);
  const [search, setSearch] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [editingClassId, setEditingClassId] = useState(null);
  const [editFormData, setEditFormData] = useState({ class_name: '', venue: '' });

  useEffect(() => {
    fetchClasses();
  }, [onReportUpdate]); // Re-fetch classes when reports are updated

  const fetchClasses = async (q = '') => {
    try {
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/classes${q ? `?q=${q}` : ''}`,
        {
          headers: {
            'x-auth-token': localStorage.getItem('token'),
          },
        }
      );
      setClasses(res.data || []);
      setError(null);
      setSuccess(null);
    } catch (err) {
      console.error('Error fetching classes:', err);
      setError(err.response?.data?.message || 'Error fetching classes');
      setSuccess(null);
      setClasses([]);
    }
  };

  const handleSearch = (e) => {
    const value = e.target.value;
    setSearch(value);
    fetchClasses(value);
  };

  const startEdit = (cls) => {
    setEditingClassId(cls.class_id);
    setEditFormData({
      class_name: cls.class_name,
      venue: cls.venue,
    });
    setError(null);
    setSuccess(null);
  };

  const handleEditChange = (e) => {
    setEditFormData({ ...editFormData, [e.target.name]: e.target.value });
  };

  const submitEdit = async (classId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token.');
      return;
    }

    try {
      await axios.put(
        `${process.env.REACT_APP_API_URL}/classes/${classId}`,
        editFormData,
        {
          headers: { 'x-auth-token': token },
        }
      );
      setEditingClassId(null);
      setSuccess('Class updated successfully');
      setError(null);
      fetchClasses(search); // Re-fetch with current search query
      setTimeout(() => setSuccess(null), 3000); // Clear success message
    } catch (err) {
      setError(err.response?.data?.message || 'Error updating class');
      setSuccess(null);
    }
  };

  return (
    <Card className="p-4">
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}
      <InputGroup className="mb-3 search-input">
        <FormControl
          placeholder="Search classes..."
          value={search}
          onChange={handleSearch}
        />
      </InputGroup>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Venue</th>
            {role === 'lecturer' && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {classes.length > 0 ? (
            classes.map((cls) => (
              <tr key={cls.class_id}>
                <td>{cls.class_id}</td>
                <td>{cls.class_name}</td>
                <td>{cls.venue}</td>
                {role === 'lecturer' && (
                  <td>
                    {editingClassId === cls.class_id ? (
                      <Form
                        onSubmit={(e) => {
                          e.preventDefault();
                          submitEdit(cls.class_id);
                        }}
                      >
                        <Form.Group className="mb-2">
                          <Form.Control
                            type="text"
                            name="class_name"
                            value={editFormData.class_name}
                            onChange={handleEditChange}
                            placeholder="Class Name"
                            required
                          />
                        </Form.Group>
                        <Form.Group className="mb-2">
                          <Form.Control
                            type="text"
                            name="venue"
                            value={editFormData.venue}
                            onChange={handleEditChange}
                            placeholder="Venue"
                            required
                          />
                        </Form.Group>
                        <Button type="submit" variant="primary" className="me-2">
                          Save
                        </Button>
                        <Button
                          variant="secondary"
                          onClick={() => setEditingClassId(null)}
                        >
                          Cancel
                        </Button>
                      </Form>
                    ) : (
                      <Button
                        variant="warning"
                        onClick={() => startEdit(cls)}
                      >
                        Edit
                      </Button>
                    )}
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={role === 'lecturer' ? 4 : 3} className="text-center">
                No classes found.
              </td>
            </tr>
          )}
        </tbody>
      </Table>
    </Card>
  );
};

export default Classes;
