import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Rating = ({ role }) => {
  const [form, setForm] = useState({
    lecture_id: '',
    target_id: '', // student_id if lecturer, lecturer_id if student
    rating: '',
    comment: ''
  });
  const [message, setMessage] = useState('');
  const [ratings, setRatings] = useState([]); // Store existing ratings

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const fetchRatings = async (lectureId) => {
    if (!lectureId) return;
    try {
      const res = await axios.get(
        `${process.env.REACT_APP_API_URL}/rating/${lectureId}`,
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setRatings(res.data || []);
    } catch (err) {
      console.error('Error fetching ratings:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_URL}/rating`,
        form,
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setMessage(res.data.message);
      setForm({ lecture_id: '', target_id: '', rating: '', comment: '' });

      // Fetch updated ratings after successful submit
      fetchRatings(form.lecture_id);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Error submitting rating');
      console.error('Rating submit error:', err);
    }
  };

  // Fetch ratings whenever lecture_id changes
  useEffect(() => {
    if (form.lecture_id) fetchRatings(form.lecture_id);
  }, [form.lecture_id]);

  return (
    <div>
      <h2>{role === 'student' ? 'Rate Lecturer' : 'Rate Student'}</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="lecture_id"
          placeholder="Lecture ID"
          value={form.lecture_id}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="target_id"
          placeholder={role === 'student' ? 'Lecturer ID' : 'Student ID'}
          value={form.target_id}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="rating"
          placeholder="Rating (1-5)"
          min="1"
          max="5"
          value={form.rating}
          onChange={handleChange}
          required
        />
        <textarea
          name="comment"
          placeholder="Comment (optional)"
          value={form.comment}
          onChange={handleChange}
        />
        <button type="submit">Submit Rating</button>
      </form>

      {/* Display existing ratings in a table */}
      {ratings.length > 0 && (
        <div style={{ marginTop: '20px' }}>
          <h3>Existing Ratings for Lecture {form.lecture_id}:</h3>
          <table border="1" cellPadding="8" cellSpacing="0">
            <thead>
              <tr>
                <th>ID</th>
                <th>Student ID</th>
                <th>Lecturer ID</th>
                <th>Rating</th>
                <th>Comment</th>
                <th>Created At</th>
              </tr>
            </thead>
            <tbody>
              {ratings.map((r) => (
                <tr key={r.id}>
                  <td>{r.id}</td>
                  <td>{r.student_id}</td>
                  <td>{r.lecturer_id}</td>
                  <td>{r.rating}</td>
                  <td>{r.comment || 'N/A'}</td>
                  <td>{new Date(r.created_at).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Rating;
