import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Table, Button, Form, Modal, Alert } from 'react-bootstrap';

const Lectures = ({ role }) => {
  const [lectures, setLectures] = useState([]);
  const [courses, setCourses] = useState([]);
  const [lecturers, setLecturers] = useState([]);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [newLecture, setNewLecture] = useState({ course_id: '', lecturer_id: '', date_of_lecture: '' });

  useEffect(() => {
    fetchLectures();
    fetchCourses();
    fetchLecturers();
  }, []);

  const fetchLectures = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/lectures`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setLectures(res.data || []);
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Error fetching lectures');
    }
  };

  const fetchCourses = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/courses`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setCourses(res.data || []);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchLecturers = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/users?role=lecturer`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setLecturers(res.data || []);
    } catch (err) {
      console.error(err);
    }
  };

  const handleNewChange = (e) => {
    setNewLecture({ ...newLecture, [e.target.name]: e.target.value });
  };

  const submitNewLecture = async () => {
    if (role !== 'pl') return;
    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/lectures`, newLecture, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setShowCreateModal(false);
      setNewLecture({ course_id: '', lecturer_id: '', date_of_lecture: '' });
      setSuccess(`Lecture created: ID ${res.data.id}`);
      fetchLectures();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Error creating lecture');
    }
  };

  return (
    <Card className="p-4">
      <h2>Lectures Management</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      {role === 'pl' && (
        <Button variant="primary" className="mb-3" onClick={() => setShowCreateModal(true)}>
          Add New Lecture
        </Button>
      )}

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Course Name</th>
            <th>Lecturer Name</th>
            <th>Date of Lecture</th>
          </tr>
        </thead>
        <tbody>
          {lectures.length > 0 ? (
            lectures.map((lec) => {
              const course = courses.find(c => c.id === lec.course_id);
              const lecturer = lecturers.find(l => l.id === lec.lecturer_id);
              return (
                <tr key={lec.id}>
                  <td>{lec.id}</td>
                  <td>{course?.name || 'N/A'}</td>
                  <td>{lecturer?.username || 'N/A'}</td>
                  <td>{new Date(lec.date_of_lecture).toLocaleDateString()}</td>
                </tr>
              );
            })
          ) : (
            <tr>
              <td colSpan={4} className="text-center">No lectures found.</td>
            </tr>
          )}
        </tbody>
      </Table>

      {role === 'pl' && (
        <Modal show={showCreateModal} onHide={() => setShowCreateModal(false)}>
          <Modal.Header closeButton>
            <Modal.Title>Add New Lecture</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Course</Form.Label>
                <Form.Select name="course_id" value={newLecture.course_id} onChange={handleNewChange} required>
                  <option value="">Select Course</option>
                  {courses.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                </Form.Select>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Lecturer</Form.Label>
                <Form.Select name="lecturer_id" value={newLecture.lecturer_id} onChange={handleNewChange} required>
                  <option value="">Select Lecturer</option>
                  {lecturers.map(l => <option key={l.id} value={l.id}>{l.username}</option>)}
                </Form.Select>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Date of Lecture</Form.Label>
                <Form.Control type="date" name="date_of_lecture" value={newLecture.date_of_lecture} onChange={handleNewChange} required />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowCreateModal(false)}>Cancel</Button>
            <Button variant="primary" onClick={submitNewLecture}>Create</Button>
          </Modal.Footer>
        </Modal>
      )}
    </Card>
  );
};

export default Lectures;
