import React, { useState, useEffect } from "react";
import { Card, Button, Form, Table } from "react-bootstrap";
import axios from "axios";

const Reports = () => {
  const [reports, setReports] = useState([]);
  const [formData, setFormData] = useState({
    faculty_name: "",
    class_name: "",
    week_of_reporting: "",
    lecturer_name: "",
    date_of_lecture: "",
    course_name: "",
    course_code: "",
    actual_number_of_students_present: "",
    total_number_of_registered_students: "",
    venue: "",
    scheduled_lecture_time: "",
    topic_taught: "",
    learning_outcomes: "",
    recommendations: "",
    feedback: "",
    lecturer_id: ""
  });
  const [editingId, setEditingId] = useState(null);

  // -------------------------
  // Fetch reports
  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:5000/api/reports", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setReports(res.data);
    } catch (err) {
      console.error("Failed to fetch reports:", err);
    }
  };

  // -------------------------
  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // -------------------------
  // Submit new or updated report
  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    try {
      if (editingId) {
        await axios.put(`http://localhost:5000/api/reports/${editingId}`, formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
      } else {
        await axios.post("http://localhost:5000/api/reports", formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }

      setFormData({
        faculty_name: "",
        class_name: "",
        week_of_reporting: "",
        lecturer_name: "",
        date_of_lecture: "",
        course_name: "",
        course_code: "",
        actual_number_of_students_present: "",
        total_number_of_registered_students: "",
        venue: "",
        scheduled_lecture_time: "",
        topic_taught: "",
        learning_outcomes: "",
        recommendations: "",
        feedback: "",
        lecturer_id: ""
      });
      setEditingId(null);
      fetchReports();
    } catch (err) {
      console.error("Error submitting report:", err);
    }
  };

  // -------------------------
  // Edit a report
  const handleEdit = (report) => {
    setFormData(report);
    setEditingId(report.id);
  };

  // -------------------------
  // Delete a report
  const handleDelete = async (id) => {
    const token = localStorage.getItem("token");
    try {
      await axios.delete(`http://localhost:5000/api/reports/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchReports();
    } catch (err) {
      console.error("Error deleting report:", err);
    }
  };

  return (
    <Card className="p-3">
      <h2>Lecture Reports</h2>

      <Form onSubmit={handleSubmit}>
        {Object.keys(formData).map((key) => (
          <Form.Group key={key} className="mb-3">
            <Form.Label>{key.replace(/_/g, " ")}</Form.Label>
            <Form.Control
              type="text"
              name={key}
              value={formData[key]}
              onChange={handleChange}
              required={key !== "feedback"} // feedback optional
            />
          </Form.Group>
        ))}
        <Button type="submit">{editingId ? "Update Report" : "Submit Report"}</Button>
      </Form>

      <h3 className="mt-4">Reports List</h3>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Faculty</th>
            <th>Class</th>
            <th>Course</th>
            <th>Lecturer</th>
            <th>Date</th>
            <th>Topic</th>
            <th>Feedback</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {reports.map((report) => (
            <tr key={report.id}>
              <td>{report.id}</td>
              <td>{report.faculty_name}</td>
              <td>{report.class_name}</td>
              <td>{report.course_name}</td>
              <td>{report.lecturer_name}</td>
              <td>{report.date_of_lecture}</td>
              <td>{report.topic_taught}</td>
              <td>{report.feedback || "None"}</td>
              <td>
                <Button
                  variant="warning"
                  size="sm"
                  onClick={() => handleEdit(report)}
                >
                  Edit
                </Button>{" "}
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() => handleDelete(report.id)}
                >
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Card>
  );
};

export default Reports;
