import React, { useState, useEffect, useCallback } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';

const Reports = ({ role }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);
  const [feedbacks, setFeedbacks] = useState({});
  const [expandedReportId, setExpandedReportId] = useState(null);

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const fetchReports = useCallback(async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }

    const url = role === 'pl'
      ? `${process.env.REACT_APP_API_URL}/reports/prl-feedback`
      : `${process.env.REACT_APP_API_URL}/reports`;

    try {
      const res = await axios.get(url, {
        headers: { 'x-auth-token': token }
      });
      setReports(res.data);
    } catch (err) {
      console.error(err);
      setError('Error fetching reports');
    }
  }, [role]);

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  const onSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    const submitData = {
      faculty_name: formData.faculty_name.trim(),
      class_name: formData.class_name.trim(),
      week_of_reporting: Number(formData.week_of_reporting),
      date_of_lecture: formData.date_of_lecture,
      course_name: formData.course_name.trim(),
      course_code: formData.course_code.trim(),
      lecturer_name: formData.lecturer_name.trim(),
      actual_number_of_students_present: Number(formData.actual_students),
      total_number_of_registered_students: Number(formData.total_registered),
      venue: formData.venue.trim(),
      scheduled_lecture_time: formData.scheduled_time.trim(),
      topic_taught: formData.topic_taught.trim(),
      learning_outcomes: formData.learning_outcomes.trim(),
      recommendations: formData.recommendations.trim(),
      lecturer_id: formData.lecturer_id.trim() || null
    };

    // Validation
    const requiredFields = [
      'faculty_name',
      'class_name',
      'week_of_reporting',
      'date_of_lecture',
      'course_name',
      'course_code',
      'lecturer_name',
      'actual_number_of_students_present',
      'total_number_of_registered_students',
      'venue',
      'scheduled_lecture_time',
      'topic_taught',
      'learning_outcomes',
      'recommendations'
    ];

    const missingFields = requiredFields.filter((key) => {
      const val = submitData[key];
      return (
        val === '' ||
        val === null ||
        val === undefined ||
        (typeof val === 'number' && isNaN(val))
      );
    });

    if (missingFields.length > 0) {
      setError(`Missing required fields: ${missingFields.join(', ')}`);
      return;
    }

    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/reports`,
        submitData,
        { headers: { 'x-auth-token': token } }
      );
      alert('Report submitted successfully');

      // Reset form
      setFormData({
        faculty_name: '',
        class_name: '',
        week_of_reporting: '',
        date_of_lecture: '',
        course_name: '',
        course_code: '',
        lecturer_name: '',
        actual_students: '',
        total_registered: '',
        venue: '',
        scheduled_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: '',
        lecturer_id: ''
      });

      fetchReports();
    } catch (err) {
      console.error('Error submitting report:', err.response?.data || err.message);
      setError(err.response?.data?.message || 'Submission failed');
    }
  };

  return (
    <Card className="p-4">
      <h2>{role === 'lecturer' ? 'Submit Report' : 'View Reports'}</h2>
      {error && <Alert variant="danger">{error}</Alert>}

      {role === 'lecturer' && (
        <Form onSubmit={onSubmit}>
          {Object.entries(formData).map(([key, value]) => (
            <Form.Group controlId={key} key={key} className="mb-3">
              <Form.Label>{key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}</Form.Label>
              <Form.Control
                type={key.includes('date') ? 'date' : 'text'}
                name={key}
                value={value}
                onChange={onChange}
                required={key !== 'lecturer_id'}
              />
            </Form.Group>
          ))}

          <Button type="submit" variant="primary">
            Submit Report
          </Button>
        </Form>
      )}

      <h3 className="mt-4">Reports</h3>
      {reports.length ? (
        <ul>
          {reports.map((report) => (
            <li key={report.id}>
              <Button
                variant="link"
                onClick={() => setExpandedReportId(expandedReportId === report.id ? null : report.id)}
              >
                {expandedReportId === report.id ? 'Hide' : 'View'} - {report.topic_taught}
              </Button>
              {expandedReportId === report.id && (
                <div>
                  <p><strong>Date:</strong> {report.date_of_lecture}</p>
                  <p><strong>Lecturer:</strong> {report.lecturer_name}</p>
                  <p><strong>Class:</strong> {report.class_name}</p>
                  <p><strong>Feedback:</strong> {report.feedback || 'None'}</p>
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No reports available</p>
      )}
    </Card>
  );
};

export default Reports;
