import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Table, Button, Alert, Card, Form, InputGroup, FormControl } from 'react-bootstrap';

const Reports = ({ role }) => {
  const [reports, setReports] = useState([]);
  const [feedbacks, setFeedbacks] = useState({});
  const [expandedReportId, setExpandedReportId] = useState(null);
  const [editReportId, setEditReportId] = useState(null);
  const [editFormData, setEditFormData] = useState({});
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });

  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [search, setSearch] = useState('');

  const fetchReports = useCallback(async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/reports${search ? `?q=${search}` : ''}`, {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      setReports(res.data || []);
      setError(null);
    } catch (err) {
      setReports([]);
      // only show error for non-PL roles
      if (role !== 'pl') {
        setError(err.response?.data?.message || 'Error fetching reports');
      } else {
        setError(null);
      }
    }
  }, [search, role]);

  const fetchClasses = useCallback(async () => {
    try {
      await axios.get(`${process.env.REACT_APP_API_URL}/classes`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
    } catch (err) {
      console.error('Error fetching classes:', err);
    }
  }, []);

  useEffect(() => {
    // Only reset state if role is not PL (keeps PL reports intact)
    if (role !== 'pl') {
      setReports([]);
      setFeedbacks({});
      setExpandedReportId(null);
      setEditReportId(null);
      setEditFormData({});
      setFormData({
        faculty_name: '',
        class_name: '',
        week_of_reporting: '',
        date_of_lecture: '',
        course_name: '',
        course_code: '',
        lecturer_name: '',
        actual_students: '',
        total_registered: '',
        venue: '',
        scheduled_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: '',
        lecturer_id: ''
      });
    }

    fetchReports();
    fetchClasses();
  }, [role, fetchReports, fetchClasses]);

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  return (
    <Card className="p-4">
      <h2>Reports</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <InputGroup className="mb-3 search-input">
        <FormControl
          placeholder="Search reports..."
          value={search}
          onChange={handleSearch}
        />
      </InputGroup>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Faculty Name</th>
            <th>Class Name</th>
            <th>Course Name</th>
            <th>Course Code</th>
            <th>Lecturer Name</th>
            <th>Week</th>
            <th>Date</th>
            {role === 'pl' && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {reports.length > 0 ? (
            reports.map((report) => (
              <tr key={report.id}>
                <td>{report.faculty_name}</td>
                <td>{report.class_name}</td>
                <td>{report.course_name}</td>
                <td>{report.course_code}</td>
                <td>{report.lecturer_name}</td>
                <td>{report.week_of_reporting}</td>
                <td>{report.date_of_lecture}</td>
                {role === 'pl' && (
                  <td>
                    {/* existing PL actions */}
                    <Button
                      variant="primary"
                      onClick={() =>
                        setExpandedReportId(
                          expandedReportId === report.id ? null : report.id
                        )
                      }
                    >
                      {expandedReportId === report.id ? 'Hide' : 'View'}
                    </Button>
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={role === 'pl' ? 8 : 7} className="text-center">
                No reports found.
              </td>
            </tr>
          )}
        </tbody>
      </Table>
    </Card>
  );
};

export default Reports;
