// Navbar.js
import React from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';

const CustomNavbar = ({ role, logout }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();            // Clear token, reset role
    navigate('/login');  // Redirect to login page
  };

  return (
    <Navbar bg="primary" variant="dark" expand="lg">
      <Container>
        <Navbar.Brand as={Link} to="/">LUCT Reporting System</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">

            {/* ===== Student Menus ===== */}
            {role === 'student' && (
              <>
                <Nav.Link as={Link} to="/monitoring">Monitoring</Nav.Link>
                <Nav.Link as={Link} to="/rating">Rating</Nav.Link>
              </>
            )}

            {/* ===== Lecturer Menus ===== */}
            {role === 'lecturer' && (
              <>
                <Nav.Link as={Link} to="/classes">Classes</Nav.Link>
                <Nav.Link as={Link} to="/reports">Reports</Nav.Link>
                <Nav.Link as={Link} to="/monitoring">Monitoring</Nav.Link>
                <Nav.Link as={Link} to="/rating">Rating</Nav.Link>
              </>
            )}

            {/* ===== PRL Menus ===== */}
            {role === 'prl' && (
              <>
                <Nav.Link as={Link} to="/courses">Courses</Nav.Link>
                <Nav.Link as={Link} to="/reports">Reports</Nav.Link>
                <Nav.Link as={Link} to="/monitoring">Monitoring</Nav.Link>
                <Nav.Link as={Link} to="/rating">Rating</Nav.Link>
                <Nav.Link as={Link} to="/classes">Classes</Nav.Link>
              </>
            )}

            {/* ===== PL Menus ===== */}
            {role === 'pl' && (
              <>
                <Nav.Link as={Link} to="/courses">Courses</Nav.Link>
                <Nav.Link as={Link} to="/reports">Reports</Nav.Link>
                <Nav.Link as={Link} to="/monitoring">Monitoring</Nav.Link>
                <Nav.Link as={Link} to="/classes">Classes</Nav.Link>
                <Nav.Link as={Link} to="/lectures">Lectures</Nav.Link>
                <Nav.Link as={Link} to="/rating">Rating</Nav.Link>
              </>
            )}
          </Nav>

          {/* ===== Right Side (Logout) ===== */}
          <Nav>
            {role ? (
              <Nav.Link onClick={handleLogout}>Logout</Nav.Link>
            ) : (
              <Nav.Link as={Link} to="/login">Login</Nav.Link>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default CustomNavbar;
