import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Card, Alert, Table, InputGroup, FormControl } from 'react-bootstrap';

const Reports = ({ role }) => {
  const [reports, setReports] = useState([]);
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_content_covered: '',
    planned_content: '',
    reasons_for_variance: '',
    challenges: '',
    mitigation: '',
  });
  const [message, setMessage] = useState({ text: '', type: '' }); // ✅ unified success/error messages
  const [search, setSearch] = useState('');
  const [editingReportId, setEditingReportId] = useState(null);
  const [editData, setEditData] = useState({});

  useEffect(() => {
    fetchReports();
  }, []);

  // Fetch reports
  const fetchReports = async (q = '') => {
    const token = localStorage.getItem('token');
    if (!token) {
      setMessage({ text: 'No token found. Please log in.', type: 'danger' });
      return;
    }
    try {
      const url = q
        ? `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports?q=${encodeURIComponent(q)}`
        : `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports`;

      const res = await axios.get(url, { headers: { 'x-auth-token': token } });
      setReports(res.data);
    } catch (err) {
      console.error('Fetch error:', err);
      setMessage({ text: 'Error fetching reports', type: 'danger' });
    }
  };

  // Form input change
  const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  // Submit new report
  const onSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      setMessage({ text: 'No token found. Please log in.', type: 'danger' });
      return;
    }

    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports`,
        formData,
        { headers: { 'x-auth-token': token } }
      );
      setFormData({
        faculty_name: '',
        class_name: '',
        week_of_reporting: '',
        date_of_lecture: '',
        course_name: '',
        course_code: '',
        lecturer_name: '',
        actual_content_covered: '',
        planned_content: '',
        reasons_for_variance: '',
        challenges: '',
        mitigation: '',
      });
      fetchReports();
      setMessage({ text: 'Report submitted successfully!', type: 'success' });
    } catch (err) {
      console.error('Submit error:', err);
      setMessage({ text: 'Error submitting report', type: 'danger' });
    }
  };

  // Edit functions
  const startEdit = (report) => {
    setEditingReportId(report.id);
    setEditData(report);
  };

  const submitEdit = async (id) => {
    const token = localStorage.getItem('token');
    if (!token) {
      setMessage({ text: 'No token found. Please log in.', type: 'danger' });
      return;
    }

    try {
      await axios.put(
        `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/${id}`,
        editData,
        { headers: { 'x-auth-token': token } }
      );
      setEditingReportId(null);
      fetchReports();
      setMessage({ text: 'Report updated successfully!', type: 'success' }); // ✅ success alert
    } catch (err) {
      console.error('Edit error:', err);
      setMessage({ text: 'Error updating report', type: 'danger' });
    }
  };

  // Delete
  const handleDelete = async (id) => {
    const token = localStorage.getItem('token');
    if (!token) {
      setMessage({ text: 'No token found. Please log in.', type: 'danger' });
      return;
    }

    if (!window.confirm('Are you sure you want to delete this report?')) return;

    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/${id}`,
        { headers: { 'x-auth-token': token } }
      );
      fetchReports();
      setMessage({ text: 'Report deleted successfully!', type: 'success' }); // ✅ success alert
    } catch (err) {
      console.error('Delete error:', err);
      setMessage({ text: 'Error deleting report', type: 'danger' });
    }
  };

  // Search
  const handleSearch = (e) => {
    setSearch(e.target.value);
    fetchReports(e.target.value);
  };

  return (
    <Card className="p-4">
      {message.text && (
        <Alert
          variant={message.type}
          dismissible
          onClose={() => setMessage({ text: '', type: '' })}
        >
          {message.text}
        </Alert>
      )}

      <Form onSubmit={onSubmit} className="mb-4">
        <Form.Group controlId="faculty_name" className="mb-3">
          <Form.Label>Faculty Name</Form.Label>
          <Form.Control
            type="text"
            name="faculty_name"
            value={formData.faculty_name}
            onChange={onChange}
            required
          />
        </Form.Group>

        <Form.Group controlId="class_name" className="mb-3">
          <Form.Label>Class Name</Form.Label>
          <Form.Control
            type="text"
            name="class_name"
            value={formData.class_name}
            onChange={onChange}
            required
          />
        </Form.Group>

        <Button variant="primary" type="submit">Submit Report</Button>
      </Form>

      <InputGroup className="mb-3">
        <FormControl
          placeholder="Search reports..."
          value={search}
          onChange={handleSearch}
        />
      </InputGroup>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Faculty</th>
            <th>Class</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {reports.map((report) => (
            <tr key={report.id}>
              <td>{report.id}</td>
              <td>
                {editingReportId === report.id ? (
                  <Form.Control
                    type="text"
                    value={editData.faculty_name}
                    onChange={(e) => setEditData({ ...editData, faculty_name: e.target.value })}
                  />
                ) : (
                  report.faculty_name
                )}
              </td>
              <td>
                {editingReportId === report.id ? (
                  <Form.Control
                    type="text"
                    value={editData.class_name}
                    onChange={(e) => setEditData({ ...editData, class_name: e.target.value })}
                  />
                ) : (
                  report.class_name
                )}
              </td>
              <td>
                {editingReportId === report.id ? (
                  <Button variant="success" size="sm" onClick={() => submitEdit(report.id)}>Save</Button>
                ) : (
                  <Button variant="warning" size="sm" onClick={() => startEdit(report)}>Edit</Button>
                )}{' '}
                <Button variant="danger" size="sm" onClick={() => handleDelete(report.id)}>Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Card>
  );
};

export default Reports;
