import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Rating = ({ role, lectureId }) => {
  const [ratingData, setRatingData] = useState({ target_id: '', rating: '', comment: '' });
  const [existingRating, setExistingRating] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    if (!lectureId) return;

    axios.get(`${process.env.REACT_APP_API_URL}/rating/${lectureId}`, {
      headers: { 'x-auth-token': localStorage.getItem('token') }
    })
    .then(res => {
      if (res.data.length > 0) {
        setExistingRating(res.data[0]);
        setRatingData({
          target_id: role === 'student' ? res.data[0].lecturer_id : res.data[0].student_id,
          rating: res.data[0].rating,
          comment: res.data[0].comment || ''
        });
      }
    })
    .catch(err => console.error('Fetch existing rating error:', err));
  }, [lectureId, role]);

  const handleChange = (e) => setRatingData({ ...ratingData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post(`${process.env.REACT_APP_API_URL}/rating`, { lecture_id: lectureId, ...ratingData }, {
      headers: { 'x-auth-token': localStorage.getItem('token') }
    })
    .then(res => {
      setMessage(res.data.message);
      setExistingRating({ ...ratingData });
    })
    .catch(err => {
      console.error('Rating submit error:', err);
      setMessage(err.response?.data?.message || 'Error submitting rating');
    });
  };

  return (
    <div>
      <h2>{role === 'student' ? 'Rate Lecturer' : 'Rate Student'}</h2>

      {existingRating && (
        <p>Your existing rating: {existingRating.rating} / Comment: {existingRating.comment || 'None'}</p>
      )}

      {message && <p>{message}</p>}

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="target_id"
          placeholder={role === 'student' ? 'Lecturer ID' : 'Student ID'}
          value={ratingData.target_id}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="rating"
          placeholder="Rating (1-5)"
          min="1"
          max="5"
          value={ratingData.rating}
          onChange={handleChange}
          required
        />
        <textarea
          name="comment"
          placeholder="Comment (optional)"
          value={ratingData.comment}
          onChange={handleChange}
        />
        <button type="submit">{existingRating ? 'Update Rating' : 'Submit Rating'}</button>
      </form>
    </div>
  );
};

export default Rating;
