import axios from 'axios';
import { useState } from 'react';

const Rating = () => {
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');
  const [message, setMessage] = useState('');

  const onSubmit = async (e) => {
    e.preventDefault();
    setMessage(''); // reset message

    try {
      // If frontend is on a different port than backend, use full URL
      const res = await axios.post('/api/ratings', { rating, comment });
      setMessage(res.data.message); // Success feedback
      setRating('');
      setComment('');
    } catch (err) {
      // Error feedback
      if (err.response) {
        // Backend responded with status code out of 2xx
        setMessage(err.response.data.message || 'Error submitting rating');
      } else if (err.request) {
        // Request was made but no response
        setMessage('No response from server');
      } else {
        // Something else
        setMessage('Error: ' + err.message);
      }
    }
  };

  return (
    <form onSubmit={onSubmit}>
      <input
        type="number"
        value={rating}
        onChange={(e) => setRating(e.target.value)}
        placeholder="Rating"
        required
      />
      <input
        type="text"
        value={comment}
        onChange={(e) => setComment(e.target.value)}
        placeholder="Comment"
        required
      />
      <button type="submit">Submit</button>
      {message && <p>{message}</p>}
    </form>
  );
};

export default Rating;
