import React, { useState } from 'react';
import axios from 'axios';

const Rating = ({ role }) => {
  const [form, setForm] = useState({
    lecture_id: '',
    target_id: '', // student_id if lecturer, lecturer_id if student
    rating: '',
    comment: ''
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.lecture_id || !form.target_id || !form.rating) {
      setMessage('Please fill all required fields.');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setMessage('User not authenticated. Please log in.');
        return;
      }

      await axios.post(
        `${process.env.REACT_APP_API_URL}/rating`, // ✅ matches backend
        form,
        { headers: { 'x-auth-token': token } }
      );

      setMessage('Rating submitted successfully!');
      setForm({ lecture_id: '', target_id: '', rating: '', comment: '' });
    } catch (err) {
      console.error('Rating submit error:', err);
      setMessage(err.response?.data?.message || 'Error submitting rating');
    }
  };

  return (
    <div className="p-3">
      <h2>{role === 'student' ? 'Rate Lecturer' : 'Rate Student'}</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            name="lecture_id"
            placeholder="Lecture ID"
            value={form.lecture_id}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <input
            type="text"
            name="target_id"
            placeholder={role === 'student' ? 'Lecturer ID' : 'Student ID'}
            value={form.target_id}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <input
            type="number"
            name="rating"
            placeholder="Rating (1-5)"
            min="1"
            max="5"
            value={form.rating}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <textarea
            name="comment"
            placeholder="Comment (optional)"
            value={form.comment}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Submit Rating</button>
      </form>
    </div>
  );
};

export default Rating;
