import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Card, InputGroup, FormControl } from 'react-bootstrap';

const Lectures = ({ role }) => {
  const [lectures, setLectures] = useState([]);
  const [reports, setReports] = useState([]);
  const [formData, setFormData] = useState({ report_id: '', lecturer_id: '' });
  const [search, setSearch] = useState('');

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  useEffect(() => {
    fetchLectures();
    fetchReports();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const fetchLectures = async () => {
    try {
      const res = await axios.get(`${API_URL}/lectures`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setLectures(res.data);
    } catch (err) {
      console.error('Fetch lectures error:', err);
    }
  };

  const fetchReports = async () => {
    try {
      const res = await axios.get(`${API_URL}/lectures/available-reports`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setReports(res.data);
    } catch (err) {
      console.error('Fetch reports error:', err);
    }
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!formData.report_id || !formData.lecturer_id) {
      return alert('Select a lecture and lecturer');
    }

    try {
      await axios.post(`${API_URL}/lectures`, formData, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setFormData({ report_id: '', lecturer_id: '' });
      fetchLectures();
    } catch (err) {
      console.error('Error assigning lecture:', err);
      alert('Error assigning lecture');
    }
  };

  const deleteLecture = async (id) => {
    if (!window.confirm('Are you sure you want to delete this lecture?')) return;
    try {
      await axios.delete(`${API_URL}/lectures/${id}`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchLectures();
    } catch (err) {
      console.error('Delete lecture error:', err);
    }
  };

  const filteredLectures = lectures.filter(l =>
    (l.lecture_name || '').toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Card className="p-4">
      {role === 'pl' && (
        <Form onSubmit={onSubmit} className="mb-4">
          <Form.Group controlId="report_id" className="mb-3">
            <Form.Label>Select Lecture (from Reports)</Form.Label>
            <Form.Select
              value={formData.report_id}
              onChange={(e) => setFormData({ ...formData, report_id: e.target.value })}
              required
            >
              <option value="">Select lecture</option>
              {reports.map(r => (
                <option key={r.id} value={r.id}>
                  {r.course_name}
                </option>
              ))}
            </Form.Select>
          </Form.Group>

          <Form.Group controlId="lecturer_id" className="mb-3">
            <Form.Label>Lecturer ID</Form.Label>
            <Form.Control
              type="number"
              value={formData.lecturer_id}
              onChange={(e) => setFormData({ ...formData, lecturer_id: e.target.value })}
              required
            />
          </Form.Group>

          <Button variant="primary" type="submit">Assign Lecture</Button>
        </Form>
      )}

      <InputGroup className="mb-3">
        <FormControl
          placeholder="Search lectures..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
      </InputGroup>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Lecture Name</th>
            <th>Date</th>
            {role === 'pl' && <th>Action</th>}
          </tr>
        </thead>
        <tbody>
          {filteredLectures.map(l => (
            <tr key={l.id}>
              <td>{l.id}</td>
              <td>{l.lecture_name || 'N/A'}</td>
              <td>{l.date_of_lecture?.split('T')[0]}</td>
              {role === 'pl' && (
                <td>
                  <Button variant="danger" size="sm" onClick={() => deleteLecture(l.id)}>Delete</Button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </Table>
    </Card>
  );
};

export default Lectures;
