import React, { useState, useEffect, useCallback } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';
import * as XLSX from 'xlsx'; // âœ… for Excel export

const Reports = ({ role }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);
  const [feedbacks, setFeedbacks] = useState({}); // feedback per report
  const [expandedReportId, setExpandedReportId] = useState(null);

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const fetchReports = useCallback(async (q = '') => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }

    let url;
    if (role === 'pl') {
      url = `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/prl-feedback`;
      console.log('Fetching PRL feedback reports for PL:', url);
    } else {
      url = `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports${q ? `?q=${q}` : ''}`;
      console.log('Fetching all reports for role:', role, url);
    }

    try {
      setError(null);
      let response;
      if (role === 'pl') {
        response = await axios.get(url, { headers: { 'x-auth-token': token } });
      } else {
        response = await axios.get(url, { headers: { 'x-auth-token': token } });
      }
      setReports(response.data);
    } catch (err) {
      console.error('Fetch error - URL:', url, 'Error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized: No token or token expired.');
        else if (status === 403) setError('Forbidden: Your role does not have access to reports.');
        else if (status === 400) setError('Invalid token or bad request.');
        else setError(`Server error (${status}): ${data.msg || 'Unknown error'}`);
      } else if (err.request) {
        setError('No response from server. Check your network or server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  }, [role]);

  const onSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    if (!token) {
      setError('No token found. Please log in.');
      return;
    }

    try {
      setError(null);
      const submitData = {
        faculty_name: formData.faculty_name,
        class_name: formData.class_name,
        week_of_reporting: parseInt(formData.week_of_reporting) || 0,
        date_of_lecture: new Date(formData.date_of_lecture).toISOString().split('T')[0],
        course_name: formData.course_name,
        course_code: formData.course_code,
        lecturer_name: formData.lecturer_name,
        actual_number_of_students_present: parseInt(formData.actual_students) || 0,
        total_number_of_registered_students: parseInt(formData.total_registered) || 0,
        venue: formData.venue,
        scheduled_lecture_time: formData.scheduled_time,
        topic_taught: formData.topic_taught,
        learning_outcomes: formData.learning_outcomes,
        recommendations: formData.recommendations,
        lecturer_id: formData.lecturer_id || null
      };

      await axios.post(
        `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports`,
        submitData,
        { headers: { 'x-auth-token': token } }
      );

      alert('Report submitted successfully');

      setFormData({
        faculty_name: '',
        class_name: '',
        week_of_reporting: '',
        date_of_lecture: '',
        course_name: '',
        course_code: '',
        lecturer_name: '',
        actual_students: '',
        total_registered: '',
        venue: '',
        scheduled_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: '',
        lecturer_id: ''
      });

      fetchReports();
    } catch (err) {
      console.error('Submit error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized: No token or token expired.');
        else if (status === 403) setError('Forbidden: Your role does not have access to submit reports.');
        else if (status === 400) setError(`Invalid request: ${data.msg || 'Check form data.'}`);
        else setError(`Server error (${status}): ${data.msg || 'Unknown error'}`);
      } else if (err.request) {
        setError('No response from server. Check your network or server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  };

  const handleFeedbackSubmit = async (e, reportId) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const feedback = feedbacks[reportId] || '';

    console.log('Feedback Submit - URL:', `${process.env.REACT_APP_API_URL}/reports/feedback/${reportId}`);
    console.log('Feedback Submit - Token:', token ? 'Present' : 'Missing');
    console.log('Feedback Submit - Report ID:', reportId);
    console.log('Feedback Submit - Feedback:', feedback);

    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }
    if (!reportId) {
      setError('No report selected for feedback.');
      return;
    }
    if (!feedback.trim()) {
      setError('Please enter feedback before submitting.');
      return;
    }

    try {
      setError(null);
      await axios.post(
        `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/feedback/${reportId}`,
        { feedback },
        { headers: { 'x-auth-token': token } }
      );
      setFeedbacks((prev) => ({ ...prev, [reportId]: '' })); // Clear feedback for this report
      fetchReports();
    } catch (err) {
      console.error('Feedback error - URL:', `${process.env.REACT_APP_API_URL}/reports/feedback/${reportId}`, 'Error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized: No token or token expired.');
        else if (status === 403) setError('Forbidden: Only PRL can add feedback.');
        else setError(`Server error (${status}): ${data.msg || 'Unknown error'}`);
      } else if (err.request) {
        setError('No response from server. Check your network or server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  };

  const downloadExcel = () => {
    if (reports.length === 0) {
      alert('No reports available to export.');
      return;
    }

    const worksheetData = reports.map((r) => ({
      'Faculty Name': r.faculty_name,
      'Class Name': r.class_name,
      'Week of Reporting': r.week_of_reporting,
      'Date of Lecture': r.date_of_lecture,
      'Course Name': r.course_name,
      'Course Code': r.course_code,
      'Lecturer Name': r.lecturer_name,
      'Actual Students': r.actual_number_of_students_present,
      'Total Registered': r.total_number_of_registered_students,
      'Venue': r.venue,
      'Scheduled Time': r.scheduled_lecture_time,
      'Topic Taught': r.topic_taught,
      'Learning Outcomes': r.learning_outcomes,
      'Recommendations': r.recommendations,
      'Lecturer ID': r.lecturer_id,
      'Feedback': r.feedback || 'None'
    }));

    const worksheet = XLSX.utils.json_to_sheet(worksheetData);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Reports');
    XLSX.writeFile(workbook, 'Lecture_Reports.xlsx');
  };

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  return (
    <Card className="p-4">
      <h2>{role === 'lecturer' ? 'Submit Report' : 'Reports'}</h2>
      {error && <Alert variant="danger">{error}</Alert>}

      {role === 'lecturer' && (
        <Form onSubmit={onSubmit}>
          {Object.entries(formData).map(([key, value]) => (
            <Form.Group controlId={key} key={key} className="mb-3">
              <Form.Label>{key.replace(/_/g, ' ')}</Form.Label>
              <Form.Control
                type={key.includes('date') ? 'date' : 'text'}
                name={key}
                value={value}
                onChange={onChange}
                required
              />
            </Form.Group>
          ))}
          <Button type="submit">Submit Report</Button>
        </Form>
      )}

      <h3 className="mt-4">Reports</h3>
      <Button variant="success" className="mb-3" onClick={downloadExcel}>
        Download Reports
      </Button>

      {reports.length ? (
        <ul>
          {reports.map((report) => (
            <li key={report.id} className="mb-3">
              <strong>{report.topic_taught}</strong> - {report.date_of_lecture} -{' '}
              {report.lecturer_name}
              <div>
                <Button
                  variant="link"
                  onClick={() =>
                    setExpandedReportId(expandedReportId === report.id ? null : report.id)
                  }
                >
                  {expandedReportId === report.id ? 'Hide Details' : 'View Details'}
                </Button>
                {expandedReportId === report.id && (
                  <div className="p-2 border">
                    {editingReportId === report.id ? (
                      <Form
                        onSubmit={(e) => {
                          e.preventDefault();
                          submitEdit(report.id);
                        }}
                      >
                        {Object.entries(editData).map(([key, value]) => (
                          <Form.Group controlId={`edit_${key}`} key={key} className="mb-2">
                            <Form.Label>{key.replace(/_/g, ' ')}</Form.Label>
                            <Form.Control
                              type={key.includes('date') ? 'date' : 'text'}
                              value={value}
                              onChange={(e) =>
                                setEditData({ ...editData, [key]: e.target.value })
                              }
                            />
                          </Form.Group>
                        ))}
                        <Button type="submit" variant="primary" className="me-2">
                          Save
                        </Button>
                        <Button
                          variant="secondary"
                          onClick={() => setEditingReportId(null)}
                        >
                          Cancel
                        </Button>
                      </Form>
                    ) : (
                      <div>
                        <p>Faculty: {report.faculty_name}</p>
                        <p>Class: {report.class_name}</p>
                        <p>Week: {report.week_of_reporting}</p>
                        <p>Course: {report.course_name}</p>
                        <p>Actual Students: {report.actual_number_of_students_present}</p>
                        <p>Total Registered: {report.total_number_of_registered_students}</p>
                        <p>Venue: {report.venue}</p>
                        <p>Scheduled Time: {report.scheduled_lecture_time}</p>
                        <p>Topic: {report.topic_taught}</p>
                        <p>Learning Outcomes: {report.learning_outcomes}</p>
                        <p>Recommendations: {report.recommendations}</p>
                        <p>Lecturer ID: {report.lecturer_id}</p>
                        <p>Feedback: {report.feedback || 'None'}</p>
                        {role === 'lecturer' && (
                          <>
                            <Button
                              variant="warning"
                              onClick={() => startEdit(report)}
                              className="me-2"
                            >
                              Edit
                            </Button>
                            <Button
                              variant="danger"
                              onClick={() => handleDelete(report.id)}
                            >
                              Delete
                            </Button>
                          </>
                        )}
                        {role === 'prl' && (
                          <Form onSubmit={(e) => handleFeedbackSubmit(e, report.id)}>
                            <Form.Control
                              type="text"
                              value={feedbacks[report.id] || ''}
                              onChange={(e) =>
                                setFeedbacks((prev) => ({
                                  ...prev,
                                  [report.id]: e.target.value
                                }))
                              }
                              placeholder="Add feedback"
                              className="mb-2"
                            />
                            <Button type="submit">Submit Feedback</Button>
                          </Form>
                        )}
                      </div>
                    )}
                  </div>
                )}
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No reports available.</p>
      )}
    </Card>
  );
};

export default Reports;
