import React, { useState, useEffect, useCallback } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';
import * as XLSX from 'xlsx';

const Reports = ({ role }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);
  const [feedbacks, setFeedbacks] = useState({});
  const [expandedReportId, setExpandedReportId] = useState(null);
  const [editingReportId, setEditingReportId] = useState(null);
  const [editData, setEditData] = useState({});

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const fetchReports = useCallback(async (q = '') => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }

    let url = '';
    if (role === 'pl') {
      url = `${process.env.REACT_APP_API_URL}/reports/prl-feedback`;
    } else {
      url = `${process.env.REACT_APP_API_URL}/reports${q ? `?q=${q}` : ''}`;
    }

    try {
      const response = await axios.get(url, {
        headers: { 'x-auth-token': token }
      });
      setReports(response.data);
      setError(null);
    } catch (err) {
      console.error('Fetch error:', err);
      setError('Failed to fetch reports');
    }
  }, [role]);

  const onSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return setError('No token found');

    try {
      const submitData = {
        faculty_name: formData.faculty_name,
        class_name: formData.class_name,
        week_of_reporting: parseInt(formData.week_of_reporting) || 0,
        date_of_lecture: new Date(formData.date_of_lecture).toISOString().split('T')[0],
        course_name: formData.course_name,
        course_code: formData.course_code,
        lecturer_name: formData.lecturer_name,
        actual_number_of_students_present: parseInt(formData.actual_students) || 0,
        total_number_of_registered_students: parseInt(formData.total_registered) || 0,
        venue: formData.venue,
        scheduled_lecture_time: formData.scheduled_time,
        topic_taught: formData.topic_taught,
        learning_outcomes: formData.learning_outcomes,
        recommendations: formData.recommendations,
        lecturer_id: formData.lecturer_id || null
      };

      await axios.post(`${process.env.REACT_APP_API_URL}/reports`, submitData, {
        headers: { 'x-auth-token': token }
      });

      alert('Report submitted successfully');
      setFormData({
        faculty_name: '', class_name: '', week_of_reporting: '', date_of_lecture: '', course_name: '',
        course_code: '', lecturer_name: '', actual_students: '', total_registered: '', venue: '',
        scheduled_time: '', topic_taught: '', learning_outcomes: '', recommendations: '', lecturer_id: ''
      });

      fetchReports();
    } catch (err) {
      console.error('Submit error:', err);
      setError('Error submitting report');
    }
  };

  // -------------------- EDIT --------------------
  const startEdit = (report) => {
    setEditingReportId(report.id);
    setEditData({
      ...report,
      actual_students: report.actual_number_of_students_present,
      total_registered: report.total_number_of_registered_students,
      scheduled_time: report.scheduled_lecture_time
    });
  };

  const handleEditChange = (e) => {
    setEditData({ ...editData, [e.target.name]: e.target.value });
  };

  const submitEdit = async (reportId) => {
    const token = localStorage.getItem('token');
    if (!token) return setError('No token found');

    try {
      const data = {
        faculty_name: editData.faculty_name,
        class_name: editData.class_name,
        week_of_reporting: editData.week_of_reporting,
        date_of_lecture: editData.date_of_lecture,
        course_name: editData.course_name,
        course_code: editData.course_code,
        lecturer_name: editData.lecturer_name,
        actual_number_of_students_present: editData.actual_students,
        total_number_of_registered_students: editData.total_registered,
        venue: editData.venue,
        scheduled_lecture_time: editData.scheduled_time,
        topic_taught: editData.topic_taught,
        learning_outcomes: editData.learning_outcomes,
        recommendations: editData.recommendations
      };

      await axios.put(`${process.env.REACT_APP_API_URL}/reports/${reportId}`, data, {
        headers: { 'x-auth-token': token }
      });
      setEditingReportId(null);
      fetchReports();
    } catch (err) {
      console.error('Edit error:', err);
      setError('Error editing report');
    }
  };

  // -------------------- DELETE --------------------
  const handleDelete = async (reportId) => {
    const token = localStorage.getItem('token');
    if (!token) return setError('No token found');

    if (!window.confirm('Are you sure you want to delete this report?')) return;

    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/reports/${reportId}`, {
        headers: { 'x-auth-token': token }
      });
      fetchReports();
    } catch (err) {
      console.error('Delete error:', err);
      setError('Error deleting report');
    }
  };

  // -------------------- FEEDBACK --------------------
  const handleFeedbackSubmit = async (e, reportId) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const feedback = feedbacks[reportId] || '';
    if (!token || !feedback.trim()) return;

    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/reports/feedback/${reportId}`, { feedback }, {
        headers: { 'x-auth-token': token }
      });
      setFeedbacks((prev) => ({ ...prev, [reportId]: '' }));
      fetchReports();
    } catch (err) {
      console.error('Feedback error:', err);
      setError('Error submitting feedback');
    }
  };

  // -------------------- EXCEL --------------------
  const downloadExcel = () => {
    const worksheetData = reports.map((report) => ({
      'Faculty Name': report.faculty_name,
      'Class Name': report.class_name,
      'Week of Reporting': report.week_of_reporting,
      'Date of Lecture': report.date_of_lecture,
      'Course Name': report.course_name,
      'Course Code': report.course_code,
      'Lecturer Name': report.lecturer_name,
      'Actual Students': report.actual_number_of_students_present,
      'Total Registered': report.total_number_of_registered_students,
      'Venue': report.venue,
      'Scheduled Time': report.scheduled_lecture_time,
      'Topic Taught': report.topic_taught,
      'Learning Outcomes': report.learning_outcomes,
      'Recommendations': report.recommendations,
      'Lecturer ID': report.lecturer_id,
      'Feedback': report.feedback || 'None'
    }));
    const worksheet = XLSX.utils.json_to_sheet(worksheetData);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Lecture Reports');
    XLSX.writeFile(workbook, 'Lecture_Reports.xlsx');
  };

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  return (
    <Card className="p-4">
      <h2>
        {role === 'lecturer' ? 'Submit Report' :
          role === 'prl' ? 'View Lecture Reports & Add Feedback' :
            'View PRL Reports'}
      </h2>
      {error && <Alert variant="danger">{error}</Alert>}

      {role === 'lecturer' && editingReportId === null && (
        <Form onSubmit={onSubmit}>
          {Object.entries(formData).map(([key, value]) => (
            <Form.Group controlId={key} className="mb-3" key={key}>
              <Form.Label>{key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}</Form.Label>
              <Form.Control
                type={key.includes('date') ? 'date' : 'text'}
                name={key}
                value={value}
                onChange={onChange}
                required
              />
            </Form.Group>
          ))}
          <Button variant="primary" type="submit">Submit Report</Button>
        </Form>
      )}

      <h3 className="mt-4">Reports</h3>
      <Button variant="success" className="mb-3" onClick={downloadExcel}>
        Download Reports as Excel
      </Button>

      {reports.length ? (
        <ul>
          {reports.map((report) => (
            <li key={report.id} className="mb-3">
              <div>
                <Button
                  variant="link"
                  onClick={() => setExpandedReportId(expandedReportId === report.id ? null : report.id)}
                >
                  {expandedReportId === report.id ? 'Hide Details' : 'View Details'} - {report.topic_taught} - {report.date_of_lecture} - {report.lecturer_name}
                </Button>

                {role === 'lecturer' && editingReportId !== report.id && (
                  <Button variant="warning" size="sm" onClick={() => startEdit(report)} className="ms-2">Edit</Button>
                )}
                {role === 'lecturer' && (
                  <Button variant="danger" size="sm" onClick={() => handleDelete(report.id)} className="ms-2">Delete</Button>
                )}
              </div>

              {expandedReportId === report.id && (
                <div className="mt-2">
                  {editingReportId === report.id ? (
                    <Form>
                      {Object.keys(editData).map((key) => (
                        <Form.Group key={key} className="mb-2">
                          <Form.Label>{key.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}</Form.Label>
                          <Form.Control
                            type={key.includes('date') ? 'date' : 'text'}
                            name={key}
                            value={editData[key]}
                            onChange={handleEditChange}
                          />
                        </Form.Group>
                      ))}
                      <Button variant="primary" onClick={() => submitEdit(report.id)} className="me-2">Save</Button>
                      <Button variant="secondary" onClick={() => setEditingReportId(null)}>Cancel</Button>
                    </Form>
                  ) : (
                    <div>
                      <p><strong>Faculty Name:</strong> {report.faculty_name}</p>
                      <p><strong>Class Name:</strong> {report.class_name}</p>
                      <p><strong>Week:</strong> {report.week_of_reporting}</p>
                      <p><strong>Date:</strong> {report.date_of_lecture}</p>
                      <p><strong>Course:</strong> {report.course_name} ({report.course_code})</p>
                      <p><strong>Lecturer:</strong> {report.lecturer_name}</p>
                      <p><strong>Actual Students:</strong> {report.actual_number_of_students_present}</p>
                      <p><strong>Total Registered:</strong> {report.total_number_of_registered_students}</p>
                      <p><strong>Venue:</strong> {report.venue}</p>
                      <p><strong>Scheduled Time:</strong> {report.scheduled_lecture_time}</p>
                      <p><strong>Topic:</strong> {report.topic_taught}</p>
                      <p><strong>Learning Outcomes:</strong> {report.learning_outcomes}</p>
                      <p><strong>Recommendations:</strong> {report.recommendations}</p>
                      <p><strong>Lecturer ID:</strong> {report.lecturer_id}</p>
                      <p><strong>Feedback:</strong> {report.feedback || 'None'}</p>

                      {role === 'prl' && (
                        <Form onSubmit={(e) => handleFeedbackSubmit(e, report.id)}>
                          <Form.Control
                            type="text"
                            name="feedback"
                            value={feedbacks[report.id] || ''}
                            onChange={(e) => setFeedbacks((prev) => ({ ...prev, [report.id]: e.target.value }))}
                            placeholder="Add feedback"
                          />
                          <Button variant="secondary" type="submit" className="mt-2">Submit Feedback</Button>
                        </Form>
                      )}
                    </div>
                  )}
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : <p>No reports available.</p>}
    </Card>
  );
};

export default Reports;
