import React, { useState, useEffect } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import axios from 'axios';

const Rating = ({ role, reportId, onRatingUpdate }) => {
  const [lectureId, setLectureId] = useState('');
  const [studentId, setStudentId] = useState('');
  const [rating, setRating] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    // Reset form when reportId changes (for lecturer context)
    setLectureId('');
    setStudentId('');
    setRating('');
    setError(null);
    setSuccess(null);
  }, [reportId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return setError('No authentication token.');

    const payload = {
      rating: parseInt(rating),
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString(),
    };

    let url = '';
    if (role === 'lecturer') {
      if (!studentId) return setError('Please provide a student ID.');
      url = `${process.env.REACT_APP_API_URL}/api/ratings/student/${reportId}`;
      payload.student_id = studentId;
      payload.lecturer_id = localStorage.getItem('userId'); // Assume userId is stored
    } else if (role === 'student') {
      if (!lectureId) return setError('Please provide a lecture ID.');
      url = `${process.env.REACT_APP_API_URL}/api/ratings/lecture/${reportId}`;
      payload.lecture_id = lectureId;
      payload.student_id = localStorage.getItem('userId'); // Assume userId is stored
    } else {
      return setError('Invalid role.');
    }

    try {
      await axios.post(url, payload, { headers: { 'x-auth-token': token } });
      setRating('');
      setLectureId('');
      setStudentId('');
      setSuccess('Rating submitted successfully');
      setError(null);
      if (onRatingUpdate) onRatingUpdate();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to submit rating.');
      setSuccess(null);
    }
  };

  return (
    <div className="mt-3">
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}
      <Form onSubmit={handleSubmit}>
        {role === 'lecturer' && (
          <>
            <Form.Group controlId="studentId" className="mb-3">
              <Form.Label>Student ID</Form.Label>
              <Form.Control
                type="text"
                value={studentId}
                onChange={(e) => setStudentId(e.target.value)}
                required
              />
            </Form.Group>
          </>
        )}
        {role === 'student' && (
          <Form.Group controlId="lectureId" className="mb-3">
            <Form.Label>Lecture ID</Form.Label>
            <Form.Control
              type="text"
              value={lectureId}
              onChange={(e) => setLectureId(e.target.value)}
              required
            />
          </Form.Group>
        )}
        <Form.Group controlId="rating" className="mb-3">
          <Form.Label>Rating (1-5)</Form.Label>
          <Form.Control
            type="number"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            min="1"
            max="5"
            required
          />
        </Form.Group>
        <Button type="submit">Submit Rating</Button>
      </Form>
    </div>
  );
};

export default Rating;
