import React, { useState, useEffect } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';

const Reports = () => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_number_of_students_present: '',
    total_number_of_registered_students: '',
    venue: '',
    scheduled_lecture_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const fetchReports = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in.');
      return;
    }

    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/reports`, {
        headers: { 'x-auth-token': token }
      });
      setReports(res.data);
    } catch (err) {
      console.error('Fetch error:', err);
      setError(err.response?.data?.msg || 'Failed to load reports');
    }
  };

  const onSubmit = async e => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No token found. Please log in.');
      return;
    }

    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/reports`, formData, {
        headers: { 'x-auth-token': token }
      });
      alert('Report submitted successfully');
      setFormData({ faculty_name: '', class_name: '', week_of_reporting: '', date_of_lecture: '', course_name: '', course_code: '', lecturer_name: '', actual_number_of_students_present: '', total_number_of_registered_students: '', venue: '', scheduled_lecture_time: '', topic_taught: '', learning_outcomes: '', recommendations: '' });
      fetchReports();
    } catch (err) {
      console.error('Submit error:', err);
      setError(err.response?.data?.msg || 'Failed to submit report');
    }
  };

  useEffect(() => {
    fetchReports();
  }, []);

  return (
    <Card className="p-4">
      <h2>Submit Report</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form onSubmit={onSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Faculty Name</Form.Label>
          <Form.Control type="text" name="faculty_name" value={formData.faculty_name} onChange={onChange} required />
        </Form.Group>

        {/* Repeat for other fields */}
        <Button type="submit">Submit Report</Button>
      </Form>

      <h3 className="mt-4">Reports</h3>
      {reports.length ? (
        <ul>{reports.map(r => <li key={r.id}>{r.topic_taught}</li>)}</ul>
      ) : (
        <p>No reports available.</p>
      )}
    </Card>
  );
};

export default Reports;
