import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Alert, Card } from 'react-bootstrap';

const Rating = ({ role }) => {
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  const [targetOptions, setTargetOptions] = useState([]);
  const [targetId, setTargetId] = useState('');
  const [ratingType, setRatingType] = useState(role === 'student' ? 'lecturer' : 'student');

  useEffect(() => {
    const fetchTargets = async () => {
      try {
        const endpoint = role === 'student' ? '/api/ratings/lecturers-to-rate' : '/api/ratings/students-to-rate';
        const res = await axios.get(`${process.env.REACT_APP_API_URL}${endpoint}`, {
          headers: { 'x-auth-token': localStorage.getItem('token') },
        });
        setTargetOptions(res.data);
      } catch (err) {
        console.error('Fetch items error:', err);
        setError('Failed to load ratee list');
      }
    };
    fetchTargets();
  }, [role]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null);
    setError(null);

    if (!targetId || rating < 1 || rating > 5) {
      setError('Please select a valid target and rating (1-5)');
      return;
    }

    try {
      const payload = {
        ratee_id: targetId,
        rating_type,
        rating,
        comment,
        course_code: '', // Add logic to fetch or select course_code if needed
      };
      await axios.post(`${process.env.REACT_APP_API_URL}/api/ratings`, payload, {
        headers: { 'x-auth-token': localStorage.getItem('token') },
      });
      setMessage('Rating submitted successfully');
      setRating(0);
      setComment('');
      setTargetId('');
    } catch (err) {
      console.error('Fetch ratings error:', err.response?.data || err.message);
      setError('Failed to submit rating');
    }
  };

  return (
    <Card className="p-4">
      <h2>{role === 'student' ? 'Rate Lecturer' : 'Rate Student'}</h2>
      {message && <Alert variant="success">{message}</Alert>}
      {error && <Alert variant="danger">{error}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>{role === 'student' ? 'Select Lecturer' : 'Select Student'}</Form.Label>
          <Form.Control
            as="select"
            value={targetId}
            onChange={(e) => setTargetId(e.target.value)}
            required
          >
            <option value="">Select {role === 'student' ? 'Lecturer' : 'Student'}</option>
            {targetOptions.map((option) => (
              <option key={option.id} value={option.id}>
                {option.username}
              </option>
            ))}
          </Form.Control>
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Rating (1-5)</Form.Label>
          <Form.Control
            type="number"
            min="1"
            max="5"
            value={rating}
            onChange={(e) => setRating(Number(e.target.value))}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Comment</Form.Label>
          <Form.Control
            as="textarea"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
          />
        </Form.Group>
        <Button variant="primary" type="submit">Submit Rating</Button>
      </Form>
    </Card>
  );
};

export default Rating;
