import React, { useState, useEffect, useCallback } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';
import * as XLSX from 'xlsx';

const Reports = ({ role }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);
  const [feedbacks, setFeedbacks] = useState({});
  const [expandedReportId, setExpandedReportId] = useState(null);

  // For editing
  const [editingReportId, setEditingReportId] = useState(null);
  const [editData, setEditData] = useState({});

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const fetchReports = useCallback(async () => {
    const token = localStorage.getItem('token');
    if (!token) return setError('No authentication token.');

    const url =
      role === 'pl'
        ? `${process.env.REACT_APP_API_URL}/reports/prl-feedback`
        : `${process.env.REACT_APP_API_URL}/reports`;

    try {
      const res = await axios.get(url, { headers: { 'x-auth-token': token } });
      setReports(res.data);
      setError(null);
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    }
  }, [role]);

  const onSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return setError('No token found');

    const submitData = {
      faculty_name: formData.faculty_name,
      class_name: formData.class_name,
      week_of_reporting: parseInt(formData.week_of_reporting) || 0,
      date_of_lecture: formData.date_of_lecture,
      course_name: formData.course_name,
      course_code: formData.course_code,
      lecturer_name: formData.lecturer_name,
      actual_number_of_students_present: parseInt(formData.actual_students) || 0,
      total_number_of_registered_students: parseInt(formData.total_registered) || 0,
      venue: formData.venue,
      scheduled_lecture_time: formData.scheduled_time,
      topic_taught: formData.topic_taught,
      learning_outcomes: formData.learning_outcomes,
      recommendations: formData.recommendations,
      lecturer_id: formData.lecturer_id || null
    };

    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/reports`, submitData, {
        headers: { 'x-auth-token': token }
      });
      setFormData({
        faculty_name: '',
        class_name: '',
        week_of_reporting: '',
        date_of_lecture: '',
        course_name: '',
        course_code: '',
        lecturer_name: '',
        actual_students: '',
        total_registered: '',
        venue: '',
        scheduled_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: '',
        lecturer_id: ''
      });
      fetchReports();
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    }
  };

  const startEdit = (report) => {
    setEditingReportId(report.id);
    setEditData({
      ...report,
      actual_students: report.actual_number_of_students_present,
      total_registered: report.total_number_of_registered_students,
      date_of_lecture: report.date_of_lecture?.split('T')[0] || '',
      scheduled_time: report.scheduled_lecture_time || ''
    });
  };

  const submitEdit = async (reportId) => {
    const token = localStorage.getItem('token');
    if (!token) return setError('No token found');

    const data = {
      faculty_name: editData.faculty_name,
      class_name: editData.class_name,
      week_of_reporting: parseInt(editData.week_of_reporting) || 0,
      date_of_lecture: editData.date_of_lecture,
      course_name: editData.course_name,
      course_code: editData.course_code,
      lecturer_name: editData.lecturer_name,
      actual_number_of_students_present: parseInt(editData.actual_students) || 0,
      total_number_of_registered_students: parseInt(editData.total_registered) || 0,
      venue: editData.venue,
      scheduled_lecture_time: editData.scheduled_time,
      topic_taught: editData.topic_taught,
      learning_outcomes: editData.learning_outcomes,
      recommendations: editData.recommendations
    };

    try {
      await axios.put(`${process.env.REACT_APP_API_URL}/reports/${reportId}`, data, {
        headers: { 'x-auth-token': token }
      });
      setEditingReportId(null);
      fetchReports();
    } catch (err) {
      setError('Edit error: ' + (err.response?.data?.message || err.message));
    }
  };

  const handleDelete = async (reportId) => {
    const token = localStorage.getItem('token');
    if (!token) return setError('No token found');

    if (!window.confirm('Are you sure you want to delete this report?')) return;

    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/reports/${reportId}`, {
        headers: { 'x-auth-token': token }
      });
      fetchReports();
    } catch (err) {
      setError('Delete error: ' + (err.response?.data?.message || err.message));
    }
  };

  const handleFeedbackSubmit = async (e, reportId) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const feedback = feedbacks[reportId] || '';
    if (!token) return setError('No token found');
    if (!feedback.trim()) return setError('Please enter feedback');

    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/reports/feedback/${reportId}`,
        { feedback },
        { headers: { 'x-auth-token': token } }
      );
      setFeedbacks((prev) => ({ ...prev, [reportId]: '' }));
      fetchReports();
    } catch (err) {
      setError(err.response?.data?.message || err.message);
    }
  };

  const downloadExcel = () => {
    const worksheetData = reports.map((r) => ({
      'Faculty Name': r.faculty_name,
      'Class Name': r.class_name,
      'Week of Reporting': r.week_of_reporting,
      'Date of Lecture': r.date_of_lecture,
      'Course Name': r.course_name,
      'Course Code': r.course_code,
      'Lecturer Name': r.lecturer_name,
      'Actual Students': r.actual_number_of_students_present,
      'Total Registered': r.total_number_of_registered_students,
      'Venue': r.venue,
      'Scheduled Time': r.scheduled_lecture_time,
      'Topic Taught': r.topic_taught,
      'Learning Outcomes': r.learning_outcomes,
      'Recommendations': r.recommendations,
      'Lecturer ID': r.lecturer_id,
      'Feedback': r.feedback || 'None'
    }));

    const worksheet = XLSX.utils.json_to_sheet(worksheetData);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Reports');
    XLSX.writeFile(workbook, 'Lecture_Reports.xlsx');
  };

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  return (
    <Card className="p-4">
      <h2>{role === 'lecturer' ? 'Submit Report' : 'Reports'}</h2>
      {error && <Alert variant="danger">{error}</Alert>}

      {role === 'lecturer' && (
        <Form onSubmit={onSubmit}>
          {Object.entries(formData).map(([key, value]) => (
            <Form.Group controlId={key} key={key} className="mb-3">
              <Form.Label>{key.replace(/_/g, ' ')}</Form.Label>
              <Form.Control
                type={key.includes('date') ? 'date' : 'text'}
                name={key}
                value={value}
                onChange={onChange}
                required
              />
            </Form.Group>
          ))}
          <Button type="submit">Submit Report</Button>
        </Form>
      )}

      <h3 className="mt-4">Reports</h3>
      <Button variant="success" className="mb-3" onClick={downloadExcel}>
        Download Reports
      </Button>

      {reports.length ? (
        <ul>
          {reports.map((report) => (
            <li key={report.id} className="mb-3">
              <strong>{report.topic_taught}</strong> - {report.date_of_lecture} -{' '}
              {report.lecturer_name}
              <div>
                <Button
                  variant="link"
                  onClick={() =>
                    setExpandedReportId(expandedReportId === report.id ? null : report.id)
                  }
                >
                  {expandedReportId === report.id ? 'Hide Details' : 'View Details'}
                </Button>
                {expandedReportId === report.id && (
                  <div className="p-2 border">
                    {editingReportId === report.id ? (
                      <Form
                        onSubmit={(e) => {
                          e.preventDefault();
                          submitEdit(report.id);
                        }}
                      >
                        {Object.entries(editData).map(([key, value]) => (
                          <Form.Group controlId={`edit_${key}`} key={key} className="mb-2">
                            <Form.Label>{key.replace(/_/g, ' ')}</Form.Label>
                            <Form.Control
                              type={key.includes('date') ? 'date' : 'text'}
                              value={value}
                              onChange={(e) =>
                                setEditData({ ...editData, [key]: e.target.value })
                              }
                            />
                          </Form.Group>
                        ))}
                        <Button type="submit" variant="primary" className="me-2">
                          Save
                        </Button>
                        <Button
                          variant="secondary"
                          onClick={() => setEditingReportId(null)}
                        >
                          Cancel
                        </Button>
                      </Form>
                    ) : (
                      <div>
                        <p>Faculty: {report.faculty_name}</p>
                        <p>Class: {report.class_name}</p>
                        <p>Week: {report.week_of_reporting}</p>
                        <p>Course: {report.course_name}</p>
                        <p>Actual Students: {report.actual_number_of_students_present}</p>
                        <p>Total Registered: {report.total_number_of_registered_students}</p>
                        <p>Venue: {report.venue}</p>
                        <p>Scheduled Time: {report.scheduled_lecture_time}</p>
                        <p>Topic: {report.topic_taught}</p>
                        <p>Learning Outcomes: {report.learning_outcomes}</p>
                        <p>Recommendations: {report.recommendations}</p>
                        <p>Lecturer ID: {report.lecturer_id}</p>
                        <p>Feedback: {report.feedback || 'None'}</p>
                        {role === 'lecturer' && (
                          <>
                            <Button
                              variant="warning"
                              onClick={() => startEdit(report)}
                              className="me-2"
                            >
                              Edit
                            </Button>
                            <Button
                              variant="danger"
                              onClick={() => handleDelete(report.id)}
                            >
                              Delete
                            </Button>
                          </>
                        )}
                        {role === 'prl' && (
                          <Form onSubmit={(e) => handleFeedbackSubmit(e, report.id)}>
                            <Form.Control
                              type="text"
                              value={feedbacks[report.id] || ''}
                              onChange={(e) =>
                                setFeedbacks((prev) => ({
                                  ...prev,
                                  [report.id]: e.target.value
                                }))
                              }
                              placeholder="Add feedback"
                              className="mb-2"
                            />
                            <Button type="submit">Submit Feedback</Button>
                          </Form>
                        )}
                      </div>
                    )}
                  </div>
                )}
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No reports available.</p>
      )}
    </Card>
  );
};

export default Reports;
