import React, { useState, useEffect } from 'react';
import { Form, Button, Alert, ListGroup } from 'react-bootstrap';
import axios from 'axios';

const Rating = ({ role, onRatingUpdate }) => {
  const [targets, setTargets] = useState([]);
  const [selectedTarget, setSelectedTarget] = useState('');
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [existingRatings, setExistingRatings] = useState([]);

  const token = localStorage.getItem('token');

  // Fetch students or lectures
  useEffect(() => {
    const fetchTargets = async () => {
      if (!token) return;

      const url =
        role === 'lecturer'
          ? `${process.env.REACT_APP_API_URL}/api/ratings/students`
          : `${process.env.REACT_APP_API_URL}/api/ratings/lectures`;

      try {
        const res = await axios.get(url, { headers: { 'x-auth-token': token } });
        setTargets(res.data);
        if (res.data.length > 0) setSelectedTarget(res.data[0].id);
      } catch (err) {
        console.error(err);
      }
    };

    fetchTargets();
  }, [role, token]);

  // Fetch existing ratings whenever the selected target changes
  useEffect(() => {
    const fetchRatings = async () => {
      if (!token || !selectedTarget) return;

      const url =
        role === 'lecturer'
          ? `${process.env.REACT_APP_API_URL}/api/ratings/student/${selectedTarget}`
          : `${process.env.REACT_APP_API_URL}/api/ratings/lecture/${selectedTarget}`;

      try {
        const res = await axios.get(url, { headers: { 'x-auth-token': token } });
        setExistingRatings(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchRatings();
  }, [selectedTarget, role, token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);

    if (!selectedTarget) return setError('Please select a target to rate.');
    if (!rating || rating < 1 || rating > 5) return setError('Rating must be 1-5.');

    const payload = { rating: parseInt(rating) };
    if (role === 'student') payload.comment = comment;

    const url =
      role === 'lecturer'
        ? `${process.env.REACT_APP_API_URL}/api/ratings/student/${selectedTarget}`
        : `${process.env.REACT_APP_API_URL}/api/ratings/lecture/${selectedTarget}`;

    try {
      await axios.post(url, payload, { headers: { 'x-auth-token': token } });
      setRating('');
      setComment('');
      setSuccess('Rating submitted successfully!');
      if (onRatingUpdate) onRatingUpdate();
      
      // Refresh existing ratings after submission
      const res = await axios.get(url, { headers: { 'x-auth-token': token } });
      setExistingRatings(res.data);

      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to submit rating.');
    }
  };

  return (
    <div className="mt-3">
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="targetSelect" className="mb-3">
          <Form.Label>{role === 'lecturer' ? 'Select Student' : 'Select Lecture'}</Form.Label>
          <Form.Select
            value={selectedTarget}
            onChange={(e) => setSelectedTarget(e.target.value)}
          >
            {targets.map((t) => (
              <option key={t.id} value={t.id}>
                {role === 'lecturer' ? t.name : t.title}
              </option>
            ))}
          </Form.Select>
        </Form.Group>

        {role === 'student' && (
          <Form.Group controlId="comment" className="mb-3">
            <Form.Label>Comment (optional)</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={comment}
              onChange={(e) => setComment(e.target.value)}
            />
          </Form.Group>
        )}

        <Form.Group controlId="rating" className="mb-3">
          <Form.Label>Rating (1-5)</Form.Label>
          <Form.Control
            type="number"
            min="1"
            max="5"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            required
          />
        </Form.Group>

        <Button type="submit">Submit Rating</Button>
      </Form>

      {/* Display existing ratings */}
      <div className="mt-4">
        <h5>Existing Ratings:</h5>
        {existingRatings.length === 0 ? (
          <p>No ratings yet.</p>
        ) : (
          <ListGroup>
            {existingRatings.map((r) => (
              <ListGroup.Item key={r.id}>
                <strong>Rating:</strong> {r.rating} {r.comment && <>| <strong>Comment:</strong> {r.comment}</>}
                {r.lecturer_id && <> | <strong>By Lecturer ID:</strong> {r.lecturer_id}</>}
                {r.student_id && role === 'lecturer' && <> | <strong>Student ID:</strong> {r.student_id}</>}
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}
      </div>
    </div>
  );
};

export default Rating;
