import React, { useState, useEffect, useCallback } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';

const Reports = ({ role }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);
  const [feedbacks, setFeedbacks] = useState({}); // feedback per report
  const [expandedReportId, setExpandedReportId] = useState(null);

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const fetchReports = useCallback(async (q = '') => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }

    let url;
    if (role === 'pl') {
      url = `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/prl-feedback`;
      console.log('Fetching PRL feedback reports for PL:', url);
    } else {
      url = `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports${q ? `?q=${q}` : ''}`;
      console.log('Fetching all reports for role:', role, url);
    }

    try {
      setError(null);
      const response = await axios.get(url, { headers: { 'x-auth-token': token } });
      setReports(response.data);
    } catch (err) {
      console.error('Fetch error - URL:', url, 'Error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized: No token or token expired.');
        else if (status === 403) setError('Forbidden: Your role does not have access to reports.');
        else if (status === 400) setError('Invalid token or bad request.');
        else setError(`Server error (${status}): ${data.message || 'Unknown error'}`);
      } else if (err.request) {
        setError('No response from server. Check your network or server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  }, [role]);

  const onSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    if (!token) {
      setError('No token found. Please log in.');
      return;
    }

    // Frontend validation for required fields (all except lecturer_id)
    const requiredFields = {
      faculty_name: formData.faculty_name,
      class_name: formData.class_name,
      week_of_reporting: formData.week_of_reporting,
      date_of_lecture: formData.date_of_lecture,
      course_name: formData.course_name,
      course_code: formData.course_code,
      lecturer_name: formData.lecturer_name,
      actual_students: formData.actual_students,
      total_registered: formData.total_registered,
      venue: formData.venue,
      scheduled_time: formData.scheduled_time,
      topic_taught: formData.topic_taught,
      learning_outcomes: formData.learning_outcomes,
      recommendations: formData.recommendations,
    };

    const missingFields = Object.entries(requiredFields)
      .filter(([_, value]) => value === '' || value === null || value === undefined)
      .map(([key]) => key);

    if (missingFields.length > 0) {
      setError(`All fields are required except lecturer_id. Missing: ${missingFields.join(', ')}`);
      return;
    }

    try {
      setError(null);

      const submitData = {
        faculty_name: formData.faculty_name.trim(),
        class_name: formData.class_name.trim(),
        week_of_reporting: parseInt(formData.week_of_reporting, 10),
        date_of_lecture: new Date(formData.date_of_lecture).toISOString().split('T')[0],
        course_name: formData.course_name.trim(),
        course_code: formData.course_code.trim(),
        lecturer_name: formData.lecturer_name.trim(),
        actual_number_of_students_present: parseInt(formData.actual_students, 10),
        total_number_of_registered_students: parseInt(formData.total_registered, 10),
        venue: formData.venue.trim(),
        scheduled_lecture_time: formData.scheduled_time.trim(),
        topic_taught: formData.topic_taught.trim(),
        learning_outcomes: formData.learning_outcomes.trim(),
        recommendations: formData.recommendations.trim(),
        lecturer_id: formData.lecturer_id.trim() || null,
      };

      await axios.post(
        `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports`,
        submitData,
        { headers: { 'x-auth-token': token } }
      );

      alert('Report submitted successfully');

      setFormData({
        faculty_name: '',
        class_name: '',
        week_of_reporting: '',
        date_of_lecture: '',
        course_name: '',
        course_code: '',
        lecturer_name: '',
        actual_students: '',
        total_registered: '',
        venue: '',
        scheduled_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: '',
        lecturer_id: ''
      });

      fetchReports();
    } catch (err) {
      console.error('Submit error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 400) {
          setError(`Invalid request: ${data.message || 'Check form data.'}`);
        } else if (status === 401) {
          setError('Unauthorized: No token or token expired.');
        } else if (status === 403) {
          setError('Forbidden: Your role does not have permission to submit reports.');
        } else {
          setError(`Server error (${status}): ${data.message || 'Unknown error'}`);
        }
      } else if (err.request) {
        setError('No response from server. Check your network or server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  };

  const handleFeedbackSubmit = async (e, reportId) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const feedback = feedbacks[reportId] || '';

    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }
    if (!reportId) {
      setError('No report selected for feedback.');
      return;
    }
    if (!feedback.trim()) {
      setError('Please enter feedback before submitting.');
      return;
    }

    try {
      setError(null);
      await axios.post(
        `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/feedback/${reportId}`,
        { feedback },
        { headers: { 'x-auth-token': token } }
      );
      setFeedbacks((prev) => ({ ...prev, [reportId]: '' })); // Clear feedback for this report
      fetchReports();
    } catch (err) {
      console.error('Feedback error - URL:', `${process.env.REACT_APP_API_URL}/reports/feedback/${reportId}`, 'Error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized: No token or token expired.');
        else if (status === 403) setError('Forbidden: Only PRL can add feedback.');
        else setError(`Server error (${status}): ${data.message || 'Unknown error'}`);
      } else if (err.request) {
        setError('No response from server. Check your network or server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  };

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  return (
    <Card className="p-4">
      <h2>
        {role === 'lecturer'
          ? 'Submit Report'
          : role === 'prl'
          ? 'View Lecture Reports & Add Feedback'
          : 'View PRL Reports'}
      </h2>
      {error && <Alert variant="danger">{error}</Alert>}

      {role === 'lecturer' && (
        <Form onSubmit={onSubmit}>
          {Object.entries(formData).map(([key, value]) => (
            <Form.Group controlId={key} className="mb-3" key={key}>
              <Form.Label>
                {key.replace(/_/g, ' ').replace(/\b\w/g, (l) => l.toUpperCase())}
              </Form.Label>
              <Form.Control
                type={key.includes('date') ? 'date' : 'text'}
                name={key}
                value={value}
                onChange={onChange}
                required={key !== 'lecturer_id'}
              />
            </Form.Group>
          ))}

          <Button variant="primary" type="submit">
            Submit Report
          </Button>
        </Form>
      )}

      <h3 className="mt-4">Reports</h3>
      {reports.length ? (
        <ul>
          {reports.map((report) => (
            <li key={report.id}>
              <Button
                variant="link"
                onClick={() =>
                  setExpandedReportId(expandedReportId === report.id ? null : report.id)
                }
              >
                {expandedReportId === report.id ? 'Hide Details' : 'View Details'} -{' '}
                {report.topic_taught} - {report.date_of_lecture} - {report.lecturer_name}
              </Button>
              {(role === 'prl' || role === 'pl') && expandedReportId === report.id && (
                <div>
                  <p><strong>Faculty Name:</strong> {report.faculty_name}</p>
                  <p><strong>Class Name:</strong> {report.class_name}</p>
                  <p><strong>Week of Reporting:</strong> {report.week_of_reporting}</p>
                  <p><strong>Date of Lecture:</strong> {report.date_of_lecture}</p>
                  <p><strong>Course Name:</strong> {report.course_name}</p>
                  <p><strong>Course Code:</strong> {report.course_code}</p>
                  <p><strong>Lecturer Name:</strong> {report.lecturer_name}</p>
                  <p><strong>Actual Students:</strong> {report.actual_number_of_students_present}</p>
                  <p><strong>Total Registered:</strong> {report.total_number_of_registered_students}</p>
                  <p><strong>Venue:</strong> {report.venue}</p>
                  <p><strong>Scheduled Time:</strong> {report.scheduled_lecture_time}</p>
                  <p><strong>Topic Taught:</strong> {report.topic_taught}</p>
                  <p><strong>Learning Outcomes:</strong> {report.learning_outcomes}</p>
                  <p><strong>Recommendations:</strong> {report.recommendations}</p>
                  <p><strong>Lecturer ID:</strong> {report.lecturer_id}</p>
                  <p><strong>Feedback:</strong> {report.feedback || 'None'}</p>
                  {role === 'prl' && (
                    <Form onSubmit={(e) => handleFeedbackSubmit(e, report.id)}>
                      <Form.Control
                        type="text"
                        name="feedback"
                        value={feedbacks[report.id] || ''}
                        onChange={(e) =>
                          setFeedbacks((prev) => ({ ...prev, [report.id]: e.target.value }))
                        }
                        placeholder="Add feedback"
                      />
                      <Button variant="secondary" type="submit" className="mt-2">
                        Submit Feedback
                      </Button>
                    </Form>
                  )}
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No reports available.</p>
      )}
    </Card>
  );
};

export default Reports;
