import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Card, InputGroup, FormControl } from 'react-bootstrap';

const Reports = ({ role }) => {
  const [reports, setReports] = useState([]);
  const [formData, setFormData] = useState({
    faculty_name: '', class_name: '', week_of_reporting: '', date_of_lecture: '',
    course_name: '', course_code: '', lecturer_name: '', actual_students: '',
    total_registered: '', venue: '', scheduled_time: '', topic_taught: '',
    learning_outcomes: '', recommendations: ''
  });
  const [search, setSearch] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async (q = '') => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }
    try {
      setError(null);
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/reports${q ? `?q=${q}` : ''}`, {
        headers: { 'x-auth-token': token }
      });
      setReports(res.data);
    } catch (err) {
      console.error('Fetch error:', err.response ? err.response.data : err);
      setError('Failed to load reports. ' + (err.response?.data?.msg || 'Check server or token.'));
    }
  };

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      setError(null);
      const submitData = {
        ...formData,
        date_of_lecture: new Date(formData.date_of_lecture).toISOString().split('T')[0],
        actual_students: parseInt(formData.actual_students),
        total_registered: parseInt(formData.total_registered)
      };
      const token = localStorage.getItem('token');
      if (!token) throw new Error('No token. Please log in.');
      await axios.post(`${process.env.REACT_APP_API_URL}/api/reports`, submitData, {
        headers: { 'x-auth-token': token }
      });
      alert('Report submitted successfully');
      setFormData({
        faculty_name: '', class_name: '', week_of_reporting: '', date_of_lecture: '',
        course_name: '', course_code: '', lecturer_name: '', actual_students: '',
        total_registered: '', venue: '', scheduled_time: '', topic_taught: '',
        learning_outcomes: '', recommendations: ''
      });
      fetchReports();
    } catch (err) {
      console.error('Submit error:', err.response ? err.response.data : err);
      setError('Failed to submit report. ' + (err.response?.data?.msg || 'Check form data or server.'));
    }
  };

  const addFeedback = async (id, feedback) => {
    try {
      setError(null);
      const token = localStorage.getItem('token');
      if (!token) throw new Error('No token. Please log in.');
      await axios.put(`${process.env.REACT_APP_API_URL}/api/reports/${id}/feedback`, { feedback }, {
        headers: { 'x-auth-token': token }
      });
      fetchReports();
    } catch (err) {
      console.error('Feedback error:', err);
      setError('Failed to add feedback.');
    }
  };

  const handleSearch = e => {
    setSearch(e.target.value);
    fetchReports(e.target.value);
  };

  return (
    <Card className="p-4">
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {role === 'lecturer' && (
        <Form onSubmit={onSubmit} className="report-form">
          <Form.Group controlId="faculty_name" className="mb-3">
            <Form.Label>Faculty Name</Form.Label>
            <Form.Control type="text" name="faculty_name" value={formData.faculty_name} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="class_name" className="mb-3">
            <Form.Label>Class Name</Form.Label>
            <Form.Control type="text" name="class_name" value={formData.class_name} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="week_of_reporting" className="mb-3">
            <Form.Label>Week of Reporting</Form.Label>
            <Form.Control type="number" name="week_of_reporting" value={formData.week_of_reporting} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="date_of_lecture" className="mb-3">
            <Form.Label>Date of Lecture</Form.Label>
            <Form.Control type="date" name="date_of_lecture" value={formData.date_of_lecture} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="course_name" className="mb-3">
            <Form.Label>Course Name</Form.Label>
            <Form.Control type="text" name="course_name" value={formData.course_name} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="course_code" className="mb-3">
            <Form.Label>Course Code</Form.Label>
            <Form.Control type="text" name="course_code" value={formData.course_code} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="lecturer_name" className="mb-3">
            <Form.Label>Lecturer Name</Form.Label>
            <Form.Control type="text" name="lecturer_name" value={formData.lecturer_name} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="actual_students" className="mb-3">
            <Form.Label>Actual Number of Students Present</Form.Label>
            <Form.Control type="number" name="actual_students" value={formData.actual_students} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="total_registered" className="mb-3">
            <Form.Label>Total Number of Registered Students</Form.Label>
            <Form.Control type="number" name="total_registered" value={formData.total_registered} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="venue" className="mb-3">
            <Form.Label>Venue</Form.Label>
            <Form.Control type="text" name="venue" value={formData.venue} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="scheduled_time" className="mb-3">
            <Form.Label>Scheduled Lecture Time</Form.Label>
            <Form.Control type="time" name="scheduled_time" value={formData.scheduled_time} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="topic_taught" className="mb-3">
            <Form.Label>Topic Taught</Form.Label>
            <Form.Control as="textarea" name="topic_taught" value={formData.topic_taught} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="learning_outcomes" className="mb-3">
            <Form.Label>Learning Outcomes</Form.Label>
            <Form.Control as="textarea" name="learning_outcomes" value={formData.learning_outcomes} onChange={onChange} required />
          </Form.Group>
          <Form.Group controlId="recommendations" className="mb-3">
            <Form.Label>Lecturer's Recommendations</Form.Label>
            <Form.Control as="textarea" name="recommendations" value={formData.recommendations} onChange={onChange} required />
          </Form.Group>
          <Button variant="primary" type="submit">Submit Report</Button>
        </Form>
      )}
      <InputGroup className="mt-4 mb-3 search-input">
        <FormControl placeholder="Search reports..." value={search} onChange={handleSearch} />
      </InputGroup>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Course Name</th>
            <th>Topic Taught</th>
            <th>Week</th>
            {role === 'prl' && <th>Feedback</th>}
          </tr>
        </thead>
        <tbody>
          {reports.map(report => (
            <tr key={report.id}>
              <td>{report.id}</td>
              <td>{report.course_name}</td>
              <td>{report.topic_taught}</td>
              <td>{report.week_of_reporting}</td>
              {role === 'prl' && (
                <td>
                  {report.feedback || (
                    <FormControl
                      as="textarea"
                      onBlur={e => addFeedback(report.id, e.target.value)}
                      placeholder="Add feedback"
                    />
                  )}
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </Table>
    </Card>
  );
};

export default Reports;
