import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Button, Form, Alert } from 'react-bootstrap';

const Lectures = ({ role }) => {
  const [lectures, setLectures] = useState([]);
  const [courses, setCourses] = useState([]);
  const [users, setUsers] = useState([]);
  const [formData, setFormData] = useState({ course_id: '', lecturer_id: '' });
  const [message, setMessage] = useState(null);
  const token = localStorage.getItem('token');

  // Fetch data
  useEffect(() => {
    fetchLectures();
    if (role !== 'student') {
      fetchCourses();
      fetchUsers();
    }
  }, [role]);

  const fetchLectures = async () => {
    try {
      const res = await axios.get('/api/lectures', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setLectures(res.data);
    } catch (error) {
      console.error('Error fetching lectures:', error);
    }
  };

  const fetchCourses = async () => {
    try {
      const res = await axios.get('/api/courses', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCourses(res.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const res = await axios.get('/api/users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(res.data.filter(u => u.role === 'pl'));
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  // Add lecture
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/lectures', formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessage({ type: 'success', text: res.data.message });
      setFormData({ course_id: '', lecturer_id: '' });
      fetchLectures();
    } catch (error) {
      setMessage({ type: 'danger', text: error.response?.data?.message || 'Error adding lecture' });
    }
  };

  // Delete lecture
  const handleDelete = async (id) => {
    if (!window.confirm('Delete this lecture?')) return;
    try {
      await axios.delete(`/api/lectures/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchLectures();
    } catch (error) {
      console.error('Error deleting lecture:', error);
    }
  };

  return (
    <div className="container mt-4">
      <h2>Lectures</h2>
      {message && <Alert variant={message.type}>{message.text}</Alert>}

      {role !== 'student' && (
        <Card className="p-3 mb-3">
          <h5>Add Lecture</h5>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-2">
              <Form.Label>Course</Form.Label>
              <Form.Select
                value={formData.course_id}
                onChange={(e) => setFormData({ ...formData, course_id: e.target.value })}
                required
              >
                <option value="">Select Course</option>
                {courses.map((c) => (
                  <option key={c.id} value={c.id}>{c.course_name}</option>
                ))}
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Lecturer</Form.Label>
              <Form.Select
                value={formData.lecturer_id}
                onChange={(e) => setFormData({ ...formData, lecturer_id: e.target.value })}
                required
              >
                <option value="">Select Lecturer</option>
                {users.map((u) => (
                  <option key={u.id} value={u.id}>{u.username}</option>
                ))}
              </Form.Select>
            </Form.Group>

            <Button type="submit" variant="primary">Add Lecture</Button>
          </Form>
        </Card>
      )}

      {lectures.map((lecture) => (
        <Card key={lecture.id} className="p-3 mb-3">
          <h5>{lecture.course_name} ({lecture.course_code})</h5>
          <p>Lecturer: {lecture.lecturer_name}</p>
          {role !== 'student' && (
            <Button variant="danger" onClick={() => handleDelete(lecture.id)}>
              Delete
            </Button>
          )}
        </Card>
      ))}
    </div>
  );
};

export default Lectures;
