import React, { useState, useEffect, useCallback } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';
import * as XLSX from 'xlsx';

const Reports = ({ role, onReportUpdate }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [feedbacks, setFeedbacks] = useState({});
  const [expandedReportId, setExpandedReportId] = useState(null);
  const [editingReportId, setEditingReportId] = useState(null);
  const [editData, setEditData] = useState({});
  const [lectureRating, setLectureRating] = useState({});
  const [studentRatings, setStudentRatings] = useState({});

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const fetchReports = useCallback(async () => {
    const token = localStorage.getItem('token');
    if (!token) return setError('No authentication token.');

    const url =
      role === 'pl'
        ? `${process.env.REACT_APP_API_URL}/reports/prl-feedback`
        : `${process.env.REACT_APP_API_URL}/reports`;

    try {
      const res = await axios.get(url, { headers: { 'x-auth-token': token } });
      setReports(res.data);
      setError(null);
      setSuccess(null);
    } catch (err) {
      setError(err.response?.data?.message || err.message);
      setSuccess(null);
    }
  }, [role]);

  const onSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return setError('No token found');

    const submitData = {
      faculty_name: formData.faculty_name,
      class_name: formData.class_name,
      week_of_reporting: parseInt(formData.week_of_reporting) || 0,
      date_of_lecture: formData.date_of_lecture,
      course_name: formData.course_name,
      course_code: formData.course_code,
      lecturer_name: formData.lecturer_name,
      actual_number_of_students_present: parseInt(formData.actual_students) || 0,
      total_number_of_registered_students: parseInt(formData.total_registered) || 0,
      venue: formData.venue,
      scheduled_lecture_time: formData.scheduled_time,
      topic_taught: formData.topic_taught,
      learning_outcomes: formData.learning_outcomes,
      recommendations: formData.recommendations,
      lecturer_id: formData.lecturer_id || null
    };

    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/reports`, submitData, {
        headers: { 'x-auth-token': token }
      });
      setFormData({
        faculty_name: '',
        class_name: '',
        week_of_reporting: '',
        date_of_lecture: '',
        course_name: '',
        course_code: '',
        lecturer_name: '',
        actual_students: '',
        total_registered: '',
        venue: '',
        scheduled_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: '',
        lecturer_id: ''
      });
      setSuccess('Report and class updated successfully');
      setError(null);
      fetchReports();
      onReportUpdate();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(err.response?.data?.message || err.message);
      setSuccess(null);
    }
  };

  const handleLectureRatingSubmit = async (e, reportId) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return setError('No token found');

    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/ratings/lecture/${reportId}`,
        {
          lecture_id: reportId,
          student_id: localStorage.getItem('userId'),
          rating: lectureRating[reportId],
        },
        { headers: { 'x-auth-token': token } }
      );
      setLectureRating((prev) => ({ ...prev, [reportId]: '' }));
      setSuccess('Lecture rating submitted successfully');
      setError(null);
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(err.response?.data?.message || err.message);
      setSuccess(null);
    }
  };

  const handleStudentRatingSubmit = async (e, reportId, studentId) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return setError('No token found');

    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/ratings/student/${reportId}`,
        {
          student_id: studentId,
          lecturer_id: localStorage.getItem('userId'),
          rating: studentRatings[`${reportId}_${studentId}`],
        },
        { headers: { 'x-auth-token': token } }
      );
      setStudentRatings((prev) => ({ ...prev, [`${reportId}_${studentId}`]: '' }));
      setSuccess('Student rating submitted successfully');
      setError(null);
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      setError(err.response?.data?.message || err.message);
      setSuccess(null);
    }
  };

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  return (
    <Card className="p-4">
      <h2>{role === 'lecturer' ? 'Submit Report' : 'Reports'}</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      {role === 'lecturer' && (
        <Form onSubmit={onSubmit}>
          {Object.entries(formData).map(([key, value]) => (
            <Form.Group controlId={key} key={key} className="mb-3">
              <Form.Label>{key.replace(/_/g, ' ')}</Form.Label>
              <Form.Control
                type={key.includes('date') ? 'date' : 'text'}
                name={key}
                value={value}
                onChange={onChange}
                required
              />
            </Form.Group>
          ))}
          <Button type="submit">Submit Report</Button>
        </Form>
      )}

      <h3 className="mt-4">Reports</h3>
      {reports.length ? (
        <ul>
          {reports.map((report) => (
            <li key={report.id} className="mb-3">
              <strong>{report.topic_taught}</strong> - {report.date_of_lecture} -{' '}
              {report.lecturer_name}
              <div>
                <Button
                  variant="link"
                  onClick={() =>
                    setExpandedReportId(expandedReportId === report.id ? null : report.id)
                  }
                >
                  {expandedReportId === report.id ? 'Hide Details' : 'View Details'}
                </Button>
                {expandedReportId === report.id && (
                  <div className="p-2 border">
                    <p>Faculty: {report.faculty_name}</p>
                    <p>Class: {report.class_name}</p>
                    <p>Course: {report.course_name}</p>
                    <p>Lecturer: {report.lecturer_name}</p>
                    <p>Students Present: {report.actual_number_of_students_present}</p>

                    {/* Student role: rate lecture */}
                    {role === 'student' && (
                      <Form
                        onSubmit={(e) => handleLectureRatingSubmit(e, report.id)}
                        className="mt-2"
                      >
                        <Form.Group controlId={`lecture_rating_${report.id}`}>
                          <Form.Label>Rate Lecture (1-5)</Form.Label>
                          <Form.Control
                            type="number"
                            value={lectureRating[report.id] || ''}
                            onChange={(e) =>
                              setLectureRating((prev) => ({
                                ...prev,
                                [report.id]: e.target.value
                              }))
                            }
                            min="1"
                            max="5"
                            required
                          />
                        </Form.Group>
                        <Button type="submit" className="mt-2">
                          Submit Lecture Rating
                        </Button>
                      </Form>
                    )}

                    {/* Lecturer role: rate student */}
                    {role === 'lecturer' && (
                      <Form
                        onSubmit={(e) =>
                          handleStudentRatingSubmit(e, report.id, formData.studentId)
                        }
                        className="mt-2"
                      >
                        <Form.Group controlId={`student_id_${report.id}`}>
                          <Form.Label>Student ID</Form.Label>
                          <Form.Control
                            type="text"
                            value={formData.studentId || ''}
                            onChange={(e) =>
                              setFormData({ ...formData, studentId: e.target.value })
                            }
                            required
                          />
                        </Form.Group>
                        <Form.Group controlId={`student_rating_${report.id}`}>
                          <Form.Label>Rate Student (1-5)</Form.Label>
                          <Form.Control
                            type="number"
                            value={studentRatings[`${report.id}_${formData.studentId}`] || ''}
                            onChange={(e) =>
                              setStudentRatings({
                                ...studentRatings,
                                [`${report.id}_${formData.studentId}`]: e.target.value
                              })
                            }
                            min="1"
                            max="5"
                            required
                          />
                        </Form.Group>
                        <Button type="submit" className="mt-2">
                          Submit Student Rating
                        </Button>
                      </Form>
                    )}
                  </div>
                )}
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No reports available.</p>
      )}
    </Card>
  );
};

export default Reports;
