import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Card, InputGroup, FormControl } from 'react-bootstrap';

const Rating = ({ role }) => {
  const [ratings, setRatings] = useState([]);
  const [formData, setFormData] = useState({ rating: '', comment: '' });
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchRatings();
  }, []);

  const fetchRatings = async (q = '') => {
    try {
      // Backend expects query parameter 'q' to filter by rating or comment
      const url = q
        ? `${process.env.REACT_APP_API_URL}/ratings?q=${encodeURIComponent(q)}`
        : `${process.env.REACT_APP_API_URL}/ratings`;

      const res = await axios.get(url, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });

      setRatings(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/ratings`, formData, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setFormData({ rating: '', comment: '' });
      fetchRatings(search); // ✅ keep search applied after adding new rating
    } catch (err) {
      console.error(err.response?.data?.message || 'Error submitting rating');
    }
  };

  const handleSearch = e => {
    setSearch(e.target.value);
    fetchRatings(e.target.value);
  };

  return (
    <Card className="p-4">
      <Form onSubmit={onSubmit} className="mb-4">
        <Form.Group controlId="rating" className="mb-3">
          <Form.Label>Rating (1-5)</Form.Label>
          <Form.Control
            type="number"
            name="rating"
            value={formData.rating}
            onChange={onChange}
            min="1"
            max="5"
            required
          />
        </Form.Group>
        <Form.Group controlId="comment" className="mb-3">
          <Form.Label>Comment</Form.Label>
          <Form.Control
            as="textarea"
            name="comment"
            value={formData.comment}
            onChange={onChange}
          />
        </Form.Group>
        <Button variant="primary" type="submit">Submit Rating</Button>
      </Form>
      <InputGroup className="mb-3 search-input">
        <FormControl
          placeholder="Search ratings..."
          value={search}
          onChange={handleSearch}
        />
      </InputGroup>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Rating</th>
            <th>Comment</th>
          </tr>
        </thead>
        <tbody>
          {ratings.map(rating => (
            <tr key={rating.id}>
              <td>{rating.id}</td>
              <td>{rating.rating}</td>
              <td>{rating.comment}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Card>
  );
};

export default Rating;
