import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, Card, InputGroup, FormControl } from 'react-bootstrap';

const Classes = ({ role }) => {
  const [classes, setClasses] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchClasses();
  }, []);

  const fetchClasses = async (q = '') => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/classes${q ? `?q=${q}` : ''}`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setClasses(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSearch = e => {
    setSearch(e.target.value);
    fetchClasses(e.target.value);
  };

  return (
    <Card className="p-4">
      <InputGroup className="mb-3 search-input">
        <FormControl placeholder="Search classes..." value={search} onChange={handleSearch} />
      </InputGroup>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Venue</th>
          </tr>
        </thead>
        <tbody>
          {classes.map(cls => (
            <tr key={cls.class_id}> {/* ✅ changed to class_id */}
              <td>{cls.class_id}</td>
              <td>{cls.class_name}</td> {/* ✅ changed to class_name */}
              <td>{cls.venue}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Card>
  );
};

export default Classes;
