import React, { useState, useEffect, useCallback } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';
import * as XLSX from 'xlsx';

const Reports = ({ role }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null); // ✅ Added success state
  const [feedbacks, setFeedbacks] = useState({});
  const [expandedReportId, setExpandedReportId] = useState(null);
  const [editReportId, setEditReportId] = useState(null);
  const [editData, setEditData] = useState({});

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const fetchReports = useCallback(async () => {
    const token = localStorage.getItem('token');
    if (!token) return setError('No authentication token.');

    try {
      setError(null);
      const url =
        role === 'pl'
          ? `${process.env.REACT_APP_API_URL}/reports/prl-feedback`
          : `${process.env.REACT_APP_API_URL}/reports`;
      const response = await axios.get(url, { headers: { 'x-auth-token': token } });
      setReports(response.data);
    } catch (err) {
      console.error('Fetch error:', err);
      setError('Failed to fetch reports');
    }
  }, [role]);

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  // ----------------- Edit Report -----------------
  const startEdit = (report) => {
    setEditReportId(report.id);
    setEditData({
      topic_taught: report.topic_taught,
      date_of_lecture: report.date_of_lecture.split('T')[0], // format yyyy-MM-dd
      actual_number_of_students_present: report.actual_number_of_students_present,
      total_number_of_registered_students: report.total_number_of_registered_students,
      venue: report.venue
    });
  };

  const handleEditChange = (e) => {
    setEditData({ ...editData, [e.target.name]: e.target.value });
  };

  const submitEdit = async (id) => {
    const token = localStorage.getItem('token');
    try {
      await axios.put(`${process.env.REACT_APP_API_URL}/reports/${id}`, editData, {
        headers: { 'x-auth-token': token }
      });
      setSuccess('Report updated successfully'); // ✅ Success message
      setEditReportId(null);
      fetchReports();
    } catch (err) {
      console.error('Edit error:', err);
      setError('Error editing report');
    }
  };

  // ----------------- Delete Report -----------------
  const handleDelete = async (id) => {
    const token = localStorage.getItem('token');
    if (!window.confirm('Are you sure you want to delete this report?')) return;

    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/reports/${id}`, {
        headers: { 'x-auth-token': token }
      });
      setSuccess('Report deleted successfully'); // ✅ Success message
      fetchReports();
    } catch (err) {
      console.error('Delete error:', err);
      setError('Error deleting report');
    }
  };

  return (
    <Card className="p-4">
      <h2>Reports</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>} {/* ✅ Display success */}

      {reports.length ? (
        <ul>
          {reports.map((report) => (
            <li key={report.id} className="mb-3">
              <strong>{report.topic_taught}</strong> - {report.date_of_lecture} - {report.lecturer_name}
              <div>
                <Button
                  variant="info"
                  className="me-2"
                  onClick={() => startEdit(report)}
                >
                  Edit
                </Button>
                <Button
                  variant="danger"
                  onClick={() => handleDelete(report.id)}
                >
                  Delete
                </Button>
              </div>

              {editReportId === report.id && (
                <div className="mt-2 p-2 border">
                  <Form>
                    <Form.Group className="mb-2">
                      <Form.Label>Topic Taught</Form.Label>
                      <Form.Control
                        name="topic_taught"
                        value={editData.topic_taught}
                        onChange={handleEditChange}
                      />
                    </Form.Group>
                    <Form.Group className="mb-2">
                      <Form.Label>Date of Lecture</Form.Label>
                      <Form.Control
                        type="date"
                        name="date_of_lecture"
                        value={editData.date_of_lecture}
                        onChange={handleEditChange}
                      />
                    </Form.Group>
                    <Form.Group className="mb-2">
                      <Form.Label>Actual Students Present</Form.Label>
                      <Form.Control
                        type="number"
                        name="actual_number_of_students_present"
                        value={editData.actual_number_of_students_present}
                        onChange={handleEditChange}
                      />
                    </Form.Group>
                    <Form.Group className="mb-2">
                      <Form.Label>Total Registered Students</Form.Label>
                      <Form.Control
                        type="number"
                        name="total_number_of_registered_students"
                        value={editData.total_number_of_registered_students}
                        onChange={handleEditChange}
                      />
                    </Form.Group>
                    <Form.Group className="mb-2">
                      <Form.Label>Venue</Form.Label>
                      <Form.Control
                        name="venue"
                        value={editData.venue}
                        onChange={handleEditChange}
                      />
                    </Form.Group>
                    <Button
                      variant="success"
                      onClick={() => submitEdit(report.id)}
                      className="me-2"
                    >
                      Save
                    </Button>
                    <Button variant="secondary" onClick={() => setEditReportId(null)}>
                      Cancel
                    </Button>
                  </Form>
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No reports available.</p>
      )}
    </Card>
  );
};

export default Reports;
