import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Card, Accordion, Alert } from 'react-bootstrap';

const Reports = ({ role }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_number_of_students_present: '',
    total_number_of_registered_students: '',
    venue: '',
    scheduled_lecture_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });

  const [reports, setReports] = useState([]);
  const [feedback, setFeedback] = useState('');
  const [selectedReportId, setSelectedReportId] = useState(null);
  const [expandedReportId, setExpandedReportId] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }

    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/reports`, {
        headers: { 'x-auth-token': token }
      });
      setReports(res.data);
      setError(null);
    } catch (err) {
      console.error('Fetch error:', err);
      setError(err.response?.data?.message || 'Failed to fetch reports.');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }

    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/reports`, formData, {
        headers: { 'x-auth-token': token }
      });
      setFormData({
        faculty_name: '',
        class_name: '',
        week_of_reporting: '',
        date_of_lecture: '',
        course_name: '',
        course_code: '',
        lecturer_name: '',
        actual_number_of_students_present: '',
        total_number_of_registered_students: '',
        venue: '',
        scheduled_lecture_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: '',
        lecturer_id: ''
      });
      fetchReports();
      setError(null);
    } catch (err) {
      console.error('Submit error:', err);
      setError(err.response?.data?.message || 'Failed to submit report.');
    }
  };

  const handleFeedbackSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    console.log('Feedback API URL:', `${process.env.REACT_APP_API_URL}/reports/feedback/${selectedReportId}`);
    if (!token || !selectedReportId) {
      setError('No token or report selected for feedback.');
      return;
    }

    try {
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/reports/feedback/${selectedReportId}`,
        { feedback },
        { headers: { 'x-auth-token': token } }
      );
      console.log('Feedback response:', response.data);
      setFeedback('');
      setSelectedReportId(null);
      fetchReports();
      setError(null);
    } catch (err) {
      console.error('Feedback error:', err);
      setError(err.response?.data?.message || `Server error (${err.response?.status || 'Unknown'}): Unknown error`);
    }
  };

  const toggleExpand = (reportId) => {
    setExpandedReportId(expandedReportId === reportId ? null : reportId);
  };

  return (
    <Card className="p-4">
      <h2>{role === 'lecturer' ? 'Submit Report' : role === 'prl' ? 'View Lecture Reports & Add Feedback' : 'View PRL Reports'}</h2>
      {error && <Alert variant="danger">{error}</Alert>}

      {role === 'lecturer' && (
        <Form onSubmit={handleSubmit}>
          <Form.Control type="text" name="faculty_name" value={formData.faculty_name} placeholder="Faculty Name" onChange={handleChange} required />
          <Form.Control type="text" name="class_name" value={formData.class_name} placeholder="Class Name" onChange={handleChange} required />
          <Form.Control type="text" name="week_of_reporting" value={formData.week_of_reporting} placeholder="Week of Reporting" onChange={handleChange} required />
          <Form.Control type="date" name="date_of_lecture" value={formData.date_of_lecture} placeholder="Date of Lecture" onChange={handleChange} required />
          <Form.Control type="text" name="course_name" value={formData.course_name} placeholder="Course Name" onChange={handleChange} required />
          <Form.Control type="text" name="course_code" value={formData.course_code} placeholder="Course Code" onChange={handleChange} required />
          <Form.Control type="text" name="lecturer_name" value={formData.lecturer_name} placeholder="Lecturer Name" onChange={handleChange} required />
          <Form.Control type="number" name="actual_number_of_students_present" value={formData.actual_number_of_students_present} placeholder="Actual Students Present" onChange={handleChange} required />
          <Form.Control type="number" name="total_number_of_registered_students" value={formData.total_number_of_registered_students} placeholder="Total Registered Students" onChange={handleChange} required />
          <Form.Control type="text" name="venue" value={formData.venue} placeholder="Venue" onChange={handleChange} required />
          <Form.Control type="text" name="scheduled_lecture_time" value={formData.scheduled_lecture_time} placeholder="Scheduled Lecture Time" onChange={handleChange} required />
          <Form.Control type="text" name="topic_taught" value={formData.topic_taught} placeholder="Topic Taught" onChange={handleChange} required />
          <Form.Control type="text" name="learning_outcomes" value={formData.learning_outcomes} placeholder="Learning Outcomes" onChange={handleChange} required />
          <Form.Control type="text" name="recommendations" value={formData.recommendations} placeholder="Recommendations" onChange={handleChange} required />
          <Form.Control type="text" name="lecturer_id" value={formData.lecturer_id} placeholder="Lecturer ID" onChange={handleChange} required />
          <Button variant="primary" type="submit">Submit Report</Button>
        </Form>
      )}

      <h3>Reports</h3>
      <Accordion>
        {reports.map((report) => (
          <Card key={report.id}>
            <Accordion.Header onClick={() => toggleExpand(report.id)}>
              Report for {report.course_name} - {report.course_code}
            </Accordion.Header>
            <Accordion.Body>
              {expandedReportId === report.id && (
                <div>
                  <p><strong>Faculty Name:</strong> {report.faculty_name}</p>
                  <p><strong>Class Name:</strong> {report.class_name}</p>
                  <p><strong>Week of Reporting:</strong> {report.week_of_reporting}</p>
                  <p><strong>Date of Lecture:</strong> {report.date_of_lecture}</p>
                  <p><strong>Course Name:</strong> {report.course_name}</p>
                  <p><strong>Course Code:</strong> {report.course_code}</p>
                  <p><strong>Lecturer Name:</strong> {report.lecturer_name}</p>
                  <p><strong>Actual Students:</strong> {report.actual_number_of_students_present}</p>
                  <p><strong>Total Registered:</strong> {report.total_number_of_registered_students}</p>
                  <p><strong>Venue:</strong> {report.venue}</p>
                  <p><strong>Scheduled Time:</strong> {report.scheduled_lecture_time}</p>
                  <p><strong>Topic Taught:</strong> {report.topic_taught}</p>
                  <p><strong>Learning Outcomes:</strong> {report.learning_outcomes}</p>
                  <p><strong>Recommendations:</strong> {report.recommendations}</p>
                  <p><strong>Lecturer ID:</strong> {report.lecturer_id}</p>

                  {role === 'prl' && (
                    <>
                      <Form onSubmit={(e) => { setSelectedReportId(report.id); handleFeedbackSubmit(e); }}>
                        <Form.Control
                          type="text"
                          name="feedback"
                          value={feedback}
                          onChange={(e) => setFeedback(e.target.value)}
                          placeholder="Add feedback"
                        />
                        <Button variant="secondary" type="submit">
                          Submit Feedback
                        </Button>
                      </Form>
                      <p>Current Feedback: {report.feedback || 'None'}</p>
                    </>
                  )}
                </div>
              )}
            </Accordion.Body>
          </Card>
        ))}
      </Accordion>
    </Card>
  );
};

export default Reports;
