import React, { useState, useEffect, useCallback } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';

const Reports = ({ role }) => {
  // formData for create OR edit
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });
  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);
  const [feedbacks, setFeedbacks] = useState({});
  const [expandedReportId, setExpandedReportId] = useState(null);
  const [editReportId, setEditReportId] = useState(null); // new: which report is being edited

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const fetchReports = useCallback(async (q = '') => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }
    let url;
    if (role === 'pl') {
      url = `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/prl-feedback`;
    } else {
      url = `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports${q ? `?q=${q}` : ''}`;
    }
    try {
      setError(null);
      const response = await axios.get(url, {
        headers: { 'x-auth-token': token },
      });
      setReports(response.data);
    } catch (err) {
      console.error('Fetch error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized.');
        else if (status === 403) setError('Forbidden.');
        else setError(`Server error (${status}): ${data.msg || 'Unknown error'}`);
      } else if (err.request) {
        setError('No response from server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  }, [role]);

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  const onSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No token found. Please log in.');
      return;
    }

    const submitData = {
      faculty_name: formData.faculty_name,
      class_name: formData.class_name,
      week_of_reporting: parseInt(formData.week_of_reporting) || 0,
      date_of_lecture: new Date(formData.date_of_lecture).toISOString().split('T')[0],
      course_name: formData.course_name,
      course_code: formData.course_code,
      lecturer_name: formData.lecturer_name,
      actual_number_of_students_present: parseInt(formData.actual_students) || 0,
      total_number_of_registered_students: parseInt(formData.total_registered) || 0,
      venue: formData.venue,
      scheduled_lecture_time: formData.scheduled_time,
      topic_taught: formData.topic_taught,
      learning_outcomes: formData.learning_outcomes,
      recommendations: formData.recommendations,
      lecturer_id: formData.lecturer_id || null
    };

    try {
      setError(null);
      if (editReportId) {
        // If editing
        await axios.put(
          `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/${editReportId}`,
          submitData,
          { headers: { 'x-auth-token': token } }
        );
        alert('Report updated successfully');
      } else {
        // Creating new
        await axios.post(
          `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports`,
          submitData,
          { headers: { 'x-auth-token': token } }
        );
        alert('Report submitted successfully');
      }

      // Clear form & exit edit mode
      setEditReportId(null);
      setFormData({
        faculty_name: '',
        class_name: '',
        week_of_reporting: '',
        date_of_lecture: '',
        course_name: '',
        course_code: '',
        lecturer_name: '',
        actual_students: '',
        total_registered: '',
        venue: '',
        scheduled_time: '',
        topic_taught: '',
        learning_outcomes: '',
        recommendations: '',
        lecturer_id: ''
      });

      fetchReports();
    } catch (err) {
      console.error('Submit error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized.');
        else if (status === 403) setError('Forbidden.');
        else setError(`Server error (${status}): ${data.msg || 'Unknown error'}`);
      } else if (err.request) {
        setError('No response from server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  };

  const handleDelete = async (reportId) => {
    if (!window.confirm('Are you sure you want to delete this report?')) return;
    const token = localStorage.getItem('token');
    try {
      await axios.delete(
        `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/${reportId}`,
        { headers: { 'x-auth-token': token } }
      );
      alert('Report deleted');
      // If currently editing that report, cancel edit
      if (editReportId === reportId) {
        setEditReportId(null);
        setFormData({
          faculty_name: '',
          class_name: '',
          week_of_reporting: '',
          date_of_lecture: '',
          course_name: '',
          course_code: '',
          lecturer_name: '',
          actual_students: '',
          total_registered: '',
          venue: '',
          scheduled_time: '',
          topic_taught: '',
          learning_outcomes: '',
          recommendations: '',
          lecturer_id: ''
        });
      }
      fetchReports();
    } catch (err) {
      console.error('Delete error:', err);
      setError('Failed to delete report');
    }
  };

  const startEdit = (report) => {
    setEditReportId(report.id);
    // Pre-fill formData fields based on that report's values
    setFormData({
      faculty_name: report.faculty_name,
      class_name: report.class_name,
      week_of_reporting: report.week_of_reporting,
      date_of_lecture: report.date_of_lecture,
      course_name: report.course_name,
      course_code: report.course_code,
      lecturer_name: report.lecturer_name,
      actual_students: report.actual_number_of_students_present,
      total_registered: report.total_number_of_registered_students,
      venue: report.venue,
      scheduled_time: report.scheduled_lecture_time,
      topic_taught: report.topic_taught,
      learning_outcomes: report.learning_outcomes,
      recommendations: report.recommendations,
      lecturer_id: report.lecturer_id
    });
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Excel export function (unchanged)
  const handleDownloadExcel = () => {
    const exportData = reports.map((r) => ({
      'Faculty Name': r.faculty_name,
      'Class Name': r.class_name,
      'Week of Reporting': r.week_of_reporting,
      'Date of Lecture': r.date_of_lecture,
      'Course Name': r.course_name,
      'Course Code': r.course_code,
      'Lecturer Name': r.lecturer_name,
      'Actual Students': r.actual_number_of_students_present,
      'Total Registered': r.total_number_of_registered_students,
      'Venue': r.venue,
      'Scheduled Time': r.scheduled_lecture_time,
      'Topic Taught': r.topic_taught,
      'Learning Outcomes': r.learning_outcomes,
      'Recommendations': r.recommendations,
      'Lecturer ID': r.lecturer_id,
      'Feedback': r.feedback || 'N/A'
    }));

    const worksheet = XLSX.utils.json_to_sheet(exportData);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Reports');
    const excelBuffer = XLSX.write(workbook, { bookType: 'xlsx', type: 'array' });
    const fileData = new Blob([excelBuffer], { type: 'application/octet-stream' });
    saveAs(fileData, 'Lecture_Reports.xlsx');
  };

  return (
    <Card className="p-4">
      <h2>
        {role === 'lecturer'
          ? editReportId ? 'Edit Report' : 'Submit Report'
          : role === 'prl'
          ? 'View Lecture Reports & Add Feedback'
          : 'View PRL Reports'}
      </h2>
      {error && <Alert variant="danger">{error}</Alert>}

      {role === 'lecturer' && (
        <Form onSubmit={onSubmit}>
          {Object.entries(formData).map(([key, value]) => (
            <Form.Group controlId={key} className="mb-3" key={key}>
              <Form.Label>
                {key.replace(/_/g, ' ').replace(/\b\w/g, (l) => l.toUpperCase())}
              </Form.Label>
              <Form.Control
                type={key.includes('date') ? 'date' : 'text'}
                name={key}
                value={value}
                onChange={onChange}
                required
              />
            </Form.Group>
          ))}
          <Button variant="primary" type="submit">
            {editReportId ? 'Update Report' : 'Submit Report'}
          </Button>
          {editReportId && (
            <Button
              variant="secondary"
              className="ms-2"
              onClick={() => {
                setEditReportId(null);
                setFormData({
                  faculty_name: '',
                  class_name: '',
                  week_of_reporting: '',
                  date_of_lecture: '',
                  course_name: '',
                  course_code: '',
                  lecturer_name: '',
                  actual_students: '',
                  total_registered: '',
                  venue: '',
                  scheduled_time: '',
                  topic_taught: '',
                  learning_outcomes: '',
                  recommendations: '',
                  lecturer_id: ''
                });
              }}
            >
              Cancel Edit
            </Button>
          )}
        </Form>
      )}

      <h3 className="mt-4">Reports</h3>
      {reports.length > 0 && (
        <Button className="mb-3" variant="success" onClick={handleDownloadExcel}>
          📥 Download Reports as Excel
        </Button>
      )}

      {reports.length ? (
        <ul>
          {reports.map((report) => (
            <li key={report.id}>
              <Button
                variant="link"
                onClick={() =>
                  setExpandedReportId(expandedReportId === report.id ? null : report.id)
                }
              >
                {expandedReportId === report.id ? 'Hide Details' : 'View Details'} -{' '}
                {report.topic_taught} - {report.date_of_lecture} - {report.lecturer_name}
              </Button>
              {(role === 'prl' || role === 'pl' || (role === 'lecturer' && editReportId === null)) &&
                expandedReportId === report.id && (
                <div>
                  <p><strong>Faculty Name:</strong> {report.faculty_name}</p>
                  <p><strong>Class Name:</strong> {report.class_name}</p>
                  <p><strong>Week of Reporting:</strong> {report.week_of_reporting}</p>
                  <p><strong>Date of Lecture:</strong> {report.date_of_lecture}</p>
                  <p><strong>Course Name:</strong> {report.course_name}</p>
                  <p><strong>Course Code:</strong> {report.course_code}</p>
                  <p><strong>Lecturer Name:</strong> {report.lecturer_name}</p>
                  <p><strong>Actual Students:</strong> {report.actual_number_of_students_present}</p>
                  <p><strong>Total Registered:</strong> {report.total_number_of_registered_students}</p>
                  <p><strong>Venue:</strong> {report.venue}</p>
                  <p><strong>Scheduled Time:</strong> {report.scheduled_lecture_time}</p>
                  <p><strong>Topic Taught:</strong> {report.topic_taught}</p>
                  <p><strong>Learning Outcomes:</strong> {report.learning_outcomes}</p>
                  <p><strong>Recommendations:</strong> {report.recommendations}</p>
                  <p><strong>Lecturer ID:</strong> {report.lecturer_id}</p>
                  <p><strong>Feedback:</strong> {report.feedback || 'None'}</p>
                  {role === 'lecturer' && (
                    <>
                      <Button
                        variant="warning"
                        size="sm"
                        className="me-2"
                        onClick={() => startEdit(report)}
                      >
                        Edit
                      </Button>
                      <Button
                        variant="danger"
                        size="sm"
                        onClick={() => handleDelete(report.id)}
                      >
                        Delete
                      </Button>
                    </>
                  )}
                  {role === 'prl' && (
                    <Form onSubmit={(e) => handleFeedbackSubmit(e, report.id)}>
                      <Form.Control
                        type="text"
                        name="feedback"
                        value={feedbacks[report.id] || ''}
                        onChange={(e) =>
                          setFeedbacks((prev) => ({ ...prev, [report.id]: e.target.value }))
                        }
                        placeholder="Add feedback"
                      />
                      <Button variant="secondary" type="submit">
                        Submit Feedback
                      </Button>
                    </Form>
                  )}
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No reports available.</p>
      )}
    </Card>
  );
};

export default Reports;
