import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Card, InputGroup, FormControl } from 'react-bootstrap';

const Lectures = ({ role }) => {
  const [lectures, setLectures] = useState([]);
  const [reports, setReports] = useState([]);
  const [selectedReportId, setSelectedReportId] = useState('');
  const [search, setSearch] = useState('');

  const API_URL = process.env.REACT_APP_API_URL;

  // Fetch existing lectures
  const fetchLectures = async () => {
    try {
      const res = await axios.get(`${API_URL}/lectures`, {
        headers: { 'x-auth-token': localStorage.getItem('token') },
      });
      setLectures(res.data);
    } catch (err) {
      console.error('Error fetching lectures:', err);
    }
  };

  // Fetch available reports for assigning lectures
  const fetchReports = async () => {
    try {
      const res = await axios.get(`${API_URL}/lectures/available-reports`, {
        headers: { 'x-auth-token': localStorage.getItem('token') },
      });
      setReports(res.data);
    } catch (err) {
      console.error('Error fetching reports:', err);
    }
  };

  useEffect(() => {
    fetchLectures();
    fetchReports();
  }, []);

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!selectedReportId) {
      alert('Please select a report to assign a lecture.');
      return;
    }

    try {
      await axios.post(
        `${API_URL}/lectures`,
        { report_id: selectedReportId },
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setSelectedReportId('');
      fetchLectures();
    } catch (err) {
      console.error('Error assigning lecture:', err);
      alert('Error assigning lecture. Make sure you selected a report.');
    }
  };

  const deleteLecture = async (id) => {
    if (!window.confirm('Are you sure you want to delete this lecture?')) return;
    try {
      await axios.delete(`${API_URL}/lectures/${id}`, {
        headers: { 'x-auth-token': localStorage.getItem('token') },
      });
      fetchLectures();
    } catch (err) {
      console.error(err);
    }
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
    // Optional: filter lectures locally instead of fetching
  };

  return (
    <Card className="p-4">
      {role === 'pl' && (
        <Form onSubmit={onSubmit} className="mb-4">
          <Form.Group controlId="report_id" className="mb-3">
            <Form.Label>Select Report to Assign Lecture</Form.Label>
            <Form.Control
              as="select"
              value={selectedReportId}
              onChange={(e) => setSelectedReportId(e.target.value)}
              required
            >
              <option value="">-- Select Report --</option>
              {reports.map((r) => (
                <option key={r.id} value={r.id}>
                  {r.course_name} ({r.course_code}) - {r.date_of_lecture}
                </option>
              ))}
            </Form.Control>
          </Form.Group>
          <Button variant="primary" type="submit">
            Assign Lecture
          </Button>
        </Form>
      )}

      <InputGroup className="mb-3">
        <FormControl placeholder="Search lectures..." value={search} onChange={handleSearch} />
      </InputGroup>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Course Name</th>
            <th>Course ID</th>
            <th>Lecturer Name</th>
            <th>Lecturer ID</th>
            <th>Date</th>
            {role === 'pl' && <th>Action</th>}
          </tr>
        </thead>
        <tbody>
          {lectures
            .filter((l) =>
              l.course_name?.toLowerCase().includes(search.toLowerCase())
            )
            .map((l) => (
              <tr key={l.id}>
                <td>{l.id}</td>
                <td>{l.course_name || 'N/A'}</td>
                <td>{l.course_id}</td>
                <td>{l.lecturer_name || 'N/A'}</td>
                <td>{l.lecturer_id || 'N/A'}</td>
                <td>{l.date_of_lecture || '-'}</td>
                {role === 'pl' && (
                  <td>
                    <Button variant="danger" size="sm" onClick={() => deleteLecture(l.id)}>
                      Delete
                    </Button>
                  </td>
                )}
              </tr>
            ))}
        </tbody>
      </Table>
    </Card>
  );
};

export default Lectures;
