import React, { useState, useEffect } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import axios from 'axios';

const Rating = ({ role, onRatingUpdate }) => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(''); // reportId or lectureId
  const [studentId, setStudentId] = useState(''); // only for lecturers
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  // Fetch reports from backend
  useEffect(() => {
    const fetchReports = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/reports`, {
          headers: { 'x-auth-token': token }
        });
        setReports(res.data);
      } catch (err) {
        console.error(err);
        setError('Failed to fetch reports.');
      }
    };
    fetchReports();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) return setError('No authentication token.');
    if (!selectedReport) return setError('Please select a report/lecture.');
    if (!rating || rating < 1 || rating > 5) return setError('Please enter a valid rating (1-5).');

    let payload = {
      rating: parseInt(rating),
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString(),
    };

    let url = '';

    if (role === 'lecturer') {
      if (!studentId) return setError('Please enter a Student ID.');
      payload.student_id = studentId;
      payload.lecturer_id = localStorage.getItem('userId');
      url = `${process.env.REACT_APP_API_URL}/api/ratings/student/${selectedReport}`;
    } else if (role === 'student') {
      payload.student_id = localStorage.getItem('userId');
      payload.lecture_id = selectedReport;
      payload.comment = comment;
      url = `${process.env.REACT_APP_API_URL}/api/ratings/lecture/${selectedReport}`;
    } else {
      return setError('Invalid role.');
    }

    try {
      await axios.post(url, payload, { headers: { 'x-auth-token': token } });
      setRating('');
      setSelectedReport('');
      setStudentId('');
      setComment('');
      setSuccess('Rating submitted successfully!');
      setError(null);
      if (onRatingUpdate) onRatingUpdate();
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Failed to submit rating.');
      setSuccess(null);
    }
  };

  return (
    <div className="mt-3">
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}
      <Form onSubmit={handleSubmit}>
        {/* Lecturer form */}
        {role === 'lecturer' && (
          <>
            <Form.Group controlId="reportId" className="mb-3">
              <Form.Label>Select Report</Form.Label>
              <Form.Control
                as="select"
                value={selectedReport}
                onChange={(e) => setSelectedReport(e.target.value)}
                required
              >
                <option value="">-- Select Report --</option>
                {reports.map((r) => (
                  <option key={r.id} value={r.id}>
                    {r.course_name} - {r.lecturer_name} ({r.date_of_lecture})
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="studentId" className="mb-3">
              <Form.Label>Student ID</Form.Label>
              <Form.Control
                type="text"
                value={studentId}
                onChange={(e) => setStudentId(e.target.value)}
                placeholder="Enter Student ID"
                required
              />
            </Form.Group>
          </>
        )}

        {/* Student form */}
        {role === 'student' && (
          <>
            <Form.Group controlId="lectureId" className="mb-3">
              <Form.Label>Select Lecture</Form.Label>
              <Form.Control
                as="select"
                value={selectedReport}
                onChange={(e) => setSelectedReport(e.target.value)}
                required
              >
                <option value="">-- Select Lecture --</option>
                {reports.map((r) => (
                  <option key={r.id} value={r.id}>
                    {r.course_name} - {r.lecturer_name} ({r.date_of_lecture})
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="comment" className="mb-3">
              <Form.Label>Comment</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                placeholder="Add your comment here..."
              />
            </Form.Group>
          </>
        )}

        {/* Common rating field */}
        <Form.Group controlId="rating" className="mb-3">
          <Form.Label>Rating (1-5)</Form.Label>
          <Form.Control
            type="number"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            min="1"
            max="5"
            required
          />
        </Form.Group>

        <Button type="submit">Submit Rating</Button>
      </Form>
    </div>
  );
};

export default Rating;
