import React, { useState, useEffect } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';

const Reports = ({ role }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const fetchReports = async (q = '') => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in.');
      return;
    }

    try {
      setError(null);
      const url = `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports${q ? `?q=${q}` : ''}`;
      const res = await axios.get(url, {
        headers: { 'x-auth-token': token }
      });
      setReports(res.data);
    } catch (err) {
      console.error('Fetch error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized: No token or token expired.');
        else if (status === 403) setError('Forbidden: Your role does not have access.');
        else setError(`Server error (${status}): ${data.msg || 'Unknown error'}`);
      } else {
        setError('Network or server error. Please try again.');
      }
    }
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No token found. Please log in.');
      return;
    }

    try {
      setError(null);
      const submitData = {
        faculty_name: formData.faculty_name,
        class_name: formData.class_name,
        week_of_reporting: parseInt(formData.week_of_reporting) || 0,
        date_of_lecture: new Date(formData.date_of_lecture).toISOString().split('T')[0],
        course_name: formData.course_name,
        course_code: formData.course_code,
        lecturer_name: formData.lecturer_name,
        actual_students: parseInt(formData.actual_students) || 0,
        total_registered: parseInt(formData.total_registered) || 0,
        venue: formData.venue,
        scheduled_time: formData.scheduled_time,
        topic_taught: formData.topic_taught,
        learning_outcomes: formData.learning_outcomes,
        recommendations: formData.recommendations
      };

      await axios.post(
        `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports`,
        submitData,
        { headers: { 'x-auth-token': token } }
      );

      alert('Report submitted successfully!');
      setFormData({
        faculty_name: '', class_name: '', week_of_reporting: '', date_of_lecture: '',
        course_name: '', course_code: '', lecturer_name: '', actual_students: '',
        total_registered: '', venue: '', scheduled_time: '', topic_taught: '',
        learning_outcomes: '', recommendations: ''
      });

      fetchReports();
    } catch (err) {
      console.error('Submit error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized: No token or token expired.');
        else if (status === 403) setError('Forbidden: Your role cannot submit reports.');
        else setError(`Server error (${status}): ${data.msg || 'Check form data.'}`);
      } else {
        setError('Network or server error. Please try again.');
      }
    }
  };

  useEffect(() => {
    fetchReports();
  }, []);

  return (
    <Card className="p-4">
      <h2>Submit Report</h2>
      {error && <Alert variant="danger">{error}</Alert>}

      <Form onSubmit={onSubmit}>
        <Form.Group controlId="faculty_name" className="mb-3">
          <Form.Label>Faculty Name</Form.Label>
          <Form.Control
            type="text"
            name="faculty_name"
            value={formData.faculty_name}
            onChange={onChange}
            required
          />
        </Form.Group>

        <Form.Group controlId="class_name" className="mb-3">
          <Form.Label>Class Name</Form.Label>
          <Form.Control
            type="text"
            name="class_name"
            value={formData.class_name}
            onChange={onChange}
            required
          />
        </Form.Group>

        <Form.Group controlId="week_of_reporting" className="mb-3">
          <Form.Label>Week Of Reporting</Form.Label>
          <Form.Control
            type="number"
            name="week_of_reporting"
            value={formData.week_of_reporting}
            onChange={onChange}
          />
        </Form.Group>

        <Form.Group controlId="date_of_lecture" className="mb-3">
          <Form.Label>Date Of Lecture</Form.Label>
          <Form.Control
            type="date"
            name="date_of_lecture"
            value={formData.date_of_lecture}
            onChange={onChange}
          />
        </Form.Group>

        <Form.Group controlId="course_name" className="mb-3">
          <Form.Label>Course Name</Form.Label>
          <Form.Control
            type="text"
            name="course_name"
            value={formData.course_name}
            onChange={onChange}
          />
        </Form.Group>

        <Form.Group controlId="course_code" className="mb-3">
          <Form.Label>Course Code</Form.Label>
          <Form.Control
            type="text"
            name="course_code"
            value={formData.course_code}
            onChange={onChange}
          />
        </Form.Group>

        <Form.Group controlId="lecturer_name" className="mb-3">
          <Form.Label>Lecturer Name</Form.Label>
          <Form.Control
            type="text"
            name="lecturer_name"
            value={formData.lecturer_name}
            onChange={onChange}
          />
        </Form.Group>

        <Form.Group controlId="actual_students" className="mb-3">
          <Form.Label>Actual Students Present</Form.Label>
          <Form.Control
            type="number"
            name="actual_students"
            value={formData.actual_students}
            onChange={onChange}
          />
        </Form.Group>

        <Form.Group controlId="total_registered" className="mb-3">
          <Form.Label>Total Registered Students</Form.Label>
          <Form.Control
            type="number"
            name="total_registered"
            value={formData.total_registered}
            onChange={onChange}
          />
        </Form.Group>

        <Form.Group controlId="venue" className="mb-3">
          <Form.Label>Venue</Form.Label>
          <Form.Control
            type="text"
            name="venue"
            value={formData.venue}
            onChange={onChange}
          />
        </Form.Group>

        <Form.Group controlId="scheduled_time" className="mb-3">
          <Form.Label>Scheduled Time</Form.Label>
          <Form.Control
            type="text"
            name="scheduled_time"
            value={formData.scheduled_time}
            onChange={onChange}
          />
        </Form.Group>

        <Form.Group controlId="topic_taught" className="mb-3">
          <Form.Label>Topic Taught</Form.Label>
          <Form.Control
            type="text"
            name="topic_taught"
            value={formData.topic_taught}
            onChange={onChange}
          />
        </Form.Group>

        <Form.Group controlId="learning_outcomes" className="mb-3">
          <Form.Label>Learning Outcomes</Form.Label>
          <Form.Control
            as="textarea"
            rows={2}
            name="learning_outcomes"
            value={formData.learning_outcomes}
            onChange={onChange}
          />
        </Form.Group>

        <Form.Group controlId="recommendations" className="mb-3">
          <Form.Label>Recommendations</Form.Label>
          <Form.Control
            as="textarea"
            rows={2}
            name="recommendations"
            value={formData.recommendations}
            onChange={onChange}
          />
        </Form.Group>

        <Button variant="primary" type="submit">Submit Report</Button>
      </Form>

      <h3 className="mt-4">Reports</h3>
      {reports.length ? (
        <ul>
          {reports.map((report) => (
            <li key={report.id}>{report.topic_taught} ({report.date_of_lecture})</li>
          ))}
        </ul>
      ) : (
        <p>No reports available.</p>
      )}
    </Card>
  );
};

export default Reports;
