import React, { useState, useEffect } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import axios from 'axios';

const Rating = ({ role, reportId }) => {
  const [lectureId, setLectureId] = useState(reportId || ''); // Default to reportId for students
  const [studentId, setStudentId] = useState(''); // For lecturers
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState(''); // Optional for students
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    // Reset form when reportId or role changes
    setLectureId(reportId || '');
    setStudentId('');
    setRating('');
    setComment('');
    setError(null);
    setSuccess(null);
  }, [reportId, role]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in.');
      return;
    }

    const userId = localStorage.getItem('userId');
    if (!userId) {
      setError('User ID not found. Please log in again or contact support.');
      return;
    }

    const payload = {
      rating: parseInt(rating),
    };

    let url = `${process.env.REACT_APP_API_URL}/api/rating`;
    if (role === 'student') {
      if (!lectureId || !rating) {
        setError('Please provide lecture ID and rating.');
        return;
      }
      payload.student_id = userId;
      payload.lecture_id = lectureId;
      payload.comment = comment || null; // Optional comment
      payload.lecturer_id = null; // Explicitly set to null for student ratings
    } else if (role === 'lecturer') {
      if (!studentId || !rating) {
        setError('Please provide student ID and rating.');
        return;
      }
      payload.student_id = studentId;
      payload.lecturer_id = userId;
      payload.lecture_id = reportId; // Use reportId as lecture_id
      payload.comment = null; // No comment for lecturer ratings
    } else {
      setError('Invalid role for rating.');
      return;
    }

    try {
      await axios.post(url, payload, { headers: { 'x-auth-token': token } });
      setLectureId(reportId || '');
      setStudentId('');
      setRating('');
      setComment('');
      setSuccess(role === 'student' ? 'Lecture rated successfully' : 'Student rated successfully');
      setError(null);
      setTimeout(() => setSuccess(null), 3000);
    } catch (err) {
      console.error('Rating error:', err.response ? err.response.data : err.message);
      setError('Error submitting rating. Please try again.');
    }
  };

  return (
    <div className="mt-3 p-3 border">
      <h5>{role === 'student' ? 'Rate Lecture Performance' : 'Rate Student Performance'}</h5>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}
      <Form onSubmit={handleSubmit}>
        {role === 'student' && (
          <Form.Group controlId="lectureId" className="mb-3">
            <Form.Label>Lecture ID</Form.Label>
            <Form.Control
              type="text"
              value={lectureId}
              onChange={(e) => setLectureId(e.target.value)}
              placeholder="Enter lecture ID (or use report ID)"
              required
            />
          </Form.Group>
        )}
        {role === 'lecturer' && (
          <Form.Group controlId="studentId" className="mb-3">
            <Form.Label>Student ID</Form.Label>
            <Form.Control
              type="text"
              value={studentId}
              onChange={(e) => setStudentId(e.target.value)}
              placeholder="Enter student ID"
              required
            />
          </Form.Group>
        )}
        <Form.Group controlId="rating" className="mb-3">
          <Form.Label>Rating (1-5)</Form.Label>
          <Form.Control
            type="number"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            min="1"
            max="5"
            required
          />
        </Form.Group>
        {role === 'student' && (
          <Form.Group controlId="comment" className="mb-3">
            <Form.Label>Comment (Optional)</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="Add your comment on the lecture performance..."
            />
          </Form.Group>
        )}
        <Button variant="primary" type="submit">Submit Rating</Button>
      </Form>
    </div>
  );
};

export default Rating;
