import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Rating = ({ role }) => {
  const [options, setOptions] = useState([]); // lectures for student, students for lecturer
  const [form, setForm] = useState({
    target_id: '', // lecture_id or student_id
    rating: '',
    comment: ''
  });
  const [message, setMessage] = useState('');
  const [ratings, setRatings] = useState([]);

  useEffect(() => {
    fetchOptions();
    fetchRatings();
  }, []);

  // Fetch dynamic options from backend
  const fetchOptions = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;

    try {
      let url = '';
      if (role === 'student') {
        url = `${process.env.REACT_APP_API_URL}/lectures/attended`; // lectures student attended
      } else if (role === 'lecturer') {
        url = `${process.env.REACT_APP_API_URL}/students/attended`; // students in lecturer's lectures
      }
      const res = await axios.get(url, { headers: { 'x-auth-token': token } });
      setOptions(res.data || []);
    } catch (err) {
      console.error('Fetch options error:', err);
    }
  };

  const fetchRatings = async () => {
    try {
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/rating`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setRatings(res.data.ratings || []);
    } catch (err) {
      console.error('Fetch ratings error:', err);
    }
  };

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = { ...form };
      if (role === 'student') payload.lecture_id = form.target_id;
      if (role === 'lecturer') payload.student_id = form.target_id;

      const res = await axios.post(`${process.env.REACT_APP_API_URL}/rating`, payload, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setMessage(res.data.message);
      setForm({ target_id: '', rating: '', comment: '' });
      fetchRatings();
    } catch (err) {
      console.error('Submit error:', err);
      setMessage(err.response?.data?.message || 'Error submitting rating');
    }
  };

  return (
    <div style={{ marginTop: '20px' }}>
      <div style={{
        border: '1px solid #ccc',
        padding: '20px',
        borderRadius: '10px',
        maxWidth: '400px',
        marginBottom: '20px'
      }}>
        <h3>{role === 'student' ? 'Rate Lecture' : 'Rate Student'}</h3>
        {message && <p style={{ color: 'green' }}>{message}</p>}

        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
          <select
            name="target_id"
            value={form.target_id}
            onChange={handleChange}
            required
            style={{ padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}
          >
            <option value="">Select {role === 'student' ? 'Lecture' : 'Student'}</option>
            {options.map((opt) => (
              <option key={opt.id} value={opt.id}>
                {role === 'student' ? `${opt.course_name} - ${opt.date_of_lecture}` : `${opt.name} (ID: ${opt.id})`}
              </option>
            ))}
          </select>

          <input
            type="number"
            name="rating"
            placeholder="Rating (1-5)"
            min="1"
            max="5"
            value={form.rating}
            onChange={handleChange}
            required
            style={{ padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}
          />

          <textarea
            name="comment"
            placeholder="Comment (optional)"
            value={form.comment}
            onChange={handleChange}
            style={{ padding: '8px', borderRadius: '5px', border: '1px solid #ccc', minHeight: '60px' }}
          />

          <button type="submit" style={{
            padding: '10px',
            borderRadius: '5px',
            border: 'none',
            backgroundColor: '#007bff',
            color: '#fff',
            cursor: 'pointer'
          }}>Submit Rating</button>
        </form>
      </div>

      <h3>All Ratings</h3>
      {ratings.length > 0 ? ratings.map((r) => (
        <div key={r.id} style={{
          border: '1px solid #ccc',
          borderRadius: '10px',
          padding: '15px',
          marginBottom: '10px'
        }}>
          <p><strong>Lecture ID:</strong> {r.lecture_id}</p>
          <p><strong>Student ID:</strong> {r.student_id}</p>
          <p><strong>Lecturer ID:</strong> {r.lecturer_id}</p>
          <p><strong>Rating:</strong> {r.rating}</p>
          <p><strong>Comment:</strong> {r.comment || '-'}</p>
        </div>
      )) : <p>No ratings yet.</p>}
    </div>
  );
};

export default Rating;
