import React, { useState, useEffect, useCallback } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import axios from 'axios';
import * as XLSX from 'xlsx';

const Reports = ({ role }) => {
  const [formData, setFormData] = useState({
    faculty_name: '',
    class_name: '',
    week_of_reporting: '',
    date_of_lecture: '',
    course_name: '',
    course_code: '',
    lecturer_name: '',
    actual_students: '',
    total_registered: '',
    venue: '',
    scheduled_time: '',
    topic_taught: '',
    learning_outcomes: '',
    recommendations: '',
    lecturer_id: ''
  });

  const [reports, setReports] = useState([]);
  const [error, setError] = useState(null);
  const [feedbacks, setFeedbacks] = useState({});
  const [expandedReportId, setExpandedReportId] = useState(null);
  const [editingId, setEditingId] = useState(null); // Tracks if editing a specific report
  const [currentUser, setCurrentUser] = useState(null); // Current user's info for ownership checks

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  // Fetch current user for ownership checks (only for lecturers)
  useEffect(() => {
    const fetchCurrentUser = async () => {
      if (role !== 'lecturer') return;
      const token = localStorage.getItem('token');
      if (!token) return;

      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/auth/me`,
          { headers: { 'x-auth-token': token } }
        );
        setCurrentUser(response.data); // Assumes { lecturer_id, ... }
      } catch (err) {
        console.error('Failed to fetch current user:', err);
        // Optionally setError here if needed
      }
    };

    fetchCurrentUser();
  }, [role]);

  const fetchReports = useCallback(async (q = '') => {
    const token = localStorage.getItem('token');
    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }

    let url;
    if (role === 'pl') {
      url = `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/prl-feedback`;
      console.log('Fetching PRL feedback reports for PL:', url);
    } else {
      url = `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports${q ? `?q=${q}` : ''}`;
      console.log('Fetching all reports for role:', role, url);
    }

    try {
      setError(null);
      let response;
      if (role === 'pl') {
        response = await axios.get(url, { headers: { 'x-auth-token': token } });
      } else {
        response = await axios.get(url, { headers: { 'x-auth-token': token } });
      }
      setReports(response.data);
    } catch (err) {
      console.error('Fetch error - URL:', url, 'Error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized: No token or token expired.');
        else if (status === 403) setError('Forbidden: Your role does not have access to reports.');
        else if (status === 400) setError('Invalid token or bad request.');
        else setError(`Server error (${status}): ${data.msg || 'Unknown error'}`);
      } else if (err.request) {
        setError('No response from server. Check your network or server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  }, [role]);

  const resetForm = () => {
    setFormData({
      faculty_name: '',
      class_name: '',
      week_of_reporting: '',
      date_of_lecture: '',
      course_name: '',
      course_code: '',
      lecturer_name: '',
      actual_students: '',
      total_registered: '',
      venue: '',
      scheduled_time: '',
      topic_taught: '',
      learning_outcomes: '',
      recommendations: '',
      lecturer_id: ''
    });
    setEditingId(null);
  };

  // Prefill form for editing a report
  const handleEdit = (report) => {
    setFormData({
      faculty_name: report.faculty_name || '',
      class_name: report.class_name || '',
      week_of_reporting: report.week_of_reporting || '',
      date_of_lecture: report.date_of_lecture || '',
      course_name: report.course_name || '',
      course_code: report.course_code || '',
      lecturer_name: report.lecturer_name || '',
      actual_students: report.actual_number_of_students_present || '',
      total_registered: report.total_number_of_registered_students || '',
      venue: report.venue || '',
      scheduled_time: report.scheduled_lecture_time || '',
      topic_taught: report.topic_taught || '',
      learning_outcomes: report.learning_outcomes || '',
      recommendations: report.recommendations || '',
      lecturer_id: report.lecturer_id || ''
    });
    setEditingId(report.id);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    if (!token) {
      setError('No token found. Please log in.');
      return;
    }

    try {
      setError(null);
      const submitData = {
        faculty_name: formData.faculty_name,
        class_name: formData.class_name,
        week_of_reporting: parseInt(formData.week_of_reporting) || 0,
        date_of_lecture: new Date(formData.date_of_lecture).toISOString().split('T')[0],
        course_name: formData.course_name,
        course_code: formData.course_code,
        lecturer_name: formData.lecturer_name,
        actual_number_of_students_present: parseInt(formData.actual_students) || 0,
        total_number_of_registered_students: parseInt(formData.total_registered) || 0,
        venue: formData.venue,
        scheduled_lecture_time: formData.scheduled_time,
        topic_taught: formData.topic_taught,
        learning_outcomes: formData.learning_outcomes,
        recommendations: formData.recommendations,
        lecturer_id: formData.lecturer_id || null
      };

      let response;
      if (editingId) {
        // Update existing report
        response = await axios.put(
          `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/${editingId}`,
          submitData,
          { headers: { 'x-auth-token': token } }
        );
        alert('Report updated successfully');
      } else {
        // Create new report
        response = await axios.post(
          `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports`,
          submitData,
          { headers: { 'x-auth-token': token } }
        );
        alert('Report submitted successfully');
      }

      resetForm();
      fetchReports();
    } catch (err) {
      console.error('Submit/Update error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized: No token or token expired.');
        else if (status === 403) setError(editingId ? 'Forbidden: You cannot edit this report.' : 'Forbidden: Your role does not have access to submit reports.');
        else if (status === 400) setError(`Invalid request: ${data.msg || 'Check form data.'}`);
        else setError(`Server error (${status}): ${data.msg || 'Unknown error'}`);
      } else if (err.request) {
        setError('No response from server. Check your network or server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  };

  const handleDelete = async (reportId, reportLecturerId) => {
    if (!confirm('Are you sure you want to delete this report? This action cannot be undone.')) {
      return;
    }

    // Ownership check for lecturers
    if (role === 'lecturer' && currentUser?.lecturer_id !== reportLecturerId) {
      setError('You can only delete your own reports.');
      return;
    }

    const token = localStorage.getItem('token');
    if (!token) {
      setError('No token found. Please log in.');
      return;
    }

    try {
      setError(null);
      await axios.delete(
        `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/${reportId}`,
        { headers: { 'x-auth-token': token } }
      );
      alert('Report deleted successfully');
      fetchReports();
    } catch (err) {
      console.error('Delete error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized: No token or token expired.');
        else if (status === 403) setError('Forbidden: You do not have permission to delete this report.');
        else setError(`Server error (${status}): ${data.msg || 'Unknown error'}`);
      } else if (err.request) {
        setError('No response from server. Check your network or server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  };

  const handleFeedbackSubmit = async (e, reportId) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const feedback = feedbacks[reportId] || '';

    console.log('Feedback Submit - URL:', `${process.env.REACT_APP_API_URL}/reports/feedback/${reportId}`);
    console.log('Feedback Submit - Token:', token ? 'Present' : 'Missing');
    console.log('Feedback Submit - Report ID:', reportId);
    console.log('Feedback Submit - Feedback:', feedback);

    if (!token) {
      setError('No authentication token. Please log in again.');
      return;
    }
    if (!reportId) {
      setError('No report selected for feedback.');
      return;
    }
    if (!feedback.trim()) {
      setError('Please enter feedback before submitting.');
      return;
    }

    try {
      setError(null);
      await axios.post(
        `${process.env.REACT_APP_API_URL.replace(/\/$/, '')}/reports/feedback/${reportId}`,
        { feedback },
        { headers: { 'x-auth-token': token } }
      );
      setFeedbacks((prev) => ({ ...prev, [reportId]: '' }));
      fetchReports();
    } catch (err) {
      console.error('Feedback error - URL:', `${process.env.REACT_APP_API_URL}/reports/feedback/${reportId}`, 'Error:', err);
      if (err.response) {
        const { status, data } = err.response;
        if (status === 401) setError('Unauthorized: No token or token expired.');
        else if (status === 403) setError('Forbidden: Only PRL can add feedback.');
        else setError(`Server error (${status}): ${data.msg || 'Unknown error'}`);
      } else if (err.request) {
        setError('No response from server. Check your network or server.');
      } else {
        setError('Error: ' + err.message);
      }
    }
  };

  const downloadExcel = () => {
    // Prepare data for Excel
    const worksheetData = reports.map((report) => ({
      'Faculty Name': report.faculty_name,
      'Class Name': report.class_name,
      'Week of Reporting': report.week_of_reporting,
      'Date of Lecture': report.date_of_lecture,
      'Course Name': report.course_name,
      'Course Code': report.course_code,
      'Lecturer Name': report.lecturer_name,
      'Actual Students Present': report.actual_number_of_students_present,
      'Total Registered Students': report.total_number_of_registered_students,
      'Venue': report.venue,
      'Scheduled Time': report.scheduled_lecture_time,
      'Topic Taught': report.topic_taught,
      'Learning Outcomes': report.learning_outcomes,
      'Recommendations': report.recommendations,
      'Lecturer ID': report.lecturer_id,
      'Feedback': report.feedback || 'None'
    }));

    // Create worksheet and workbook
    const worksheet = XLSX.utils.json_to_sheet(worksheetData);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Lecture Reports');

    // Download the Excel file
    XLSX.writeFile(workbook, 'Lecture_Reports.xlsx');
  };

  // Check if current user owns the report (for lecturers)
  const canEditOrDelete = (report) => {
    if (role === 'prl') return true; // PRL can delete any
    if (role === 'lecturer' && currentUser?.lecturer_id === report.lecturer_id) return true;
    return false;
  };

  useEffect(() => {
    fetchReports();
  }, [fetchReports]);

  return (
    <Card className="p-4">
      <h2>
        {role === 'lecturer'
          ? 'Submit Report'
          : role === 'prl'
          ? 'View Lecture Reports & Add Feedback'
          : 'View PRL Reports'}
      </h2>
      {error && <Alert variant="danger">{error}</Alert>}

      {role === 'lecturer' && (
        <Form onSubmit={onSubmit}>
          {editingId && <Alert variant="info">Editing Report ID: {editingId}</Alert>}
          {Object.entries(formData).map(([key, value]) => (
            <Form.Group controlId={key} className="mb-3" key={key}>
              <Form.Label>
                {key.replace(/_/g, ' ').replace(/\b\w/g, (l) => l.toUpperCase())}
              </Form.Label>
              <Form.Control
                type={key.includes('date') ? 'date' : 'text'}
                name={key}
                value={value}
                onChange={onChange}
                required
              />
            </Form.Group>
          ))}

          <Button variant="primary" type="submit">
            {editingId ? 'Update Report' : 'Submit Report'}
          </Button>
          {editingId && (
            <Button variant="secondary" className="ms-2" type="button" onClick={resetForm}>
              Cancel Edit
            </Button>
          )}
        </Form>
      )}

      <h3 className="mt-4">Reports</h3>
      <Button variant="success" className="mb-3" onClick={downloadExcel}>
        Download Reports as Excel
      </Button>
      {reports.length ? (
        <ul className="list-unstyled">
          {reports.map((report) => (
            <li key={report.id} className="border p-3 mb-3 rounded">
              <div className="d-flex justify-content-between align-items-center mb-2">
                <Button
                  variant="link"
                  onClick={() =>
                    setExpandedReportId(expandedReportId === report.id ? null : report.id)
                  }
                >
                  {expandedReportId === report.id ? 'Hide Details' : 'View Details'} -{' '}
                  {report.topic_taught} - {report.date_of_lecture} - {report.lecturer_name}
                </Button>
                {(role === 'lecturer' || role === 'prl') && canEditOrDelete(report) && (
                  <div>
                    <Button
                      variant="info"
                      size="sm"
                      className="me-2"
                      onClick={() => handleEdit(report)}
                      disabled={role !== 'lecturer'}
                    >
                      Edit
                    </Button>
                    <Button
                      variant="danger"
                      size="sm"
                      onClick={() => handleDelete(report.id, report.lecturer_id)}
                    >
                      Delete
                    </Button>
                  </div>
                )}
              </div>
              {expandedReportId === report.id && (
                <div className="mt-3">
                  <p><strong>Faculty Name:</strong> {report.faculty_name}</p>
                  <p><strong>Class Name:</strong> {report.class_name}</p>
                  <p><strong>Week of Reporting:</strong> {report.week_of_reporting}</p>
                  <p><strong>Date of Lecture:</strong> {report.date_of_lecture}</p>
                  <p><strong>Course Name:</strong> {report.course_name}</p>
                  <p><strong>Course Code:</strong> {report.course_code}</p>
                  <p><strong>Lecturer Name:</strong> {report.lecturer_name}</p>
                  <p><strong>Actual Students:</strong> {report.actual_number_of_students_present}</p>
                  <p><strong>Total Registered:</strong> {report.total_number_of_registered_students}</p>
                  <p><strong>Venue:</strong> {report.venue}</p>
                  <p><strong>Scheduled Time:</strong> {report.scheduled_lecture_time}</p>
                  <p><strong>Topic Taught:</strong> {report.topic_taught}</p>
                  <p><strong>Learning Outcomes:</strong> {report.learning_outcomes}</p>
                  <p><strong>Recommendations:</strong> {report.recommendations}</p>
                  <p><strong>Lecturer ID:</strong> {report.lecturer_id}</p>
                  <p><strong>Feedback:</strong> {report.feedback || 'None'}</p>
                  {(role === 'prl' || role === 'pl') && (
                    <Form onSubmit={(e) => handleFeedbackSubmit(e, report.id)} className="mt-3">
                      <Form.Control
                        type="text"
                        name="feedback"
                        value={feedbacks[report.id] || ''}
                        onChange={(e) =>
                          setFeedbacks((prev) => ({ ...prev, [report.id]: e.target.value }))
                        }
                        placeholder="Add feedback"
                      />
                      <Button variant="secondary" type="submit" className="mt-2">
                        Submit Feedback
                      </Button>
                    </Form>
                  )}
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No reports available.</p>
      )}
    </Card>
  );
};

export default Reports;
