import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Card, InputGroup, FormControl } from 'react-bootstrap';

const Lectures = ({ role }) => {
  const [lectures, setLectures] = useState([]);
  const [reports, setReports] = useState([]);
  const [formData, setFormData] = useState({ course_id: '', lecturer_id: '', date_of_lecture: '' });
  const [search, setSearch] = useState('');

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

  useEffect(() => {
    fetchLectures();
    fetchReports();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const fetchLectures = async () => {
    try {
      const res = await axios.get(`${API_URL}/lectures`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setLectures(res.data);
    } catch (err) {
      console.error('Fetch lectures error:', err);
    }
  };

  const fetchReports = async () => {
    try {
      const res = await axios.get(`${API_URL}/lectures/available-reports`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setReports(res.data);
    } catch (err) {
      console.error('Fetch reports error:', err);
    }
  };

  const onChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (name === 'course_id') {
      const selected = reports.find(r => r.id === parseInt(value));
      if (selected) {
        setFormData({
          course_id: selected.id,
          lecturer_id: selected.lecturer_id,
          date_of_lecture: selected.date_of_lecture
        });
      }
    }
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_URL}/lectures`, formData, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setFormData({ course_id: '', lecturer_id: '', date_of_lecture: '' });
      fetchLectures();
    } catch (err) {
      console.error('Add lecture error:', err);
    }
  };

  const deleteLecture = async (id) => {
    if (!window.confirm('Are you sure you want to delete this lecture?')) return;
    try {
      await axios.delete(`${API_URL}/lectures/${id}`, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchLectures();
    } catch (err) {
      console.error('Delete lecture error:', err);
    }
  };

  const filteredLectures = lectures.filter(l =>
    (l.lecture_name || '').toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Card className="p-4">
      {role === 'pl' && (
        <Form onSubmit={onSubmit} className="mb-4">
          <Form.Group controlId="course_id" className="mb-3">
            <Form.Label>Select Lecture (from Reports)</Form.Label>
            <Form.Select name="course_id" value={formData.course_id} onChange={onChange} required>
              <option value="">Select lecture</option>
              {reports.map(r => (
                <option key={r.id} value={r.id}>
                  {r.course_name} â€” {r.lecturer_name}
                </option>
              ))}
            </Form.Select>
          </Form.Group>

          <Form.Group controlId="lecturer_id" className="mb-3">
            <Form.Label>Lecturer ID</Form.Label>
            <Form.Control type="number" name="lecturer_id" value={formData.lecturer_id} readOnly />
          </Form.Group>

          <Form.Group controlId="date_of_lecture" className="mb-3">
            <Form.Label>Date of Lecture</Form.Label>
            <Form.Control type="date" name="date_of_lecture" value={formData.date_of_lecture?.split('T')[0] || ''} readOnly />
          </Form.Group>

          <Button variant="primary" type="submit">Assign Lecture</Button>
        </Form>
      )}

      <InputGroup className="mb-3">
        <FormControl placeholder="Search lectures..." value={search} onChange={e => setSearch(e.target.value)} />
      </InputGroup>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Lecture Name</th>
            <th>Course ID</th>
            <th>Lecturer Name</th>
            <th>Lecturer ID</th>
            <th>Date</th>
            {role === 'pl' && <th>Action</th>}
          </tr>
        </thead>
        <tbody>
          {filteredLectures.map(l => (
            <tr key={l.id}>
              <td>{l.id}</td>
              <td>{l.lecture_name || 'N/A'}</td>
              <td>{l.course_id}</td>
              <td>{l.lecturer_name || 'N/A'}</td>
              <td>{l.lecturer_id}</td>
              <td>{l.date_of_lecture?.split('T')[0]}</td>
              {role === 'pl' && (
                <td>
                  <Button variant="danger" size="sm" onClick={() => deleteLecture(l.id)}>
                    Delete
                  </Button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </Table>
    </Card>
  );
};

export default Lectures;
