import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button, Card } from 'react-bootstrap';
import { jwtDecode } from 'jwt-decode'; // <-- named import

const Login = ({ setRole }) => {
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [usernames, setUsernames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch available usernames on component mount
  useEffect(() => {
    const fetchUsernames = async () => {
      try {
        setLoading(true);
        setError(null);
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/auth/users`);
        if (res.data && Array.isArray(res.data)) {
          setUsernames(res.data.map(user => user.username));
        } else {
          setError('Invalid data format from server');
        }
      } catch (err) {
        console.error('Error fetching usernames:', err);
        setError('Failed to load usernames. Check server connection.');
      } finally {
        setLoading(false);
      }
    };
    fetchUsernames();
  }, []);

  const onChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/auth/login`, formData);
      localStorage.setItem('token', res.data.token);
      const decoded = jwtDecode(res.data.token);
      setRole(decoded.role);
      navigate('/reports');
    } catch (err) {
      console.error(err);
      alert('Login failed. Check username/password or server.');
    }
  };

  return (
    <Card className="p-4 report-form">
      <h2>Login</h2>
      <Form onSubmit={onSubmit}>
        <Form.Group controlId="username" className="mb-3">
          <Form.Label>Username</Form.Label>
          {loading ? (
            <p>Loading usernames...</p>
          ) : error ? (
            <p style={{ color: 'red' }}>{error}</p>
          ) : (
            <Form.Select
              name="username"
              value={formData.username}
              onChange={onChange}
              required
            >
              <option value="">Select a username</option>
              {usernames.map((username, index) => (
                <option key={index} value={username}>{username}</option>
              ))}
            </Form.Select>
          )}
        </Form.Group>
        <Form.Group controlId="password" className="mb-3">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            name="password"
            value={formData.password}
            onChange={onChange}
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">Login</Button>
      </Form>
    </Card>
  );
};

export default Login;
