import React, { useState } from 'react';
import axios from 'axios';

const Rating = () => {
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');

  const onSubmit = async (e) => {
    e.preventDefault();

    try {
      // Axios call matches backend route
      const res = await axios.post('/api/ratings', { rating, comment });
      console.log(res.data.message); // Success feedback
      setRating('');
      setComment('');
    } catch (err) {
      console.error(err.response?.data?.message || 'Error submitting rating');
    }
  };

  return (
    <form onSubmit={onSubmit}>
      <div>
        <label>Rating:</label>
        <input
          type="number"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
          required
        />
      </div>

      <div>
        <label>Comment:</label>
        <input
          type="text"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          required
        />
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default Rating;
